css:
@-moz-document domain("uqam.ca") {
body {
    color: #fff;
    background-color: #151515;
}
@media only screen and (max-width: 767px) and (min-width: 320px) {
    #accueil_annonces div:first-child ul.admin li:first-child {
        width: 49%;
        border-bottom: none;
        border-right: #151515 solid 3px;
    }
    #accueil_annonces div:first-child ul.admin li:last-child {
        border-left: #151515 solid 2px;
    }
}
@media only screen and (max-width: 767px) {
    #accueil_annonces div:first-child ul.types {
        border-bottom: #151515 solid 5px;
    }
    #accueil_annonces div:last-child {
        border-bottom: #151515 solid 5px;
    }
    #contenu_accueil h2 a {
        color: #0079be;
    }
    #actualites {
        border-bottom: #151515 solid 5px;
    }
    #evenements {
        border-bottom: #151515 solid 5px;
    }
}
@media only screen and (min-width: 768px) {
    #contenu_accueil #accueil_actualites h2, #contenu_accueil #accueil_reseauxsociaux h2, #contenu_accueil #actualites-evenements_titre h2 {
        border-bottom: 1px solid #fff;
    }
    #actualites .nouvelle {
        background-color: #101010;
    }
    #actualites .nouvelle p {
        background-color: #101010;
    }
    #actualites>div:first-child+.nouvelle,
    #actualites>div:first-child+.nouvelle+.nouvelle+.nouvelle,
    #actualites>div:first-child+.nouvelle+.nouvelle+.nouvelle+.nouvelle {
        border-right: 5px solid #151515;
    }
    #actualites>div:first-child+.nouvelle+.nouvelle,
    #actualites>div:first-child+.nouvelle+.nouvelle+.nouvelle+.nouvelle+.nouvelle {
        border-left: 5px solid #151515;
    }
    #contenu_accueil #accueil_reseauxsociaux #accueil_instagram>a,
    #contenu_accueil #accueil_reseauxsociaux #accueil_twitter>a {
        border-bottom: thin solid #fff;
    }
    #contenu_accueil #accueil_mediassociaux h2::after {
        background-color: #fff;
        border-left: 5px solid #fff;
    }
    #accueil_annonces div:last-child {
        border-bottom: #151515 solid 10px;
    }
    #evenements-titre {
        border-left: 5px solid #151515;
    }
    #actualites-titre {
        padding: 0;
        border-right: 5px solid #151515;
    }
    #liens_actualites,
    #liens_evenements {
        border-top: #000000 thin solid;
        border-bottom: #000000 thin solid;
    }
}
@media only screen and (min-width: 992px) {
    #evenements-titre {
        border-left: 5px solid #151515;
    }
    #actualites-titre {
        padding: 0;
        border-right: 5px solid #151515;
    }
    #accueil_annonces {
        border-left: 5px solid #151515;
    }
    #accueil_annonces div:last-child {
        border-bottom: #000 solid 0px;
        border-top: #151515 solid 10px;
    }
    #accueil_banniere {
        border-right: 5px solid #151515;
    }
    #liens_actualites,
    #liens_evenements {
        border-top: #fff thin solid;
        border-bottom: #fff thin solid;
    }
}
@media only screen and (max-width: 992px) and (min-width: 768px) {
    #accueil_annonces div:first-child ul.admin {
        border-left: #151515 solid 10px;
    }
    #accueil_annonces div:first-child ul.admin li:first-child {
        border-bottom: #151515 solid 10px;
    }
    #accueil_annonces div:first-child ul.types {
        border-bottom: #151515 solid 10px;
    }
}
#actualites .uqam_tv a:first-child,
#actualites .actualite_expert a:first-child {
    color: #fff;
}
.encadre {
    background-color: #070707;
    border: 1px solid #000000;
    border-radius: 10px;
    margin-bottom: 30px;
    padding: 25px;
}
h1 {
    color: #0079be;
}
}
