css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hypothes.is") {
  
  body {
    background-color: #282828;
    color: #aaa;
  }
  
  .search-result-container {
    color: #999;
  }
  
  .btn {
    text-shadow: none;
  }
  
  /**** Search Page ****/
  
  /* Search box */
  .search-bar {
    border-color: #333;
  }
  
  .nav-bar {
    border-bottom-color: #333;
  }
  
  
  /*** 'Last 7 days' list ***/
  
  /* Header */
  .search-result__timeframe {
    color: #aaa;
    border-bottom-color: #333;
  }
  
  /** Item **/
  .env-js-capable .search-result-bucket {
    background-color: #222;
  }
  
  .env-js-capable .search-result-bucket:hover {
    background-color: #333;
  }
  
  .search-result-bucket {
    border-bottom-color: #333;
  }
  
  .search-result-bucket__title {
    color: #aaa;
  }
  
  .search-result-bucket__annotations-count-container {
    background-color: #333;
    color: #aaa;
  }
  
  .search-result-bucket__annotations-count-container:hover {
    background-color: #666;
  }
  
  /** Item - expanded **/
  
  .env-js-capable .search-result-bucket.is-expanded {
    background-color: #222;
  }
  
  .annotation-card {
    background-color: #282828;
    border-color: #333;
  }
  
  .annotation-card__username {
    color: #999;
  }
  
  .annotation-card__groupname {
    color: #666 !important;
  }
  
  .annotation-card__quote {
    border-left-color: #333;
    color: #666;
  }
  
  .annotation-card:hover .annotation-card__quote {
    border-left-color: #333;
  }
  
  /* Tag */
  .annotation-card__tag {
    background-color: #222;
    border-color: #333;
    color: #aaa;
  }
  
  /* Share pop-up panel */
  
  .share-widget {
    background-color: #222;
    border-color: #333;
  }
  
  .share-widget::before {
    border-top-color: #444;
  }
  
  .share-widget::after {
    border-top-color: #222;
  }
  
  .share-widget-link {
    background-color: #222;
    border-color: #333;
  }
  
  .share-widget-link__text {
    background-color: #222;
    color: #999;
  }
  
  .share-widget-target {
    border-bottom-color: #333;
  }
  
  
  /* Right-side */
  
  .search-bucket-stats__incontext-link {
    color: #666;
  }
  
  .search-bucket-stats__key {
    color: #666;
  }
  
  .search-bucket-stats__val {
    color: #999;
  }
  
  /* Page No.s */
  
  .pager__item--begin {
    background-color: #222;
  }
  
  .pager__item.is-highlighted {
    background-color: #222;
    color: #999;
  }
  
  .pager__item--end {
    background-color: #222;
  }
  
  .pager__item:hover:not(.is-disabled) {
    background-color: #333;
  }
  
  
  /**** Profile Page ****/
  
  /* Right-Side Headings */
  
  .search-result-sidebar__title, .search-result-sidebar__subtitle {
    color: #0070c0;
  }
  
  /* Search box Tag */
  
  .lozenge {
    background-color: #222;
    color: #999;
  }
  
  .lozenge__close:hover {
    background-color: #282828;
    color: #f00;
  }
  
  .search-bar__input {
    background-color: #282828;
    color: #999;
  }
  
  /* Search box dropdown */
  
  .search-bar__dropdown-menu-container {
    background-color: #282828;
    border-color: #333;
  }
  
  .search-bar__dropdown-menu-title {
    color: #aaa;
  }
  
  .js-search-bar-dropdown-menu-item--active {
    background-color: #333;
  }
  
  /* No Search results */
  .search-result-zero {
    background-color: #282828;
  }
  
  /* Groups */
  
  .dropdown-menu__menu {
    background-color: #282828;
    border-color: #333;
  }
  
  .dropdown-menu__title {
    color: #999;
  }
  
  .dropdown-menu__item {
    color: #999;
  }
  
  .dropdown-menu__item:hover {
    background-color: #333;
  }
  
  .dropdown-menu__item--footer {
    border-top-color: #333;
  }
  
  /* Tag */
  .search-result-sidebar__tag {
    background-color: #222;
  }
  
  .search-result-sidebar__tag-container {
    color: #999;
  }
  
  /* Footer */
  .footer, .footer a {
    color: #999;
  }
  
/*   svg.brand__icon {
    opacity: 0.1 !important;
  } */
  
  /*** Notification Settings ***/
  
  .form-input {
    background-color: #282828;
  }
  
  .form-checkbox {
    border-color: #333;
    color: #999;
  }
  
  /***** Without Login *****/
  
  #fusion-gmap-container, #main, #sliders-container, #wrapper, .fusion-separator .icon-wrapper, body, html {
    background-color: #282828;
  }
  
  .fusion-modal h1, .fusion-post-content h1, .fusion-widget-area h1, .post-content h1, .title h1 {
    color: #aaa;
  }
  
  .fusion-header {
    background-color: #282828;
  }
  
  .h-h1-subhead {
    color: #aaa;
  }
  
  #main .post h2, .fusion-modal h2, .fusion-post-content h2, .fusion-title h2, .fusion-widget-area h2, .post-content h2, .search-page-search-form h2, .title h2 {
    color: #aaa;
  }
  
  .menu-item a {
    color: #aaa !important;
  }
  
  .walkthrough .wt-images figure img {
    filter: invert(84%);
  }
  
  .walkthrough .wt-captions figcaption {
    background-color: #282828;
  }
  
  .walkthrough .wt-captions figcaption strong {
    color: #aaa;
  }
  
  .walkthrough .wt-captions figcaption.wt-selected {
    background-color: #333;
  }
  
  
}

@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(ftp://), url-prefix(file://) {
  
  /** Toggle Sidebar **/
  .annotator-frame .annotator-frame-button {
    background: #282828;
    border-color: #333;
  }
  
  .annotator-frame .annotator-frame-button:hover {
    color: #5291f8;
  }
  
  /** Selected text Annotation Pop-up **/
  .annotator-adder {
    background-color: #282828;
  }
  
  .annotator-adder--arrow-up::before {
    background-color: #282828;
  }
  
  .annotator-adder--arrow-down::before {
    background-color: #282828;
  }
  
  .annotator-adder-actions__button {
    color: #777 !important;
  }
  
  .annotator-adder-actions .annotator-adder-actions__button:hover {
    color: #0070c0 !important;
  }
  
  .annotator-adder-actions__label {
    color: #999 !important;
  }
  
  .annotator-adder-actions .annotator-adder-actions__button:hover .annotator-adder-actions__label {
    color: #0070c0 !important;
  }
  
  /** Annotation Labels **/
  
  .annotator-frame .annotator-bucket-indicator {
    background-color: #282828;
    border-color: #333;
  }
  
  .annotator-frame .annotator-bucket-indicator .label {
    color: #aaa;
  }
  
  .annotator-frame .annotator-bucket-indicator::before {
    border-right-color: #444;
  }
  
  .annotator-frame .annotator-bucket-indicator::after {
    border-right-color: #282828;
  }
  
  .annotator-frame .annotator-bucket-indicator.upper::before {
    border-bottom-color: #444;
  }
  
  .annotator-frame .annotator-bucket-indicator.upper::after {
    border-bottom-color: #282828;
  }
  
  .annotator-frame .annotator-bucket-indicator.lower::before {
    border-top-color: #444;
  }
  
  .annotator-frame .annotator-bucket-indicator.lower::after {
    border-top-color: #282828;
  }
  
}

@-moz-document url-prefix("https://hypothes.is/app.html") {
  
  .app-content-wrapper {
    background-color: #282828;
  }
  
  .top-bar {
    background-color: #222;
    border-bottom-color: #333;
  }
  
  .annotator-frame .annotator-frame-button--sidebar_toggle {
    background: #222 !important;
  }
  
  /* Group dropdown e.g. Public */
  .group-list .new-group-btn {
    background-color: #222;
  }
  
  .dropdown span:hover {
    color: #5291f8;
  }
  
  .dropdown-menu__top-arrow {
    border-bottom-color: #333;
  }
  
  .dropdown-menu__top-arrow::after {
    border-bottom-color: #222;
  }
  
  /* Loading... Spinner */
  .spinner::before {
    background-color: #999;
  }
  
  /* Search box */
  .simple-search-input {
    color: #999;
  }
  
  /* Share */
  .sheet {
    background-color: #222;
    border-color: #333;
  }
  
  .sheet .nav-tabs {
    border-color: #333;
  }
  
  .nav-tabs > li.active a {
    border-color: #333;
  }
  
  /* Content */
  
  .selection-tabs__type {
    color: #999;
  }
  
  .annotation-header__user {
    color: #999;
  }
  
  .annotation-unavailable-message {
    border-color: #333;
  }
  
  /** Comments **/
  .thread-list__card {
    background-color: #282828;
  }
  
  .markdown-tools {
    background-color: #222;
    border-color: #333;
  }
  
  .form-input {
    background-color: #282828 !important;
    border-color: #333;
    color: #aaa;
  }
  
  .markdown-preview {
    background-color: #282828;
    border-color: #333;
    color: #aaa;
  }
  
  /* 'Preview' button on top, right */
  .markdown-tools .markdown-tools-button:hover, .markdown-tools .markdown-tools-button:focus, .markdown-tools .markdown-tools-toggle:hover, .markdown-tools .markdown-tools-toggle:focus, .markdown-tools .markdown-tools-badge:hover, .markdown-tools .markdown-tools-badge:focus {
    color: #5291f8;
  }
  
  /* Tags */
  tags-input .tags {
    background-color: #222;
    border-color: #333;
  }
  
  tags-input .tags.focused {
    background-color: #222;
  }
  
  tags-input .tag-item {
    color: #aaa;
    border-color: #333;
    font-size: 100%;
  }
  
  /* 'Post to' button */
  .dropdown-menu-btn__btn {
    background-color: #222;
    color: #aaa;
  }
  
  .dropdown-menu {
    background-color: #222;
    border-color: #333;
  }
  
  .dropdown-menu::before {
    border-bottom-color: #444;
  }
  
  .dropdown-menu::after {
    border-bottom-color: #222;
  }
  
  .dropdown-menu__row:not(:first-child) {
    border-top-style: solid;
    border-top-color: #333;
  }
  
  /* License Footer */
  .annotation-license {
    border-top-color: #333;
  }
  
  /*** Annotations added ***/
  
  /** Annotation row/item **/
  
  .annotation-quote {
    border-left-color: #444;
  }
  
  .thread-list__card:hover > .annotation .annotation-replies__link, .thread-list__card:hover > .annotation .annotation-replies__count, .thread-list__card:hover > .annotation .annotation-action-btn, .annotation:hover .annotation-replies__link, .annotation:hover .annotation-replies__count, .annotation:hover .annotation-action-btn {
    color: #aaa;
  }
  
  /* Reply thread */
  
  .annotation-thread__thread-line {
    border-right-color: #666;
  }
  
  .annotation-thread__collapse-toggle:hover, .annotation-thread__collapse-toggle.is-hovered {
    color: #aaa;
  }
  
  /** Share Pop-up Pane **/
  
  .annotation-share-dialog-target__icon:hover {
    color: #aaa;
  }
  
  .annotation-share-dialog-target {
    border-bottom-color: #333;
  }
  
  .annotation-share-dialog {
    background-color: #222;
    border-color: #333;
  }
  
  .annotation-share-dialog::before {
    border-top-color: #444;
  }
  
  .annotation-share-dialog::after {
    border-top-color: #222;
  }
  
  .annotation-share-dialog-link {
    background-color: #222;
    border-color: #333;
  }
  
  .annotation-share-dialog-link__text {
    color: #aaa;
  }
  
  .annotation-share-dialog-link__btn:hover {
    color: #5291f8;
  }
  
}

@-moz-document url-prefix("https://hyp.is") {
  
  /** Loading Page for Shared Annotation Link **/
  body {
    background-color: #222;
  }
  
  .spinner__stationary-ring {
    border-color: #333;
  }
  
}
