css:
@-moz-document domain('imgur.com') {
    html, body {
        background-color: #F9F9FA;
        color: black;
    }
    .panel {
        background-color: #E3E3E3;
    }
    .textbox {
        background-color: #c9c9c9;
        color: black;
    }
    .stat {
        color: #008FFF;
    }
    .button-css {
        border: none;
        background: #c9c9c9 !important;
    }
    .button-css:hover {
        border: none;
        background: #aaa !important;
    }
    .panel .thumbs img {
        border-color: #c9c9c9;
    }
    .posts,
    .carousel-button,
    #search_advanced {
        background-color: #c9c9c9;
    }
    #captions .usertext:before {
        border-color: transparent #c9c9c9;
    }
    #captions .usertext {
        background-color: #c9c9c9;
        color: black;
    }
    .comment-username {
        color: #7789ff !important;
    }
    .jcarousel {
        background-color: #c9c9c9;
        border: 2px solid #c9c9c9;
    }
    .right .panel.menu .button:hover {
        background-color: #aaa;
    }
    .right .panel.menu .active {
        border-right: 14px solid #c9c9c9;
    }
    .right .panel.menu .button:hover .active {
        border-right: 14px solid #aaa;
    }
    #edit-sentence,
    .sentence-sorting,
    #search_advanced,
    .sentence-sorting .selection,
    #captions .author,
    .bad-captions .link,
    .comment-reply-count,
    .load-more {
        color: black !important;
    }
    input[type="text"],
    input[type="password"],
    input[type="email"],
    textarea,
    select {
        background-color: #aaa;
    }
    .sg-title {
        color: inherit;
    }
    .caption-gradient-wrapper .caption-gradient {
        background: #c9c9c9;
    }
}
