css:
@-moz-document domain("www.insomnia.gr")
{
/*!
by Alexandros_D
*/

::-webkit-scrollbar
  {
    background: #101010!important;
    width: 8px!important;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #5d5d5d!important;
  }

/* Top Banner */
.banner-728x90-top
  {background:#232323!important;}

/* Header */
.site-header .logo-container, .site-header .menu-toolbox-container, .site-header .togglers
  {background:#232323!important;}

#ipsLayout_header nav
  {background:#232323!important;}

#ipsLayout_header header
  {background-color:#232323!important;}

/* Logo */

#elLogo img
  {filter:invert(85.9%)!important;}

/* Main Nav */
.ipsNavBar_primary > ul > li > a
  {background:none!important;
   color:#FFFFFF!important;}

.site-header .menu-toolbox-container .main-navigation ul li.ipsNavBar_active a, .site-header .menu-toolbox-container .main-navigation ul li:hover a
  {background:none!important;}

/* User Nav */
.ipsMenu
  {background:#232323!important;
   border-top:4px solid #4b4b4b!important;}

.ipsMenu:after, .ipsMenu:before 
  {border-color:transparent transparent #4b4b4b!important;}

.ipsMenu_item > a, .ipsMenu_item > span
  {color:#d8d8d8!important;}

.ipsMenu_title
  {background:#4b4b4b!important;
   color:#acacac!important;}

/* Notification&Messages Nav */
.ipsType_sectionHead
  {color:#d8d8d8!important;}

.ipsDataList_readStatus .ipsDataItem:not( .ipsDataItem_unread ):not( .ipsDataItem_selected ):not( .ipsModerated )
{background:#232323!important;}

.ipsApp .ipsButton_primary
  {background:#707070!important;}

/* Login/Register Nav */

.toolbox #elUserNav li#elSignInLink a
  {padding:10px 10px 5px!important;}

.toolbox #elUserNav li.sign-upli
  {padding:7px 0px 5px!important;}

#elRegisterButton.ipsButton
  {background:#ff0000!important;}

/* Search Nav */
.site-header .togglers .search-btn
  {background:#232323!important;}

#ipsLayout_header header{background-color:#1c1c1c!important;}

.site-header .search-form-container > div
  {background:#1c1c1c!important;}

.search-form-container #elSearch #elSearchFilter
  {background:#111111!important;}

.search-form-container #elSearch #elSearchFilter .fa
  {background:#111111!important;}

.search-form-container #elSearch input[type="search"]
  {background:#111111!important;}


/* Main Body */

body
  {background:#111111!important;}

.featured .featured-entry
  {background:#1c1c1c!important;}

.featured .dashed__top > div
  {height:35px!important;}

.featured .grid__cell-footer
  {height:35px!important;}

.box__white
  {background:#1c1c1c!important;}

.featured .featured-large .featured-entry::after
  {background:#1c1c1c!important;}

.featured .featured-entry h2 a
  {font-size:22px!important;}

.featured .featured-entry .category a
  {background:#eeeeee!important;
   border-radius:2px!important;
   color:#3e3e3e!important;
   font-weight:400!important;}

.featured .comments
  {background:#5d5d5d!important;}

.featured .comments a
  {font-size:16px!important;}

.featured .author
  {font-size:14px!important;}

.featured .timestamp
  {color:#707070!important;
   font-weight:400!important;}

/* Widgets */
.widget .widget-title
  {color:#707070!important;
   font-weight:400!important;}

/*==========FORUMS=============*/

.ipsType_pageTitle
  {color:#FFFFFF!important;}

.ipsBox
  {border:1px solid #232323!important;
   border-radius:3px!important;}

.ipsType_sectionTitle
  {background:#1c1c1c!important;}

.cForumRow_hidden .cForumTitle
  {opacity:0.8!important;}

.cForumToggle
  {opacity:0.4!important;}

.ipsApp .ipsResponsive_productsblock .ipsButton_verySmall
  {background:#ff0000!important;}

.ipsApp .ipsButton_link
  {border:0px!important;}

.ipsApp .ipsButton_primary
  {border:0px!important;}

.ipsHovercard
  {background:#232323!important;
   border:1px solid #232323!important;}

.ipsHovercard_stemBottom .ipsHovercard_stem
  {border-color:transparent transparent #232323 transparent!important;}

.ipsHovercard_stemBottom .ipsHovercard_stem:before
  {border-color:transparent transparent #232323 transparent!important;}

.ipsAreaBackground
  {background:#232323!important;}

.ipsHovercard.ipsHovercard_stemLeft
  {margin-left:-60px!important;}

/*==========REVIEWS=============*/

/* Content Text Block */
.content_text_block
  {background:#111111!important;}

.review-grids h1
  {font-size:30px!important;}

.blue-contentbg .content_text_block
  {background:#191919!important;}

.blue-contentbg .content_text_block .comments
  {background:#5d5d5d!important;}

.blue-contentbg .content_text_block .comments a
  {color:#FFFFFF!important;}

.blue-contentbg .content_text_block .comments .icon-comment
  {background:url('http://www.insomnia.gr/uploads/set_resources_4/d0e70b4cbb9ab8afb1bc1065a3f8487a_icon-icomment-white.png') no-repeat center center!important;}

.content_text_block .timestamp
  {font-size:12px!important;}

/*==========AΓΓΕΛΙΕΣ=============*/

.classifieds__categories ul li.classcat.current a
  {background:#343434!important;
   color:#FFFFFF!important;
   border-radius:0px!important;}

.classifieds__categories ul li .submenu
  {background:#1c1c1c!important;
   border-top:4px solid #1c1c1c!important;}

.classifieds__latest .entry .classified__content
  {background:#1c1c1c!important;}

.classifieds__categories ul li .submenu .items
  {background:#1c1c1c!important;}

.classifieds__categories ul li.classcat.active > a::after
  {color:#1c1c1c!important;}

.classifieds__categories ul li .submenu .categories ul li.active
  {color:#1c1c1c!important;
   font-weight:500;
   font-size:15px;}

/* Search Bar */
.classifieds__search input
  {background:#343434!important;}

.ipsApp label:not( .ipsField_unlimited ), .ipsApp input, .ipsApp button, .ipsApp select, .ipsApp textarea
  {color:#FFFFFF!important;}

.classifieds__search img
  {height:60px!important;}

.classifieds__toolbox ul li a{background:#343434!important;
  border-radius:5px!important;}

.classifieds__toolbox ul li span
  {color:#FFFFFF!important;}

.widget.widget-stream .tabs ul li a.active{background:#1a1a1a!important;}

.widget.widget-stream .tabs ul li a
  {background:#232323!important;}

.widget.widget-stream .section
  {background:#1a1a1a!important;}

.article .main-content p, .article .main-content li, .article .main-content ol, #elProductInfo_content p, #elProductInfo_content li, #elProductInfo_content ol, .comment__container p
  {color:#FFFFFF!important;}


.widget.widget-stream .section .item-content .meta .category a
  {background:#5d5d5d!important;
   border-radius:5px;}

.widget.widget-classifieds .featured .price
  {max-width:170px!important;}

#elStream_content{background:#1a1a1a!important;}

.featured .author a
  {font-weight:bolder!important;}

.widget .author a
  {font-weight:bolder!important;}

.review-missed .author
  {font-size:14px!important;}

.widget.widget-stream .section .item-content h3
  {font-size:14px!important;}

.widget.widget-stream .section .item-content h3 a
  {font-weight:bolder!important;}

}
