css:
@-moz-document domain("filecrypt.cc") {
.container .support, .container.window .topback {
    display: none;
}
.footer, .content, .big .box {
    background-color: #131313 !important;
}
body, h2, h3, h4, p {
    color: #1B91DB;
}
.container.window tr:nth-child(even) td {
    background: #282828!important;
}
.container.window tr:nth-child(odd) td {
    background: #3E4046!important;
}
ul.backup li, .mirrorgroup > span {
    opacity: 1;
}
select, input, textarea {
    background: #292A2E!important;
}
.mirrorgroup.new button {
    background: #292A2E;
    color: #1B91DB;
}
.create div.containersetup {
    border: 2px solid #1B91DB;
}
div.containersetup label > div {
    opacity: 1;
}
div.alert, .content.abuseform textarea, .content.contactform textarea, .content.abuseform input, .content.contactform input, .content.midbox input {
    background: #292A2E!important;
}
div.containersetup .wrapper > div, div.midbox, .setup, .window, .protection button, .container button.cnl, .container button.dlcdownload, button, .content.contactform, .content.abuseform, .content.stats {
    background: #131313!important;
}
a, a:visited, a:hover, a:active, a:focus {
    color: #70c8ff;
}
ul.backup:hover + textarea{
  animation-duration: 0ms;
}
.top {
    background: #1B91DB;
}
.top h1.logo {
    color: #fff;
}
svg.logo {
    fill: #fff;
}
table td {
    border-bottom: 0px;
}
}
