css:
@-moz-document domain("youtube.com") {
html,
body,
ytd-app,
ytd-watch,
ytd-account-settings,
.ytd-multi-page-menu-renderer,
.header.ytd-account-settings,
ytd-searchbox #container,
#channel-header.ytd-c4-tabbed-header-renderer,
ytd-browse,
#guide-wrapper {
    background-color: #191919 !important;
}
ytd-masthead,
#tabs-inner-container {
    background-color: #1f1f1f !important;
}
app-drawer #header {
    background-color: #0e0e0e !important;
}
#sections,
#guide-inner-content {
    background-color: #151515 !important;
}
#info .title,
ytd-compact-video-renderer h3 span,
ytd-video-meta-block div.ytd-video-meta-block,
ytd-video-meta-block yt-formatted-string,
yt-view-count-renderer span,
ytd-toggle-button-renderer,
ytd-button-renderer,
#upnext,
#simplebox-placeholder.ytd-comment-simplebox-renderer,
#content-text.ytd-comment-renderer,
#author-text.yt-simple-endpoint.ytd-comment-renderer,
a.yt-simple-endpoint.yt-formatted-string,
.content.ytd-video-secondary-info-renderer,
.date.ytd-video-secondary-info-renderer,
.content-text.ytd-metadata-with-image-row-renderer,
.content.ytd-metadata-row-renderer,
.more-button.ytd-video-secondary-info-renderer,
.less-button.ytd-video-secondary-info-renderer,
.more-button.ytd-comment-renderer,
.less-button.ytd-comment-renderer,
paper-button,
yt-formatted-string.ytd-comments-header-renderer,
#icon-label.yt-dropdown-menu,
ytd-comment-action-buttons-renderer,
#alert-message.ytd-consent-bump-renderer,
.gsfs,
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer,
#guide-section-title.ytd-guide-section-renderer,
yt-formatted-string.ytd-guide-signin-promo-renderer,
#footer.ytd-guide-renderer > #copyright,
ytd-toggle-theme-compact-link-renderer,
paper-item,
ytd-simple-menu-header-renderer,
.description.ytd-toggle-item-renderer,
.header.ytd-account-settings,
#restricted-mode.ytd-account-settings .container.ytd-account-settings,
#channel-title,
#subscriber-count,
#video-title,
#title,
.title,
paper-tabs,
paper-tab.iron-selected,
paper-ripple,
#subtitle,
#metadata-line,
.input-content.paper-input-container input,
.input-content.paper-input-container textarea,
.input-content.paper-input-container iron-autogrow-textarea,
.input-content.paper-input-container .paper-input-input,
#primary-items,
#description,
#metadata,
#left-column,
#right-column,
#result-count,
#corrected,
#corrected-link,
.video-title.ytd-watch-card-video-list-renderer,
#filter-group-name,
yt-formatted-string,
#original,
paper-tab:hover {
    color: #999 !important;
}
yt-icon,
.yt-icon-container.yt-icon,
#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
    fill: #999 !important;
}
ytd-page-manager {
    margin-top: 56px;
}
ytd-searchbox #container {
    border-color: #333 !important;
    box-shadow: none !important;
}
#sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child),
#sections.ytd-multi-page-menu-renderer > .ytd-multi-page-menu-renderer:not(:last-child) {
    border-color: #999 !important;
}
#search-form yt-icon path {
    fill: #a9a9a9 !important;
}
ytd-searchbox input {
    color: #a9a9a9 !important;
}
ytd-searchbox button {
    background-color: #393939 !important;
    border: none !important;
    height: 32px !important;
}
.sbdd_b,
.sbsb_a {
    background: #292929 !important;
}
.sbdd_a {
    z-index: 10000 !important;
    border: solid 1px #393939;
}
.sbdd_a,
.sbdd_c {
    top: 44px !important;
}
.sbdd_b {
    border-color: #393939 !important;
}
.sbsb_d {
    background-color: #393939 !important;
    color: #999 !important;
}
ytd-video-primary-info-renderer {
    border-bottom: none !important;
}
yt-img-shadow {
    opacity: 0.75 !important;
}
ytd-button-renderer.style-destructive,
yt-next-continuation paper-button,
ytd-mini-channel-renderer ytd-button-renderer #button.ytd-button-renderer,
#details-container ytd-button-renderer #button.ytd-button-renderer {
    background-color: #444 !important;
}
/* autoplay button */

.toggle-bar.paper-toggle-button {
    background-color: #666;
}
.toggle-button.paper-toggle-button {
    background-color: #666;
}
paper-toggle-button[checked]:not([disabled]) .toggle-button.paper-toggle-button {
    background-color: #999;
}
paper-toggle-button[checked]:not([disabled]) .toggle-bar.paper-toggle-button {
    background-color: #aaa;
}
ytd-compact-autoplay-renderer,
ytd-video-secondary-info-renderer,
#placeholder-area.ytd-comment-simplebox-renderer {
    border-bottom-color: #666 !important;
}
ytd-author-comment-badge-renderer,
#name.ytd-author-comment-badge-renderer,
.badge-style-type-simple.ytd-badge-supported-renderer {
    background-color: #333;
    color: #888 !important;
}
#author-text {
    font-weight: bold;
}
.badge-style-type-live-now.ytd-badge-supported-renderer {
    background: #292929 !important;
    border-color: #ab2626 !important;
}
.badge-style-type-live-now.ytd-badge-supported-renderer {
    color: #ab2626 !important;
}
.arrow {
    background-color: #444 !important;
}
#video-title {
    color: white !important;
}
.style-scope {
	background-color: #191919 !important;
}
}
