css:
@-moz-document url-prefix(https://facebook.github.io/react-native/)
{
  body {
    background: #393939;
    color: #f9f9f9;
}

.container.mainContainer {
    background: #393939 !important;
}

.mainContainer h1, .mainContainer h2, .mainContainer h3, .mainContainer h4, .mainContainer h5, .mainContainer h6{
  color: #ff5068 !important;
}

code{
  background-color: rgba(0, 0, 0, 0.07) !important;
  box-shadow: 2px 0 rgba(0, 0, 0, 0.07), -2px 0 rgba(0, 0, 0, 0.07) !important;
  color: #66ff00 !important;
}

.hljs{
  background: #292929;
}

.navGroup{
  background-color: #494949 !important;
}

nav.toc .toggleNav ul li a{
  color: #f9f9f9; 
}

.button {
    color: #00c7fa;
    border: 1px solid #00c7fa;
}

table tr, table tr:nth-of-type(even), table tr:nth-of-type(odd){
 background: #393939;
 color: #f9f9f9 !important;
}

table tr th{
 color: #ffffff;
}
}
