css:
@-moz-document domain("manhuagui.com"), domain("mhgui.com") {
iframe,
.footer,
.gg_950,
.gg_980,
.sider.pa,
.ra-4x1,
#pagination {
	display: none !important
}

::-webkit-scrollbar {
	max-width: 1mm;
	max-height: 1mm;
}

::-webkit-scrollbar-thumb {
	background: #26c;
	border-radius: 1mm
}

html,
body,
.update-cont li p,
.cover-list li p,
.hlist li em,
.idx-mc-cont p,
.crumb,
.book-intro {
	font: 400 calc(9pt + .3vw) sans-serif;
}

.content_r .text {
	font-size: calc(10pt + .3vw);
}

body {
	min-height: calc(100vh - 8mm);
	background: radial-gradient(circle at 50vw 30vh,
			rgba(128, 128, 128, .6) 10vw,
			rgba(128, 128, 128, .5) 30vw,
			rgba(128, 128, 128, .3) 60vw)
}

body>.header {
	position: fixed;
	top: -9mm;
	left: 0;
	width: 100vw;
	height: auto;
	background: rgba(60, 60, 60, .3);
	padding: 1mm 0;
	margin: 0;
	opacity: .5;
	z-index: 11;
	border: none;
	transition: .6s, top .6s .2s, width 0s;
	backdrop-filter: blur(1mm);
	-webkit-backdrop-filter: blur(1mm);
}

body>.header:hover {
	top: 0;
	opacity: 1;
	background: rgba(40, 40, 90, .7);
	box-shadow: 0 1px 0 #666;
	transition: .2s, width 0s;
	backdrop-filter: blur(3mm);
	-webkit-backdrop-filter: blur(3mm);
}

body>.header .w980 {
	width: calc(29em + 560px)
}

.w860 {
	width: calc(100% - 140px);
}

.header-inner {
	padding: 1mm 0;
	border: none;
}

.topper {
	position: relative;
	top: -1mm
}

.footer-main {
	height: auto;
}

#hList {
	width: 24em
}

#hList li {
	padding: 1mm 2mm;
	border-radius: 2mm;
}

.hlist li:hover {
	background: #eef;
}

.hList li>a.red {
	display: inline-block;
}

.htime {
	font-size: .9rem;
}

.hList li>a.hDelete {
	margin-left: 2mm
}

.hHover {
	background: #ccd
}

.nav a,
.more,
.menu-end {
	border-radius: 2mm;
	border: none;
}

.more {
	padding: 0 1.2em 0 .6em;
}

.more i {
	border-left-color: transparent;
	border-right-color: transparent;
}

.nav a:hover {
	background-color: #113;
}

.more a {
	padding: 0 .2em;
	margin: 0 -.2em;
	border-radius: .3em;
	transition: .13s
}

.more a:hover {
	text-decoration: none
}

.nav-sec a {
	display: block;
	padding: .15em 0;
	text-align: center;
	background: none
}

.nav-sec a:before {
	content: "‚ñ∏";
	margin-right: .4em
}

.user-view,
.user-area,
.user-feedback,
.user-view .panel,
.cover-list li p {
	width: auto;
	min-width: 4rem
}

.user-area a:hover {
	text-decoration: none;
}

.handle,
.user-feedback {
	padding: 0 2mm;
	transition: .2s
}

.user-feedback:hover {
	color: navy;
	background: #fff;
	border-radius: 5px;
	box-shadow: 0 1px 4px rgba(0, 0, 0, .5);
}

.user-feedback:active {
	background: #ccc;
	box-shadow: inset 0 1px 4px rgba(0, 0, 0, .5);
	transition: .1s
}

.user-area,
.topper a[href="/user/book/shelf"],
.topper a[href="/user/message/accept"] {
	height: 29px;
}

.topper a[href="/user/book/shelf"],
.topper a[href="/user/message/accept"] {
	font-size: 0;
}

.topper a[href="/user/book/shelf"]:before,
.topper a[href="/user/message/accept"]:before {
	font-size: 1rem;
	content: "Êõ¥Êñ∞";
}

.topper a[href="/user/message/accept"]:before {
	content: "Ê∂àÊÅØ";
}

.topper a[href="/user/book/shelf"]>font,
.topper a[href="/user/message/accept"]>font {
	display: inline-block;
	min-width: 1rem;
	height: 1rem;
	font-size: .9rem;
	color: #fff;
	background: #d33;
	line-height: 1.11;
	padding: .2em;
	margin-left: .2em;
	border-radius: 1em;
}

.topper a[href="/user/message/accept"]>font {
	background: #00f
}

.topper a[href="/user/book/shelf"]:hover>font {
	animation: pulse .7s infinite;
}

.update,
.cmt-list {
	height: auto
}

.update-cont {
	width: 1440px;
	height: 21vw;
	min-height: 230px;
	max-height: 300px;
	margin: 0 auto;
}

.update-wrap {
	width: auto;
	min-width: 5000%
}

.update-cont ul {
	display: flex;
	width: 1440px;
	min-width: 1000px
}

.update-cont li {
	flex: 1 1 15%;
	margin: 0 12px
}

.w996 {
	min-width: 1000px;
}

.w980.title {
	position: absolute;
	top: 15mm;
	left: 0;
	width: calc(100% - 10mm);
	line-height: 2em;
	border: none;
	padding: 0 5mm;
	z-index: 2
}

.w980.title * {
	color: #ccc
}

.w980.title>div {
	display: inline-block;
	padding: 1mm 2mm;
	font: 11pt sans-serif;
	vertical-align: middle;
	line-height: 1;
	border-radius: 2mm;
	text-shadow: 0 0 3px #000;
	background: none;
	opacity: .25;
	transition: .2s, width 0s
}

.w980.title>div:hover {
	background: rgba(0, 0, 0, .6);
	box-shadow: 0 1px 2mm 1px rgba(0, 0, 0, .5);
	opacity: 1
}

.title h1 a:hover {
	color: #aaf;
	text-decoration: none;
}

.w980.sub-btn {
	position: absolute;
	width: 98vw;
	left: 1vw;
	background: none;
	margin: 3mm auto 0;
	border: none;
	opacity: .15;
	z-index: 10;
	transition: .3s, width 0s
}

#shortcuts,
#zoomtool,
#qrcodes {
	z-index: unset;
}

.w980.sub-btn:hover {
	opacity: 1;
}

.w980.sub-btn>div {
	background-color: #ccc
}

.w980.sub-btn:hover>div {
	box-shadow: 0 0 5px 1px rgba(0, 0, 0, .5);
}

.support li a {
	background-color: transparent
}

.tbCenter {
	width: auto;
	height: auto;
	border: none;
	background: none;
}

.w998 {
	width: auto;
	min-width: 1000px;
	max-width: 2100px;
	margin: 1mm 12mm;
	box-shadow: none
}

.nav-sub-cont {
	padding: 2mm;
	border-radius: 0 0 3mm 3mm;
	box-shadow: 0 1mm 3mm rgba(0, 0, 0, .3)
}

.nav-sub .nav-sub-cont-bg {
	display: block;
	width: calc(100vw - 24mm - 6px);
	min-width: calc(1000px - 6px);
	max-width: calc(2100px - 6px);
	background: none;
	opacity: 0;
	visibility: hidden;
	transition: .2s .2s
}

.nav-sub-over .nav-sub-cont-bg {
	opacity: .93;
	visibility: visible;
	transition: .2s 0s
}

.nav-bar {
	width: calc(100% - 24mm);
	min-width: 900px;
	font: 400 12pt sans-serif;
	line-height: 40px;
	padding: 0;
	margin: 0 auto
}

.nav-sub {
	width: 15%
}

.nav-sub h2 {
	text-align: center;
	background: none;
	padding: 0
}

.nav-sub h2:after {
	display: inline-block;
	content: "‚û§Ô∏é";
	margin-left: .4em;
	transform: rotate(0deg);
	transition: .4s
}

.nav-sub-over h2:after {
	transform: rotate(90deg);
	transition: .2s
}

.nav-main {
	width: 74%
}

.nav-less {
	width: calc(11% - 1px)
}

.nav-main li.first,
.nav-main li {
	width: calc(100% / 6)
}

.nav-sec {
	width: 16em
}

.nav-sec li {
	width: 25%;
	padding: 0;
	margin: 0;
	border: none;
}

.nav-sec li a {
	transition: .2s;
}

.crumb {
	margin-bottom: 4mm
}

.detail-list li {
	width: calc(100% - 12em);
}

.book-intro a.intro-act {
	background: none;
}

.book-intro a.intro-act:after {
	content: "üîΩ";
	margin-left: .3em;
}

.book-intro a.intro-act.intro-up:after {
	content: "üîº";
}

.w728,
.fl[style="width: 728px;"] {
	width: calc(100% - 270px) !important
}

/* ============================================= */
.box-gray {
	border: none
}

.filter-nav {
	max-height: 3em;
	padding: 2mm;
	background: #eee;
	overflow: hidden;
	border-radius: 0 0 3mm 3mm;
	box-shadow: inset 0 0 2mm #555;
	transition: .5s
}

.filter-nav:hover {
	max-height: 25em;
	transition: .2s
}

.filter-nav .filter {
	width: auto;
	height: auto;
	border-bottom-color: #ccc
}

.nav-sub-cont .filter {
	width: 100%;
	height: auto;
	line-height: 1.4;
	padding: 1mm 0;
	margin: 0
}

.nav-sub-cont .filter label,
.nav-sub-cont .filter ul,
.filter-nav .filter label,
.filter-nav .filter ul {
	display: inline-block;
	float: none;
	width: auto;
	max-width: calc(100% - 5.5em);
	vertical-align: top;
	font: 400 12pt sans-serif
}

.nav-sub-cont .filter label,
.filter-nav .filter label {
	width: 5em;
	text-align: right;
	line-height: 1.5;
}

.filter-nav .filter li {
	display: inline-block;
	float: none;
	padding: 0 .2em
}

.filter:hover {
	background: rgba(0, 0, 0, .08)
}

.suggest li,
.suggest li span a {
	height: auto;
	font: 11pt sans-serif;
}

.suggest li span.state {
	display: inline-block;
	clear: both;
}

.similar-list,
.book-list ul,
.latest-list ul {
	width: auto;
	padding: 0 2mm;
	margin: 0;
	display: flex;
	flex-wrap: wrap;
}

.book-similar {
	height: auto
}

.book-sort {
	margin-bottom: 1mm;
}

.book-list li,
.latest-list li {
	float: none;
	flex: 1 1 auto;
	width: auto;
	min-width: 180px;
	max-width: 300px;
	height: auto;
	max-height: 360px;
	padding: 2mm;
	margin: 1mm -2px;
	border-radius: 2mm;
	transition: .2s;
}

.book-list li:hover,
.latest-list li:hover {
	z-index: 2;
	box-shadow: 0 1px 3mm rgba(0, 100, 200, .7);
}

.cover-list li,
.idx-mc-cont li {
	height: auto;
	padding: 0;
	margin: 1mm;
}

.book-list li p,
.similar-list p {
	width: calc(100% - 2em);
	margin: 0
}

.similar-list li {
	flex: 1 1 auto;
	height: auto;
}

.cover,
.bcover,
.hbcover,
.scover {
	width: 100%;
	height: calc(100% - 15mm);
}


.cover .bg,
.cover img,
.bcover .bg,
.bcover img,
.hcover .bg,
.hcover img,
.scover .bg,
.scover img {
	width: 100%;
	height: 100%;
	border-radius: 2mm
}

.cover img,
.bcover img,
.hcover img,
.scover img {
	object-fit: cover;
	padding: 0
}

.cover .sl,
.bcover .sl,
.hcover .sl,
.scover .sl {
	top: -2px;
	left: -2px
}

.chapter,
.chapter-list,
.chapter-cont h5 {
	width: 100%
}

.chapter-list li {
	width: 8em
}

.chapter-list li a {
	font-size: 1rem;
	padding: 0 .3em
}

.chapter-list li a,
.chapter-list li a span {
	border-radius: 5pt;
}

/* ====================================== */
.commentBox {
	background: rgba(200, 200, 200, .1)
}

.textareawrap,
.comment_tab,
.comment_con {
	margin: 0;
	padding: 0 3mm
}

.comment_con_li,
.reply_content .comment_con_li {
	margin: 1mm;
	padding: 2mm;
	border-radius: 2mm;
	transition: .2s
}

.comment_con_li:hover {
	background: rgba(255, 255, 255, .5);
	transition: .1s
}

.reply_content {
	background: rgba(99, 99, 77, .06);
	padding: 0;
	margin: 1mm 0;
	border-radius: 2mm;
	box-shadow: inset 0 0 3mm #aaa
}

.content_r .reply_content .text {
	color: #544;
}

.book-list li .updateon,
.content_r .text.del {
	color: rgba(0, 0, 0, .4);
}

.chapter-cont {
	width: calc(100% - 300px)
}

.book-detail {
