	filter: brightness(88%) contrast(130%);
}

#mangaMoreBox img {
	display: inline-block !important;
}

.img_info {
	direction: ltr;
	font-size: 10pt;
	color: #ccc;
	background: #1a1a1a;
	padding: 0 .6em;
	margin: -32px 3% 0 0;
	border-radius: 1em;
}

#page,
.img_page,
.title h1,
.title h1 a,
.title h2 {
	font: 400 12pt sans-serif;
}

.img_page {
	color: #c76;
	padding: 0 .1em;
}

.main-btn {
	position: fixed;
	bottom: -7mm;
	left: 0;
	width: 100vw;
	background: rgba(128, 128, 128, .3);
	padding: 2mm 0;
	z-index: 11;
	opacity: .5;
	box-shadow: 0 -1px 0 rgba(0, 0, 0, .4);
	transition: .6s, bottom .6s .2s, width 0s;
	backdrop-filter: blur(1mm);
	-webkit-backdrop-filter: blur(1mm);
}

.main-btn:hover {
	bottom: 0;
	background: rgba(128, 128, 128, .7);
