	backdrop-filter: blur(3mm);
	-webkit-backdrop-filter: blur(3mm);
}

.main-btn a,
.main-btn span {
	min-width: 1em;
	border-radius: 2mm;
	margin: 0 1mm
}

.more-hover div.content {
	background: #e0e6f3;
}

.more-hover {
	filter: invert(.9) hue-rotate(180deg);
}

.more div.content {
	display: block;
	visibility: hidden;
	opacity: 0;
}

.more-hover div.content {
	visibility: visible;
	border-radius: 2mm;
	opacity: 1;
	box-shadow: 0 1mm 3mm #333;
}

.cover-list,
.latest-list,
.latest-list ul,
.idx-mc-cont ul {
	width: auto;
	flex-wrap: wrap;
}

.cmt-cont,
.index-cont,
.idx-mc-cont,
