	transform: scale(1.05)
}

.cover,
.cover .bg,
.latest-list p,
.cover .mk,
.cover .tt {
	width: 100%;
	text-align: center
}

.cover img {
	width: 100%;
	object-fit: cover
}

.cover img,
.cover .bg {
	height: 100%;
	border-radius: 2mm
}

.scover .bg,
.cover .bg,
.bcover .bg,
.hcover .bg {
	background: linear-gradient(0deg,
			rgba(0, 0, 0, .7) 0,
			transparent 3em)
