	box-shadow: 0 0 1mm #fff
}

.smh-msg-cont,
.smh-msg-cont span {
	width: 100%;
	height: 100%;
	text-align: center;
	font-size: 4vw;
	text-shadow: 1px 1px 3mm #000;
	line-height: 40vh
}

.section {
	width: calc(100% - 24mm);
	min-width: 1024px;
	max-width: 2100px
}

.u-tab-content {
	background: rgba(255, 255, 255, .3);
	font-size: 0
}

.title-div {
	margin: 0 5mm 2mm;
	border: none
}

.title-div strong {
	float: none;
	background: none;
	padding: 0
}

.title-div strong:before {
	display: block;
	float: right;
	width: calc(100% - 6em);
	height: 1px;
	margin: .7em 0 0;
	content: " ";
	background: #aaa
}

.dy_content {
	padding-bottom: 0;
}

.dy_content .dy_content_li {
	background: #eee;
	padding: 1.5mm;
