	margin: 0 -1.7em 0 .5em
}

.dy_r h3 {
	margin-bottom: 2mm
}

.dy_r p {
	display: flex;
	height: auto;
	font-size: 10pt;
	line-height: 1.2;
	margin: 1mm 0;
	white-space: nowrap;
}

.dy_r p em.c_space {
	width: auto;
	height: auto;
	margin-right: 1em;
}

.dy_r p em.c_space a {
	color: #c22;
	text-shadow: 0 0 2px #fff;
}

.dy_r p .new {
	width: auto;
	background: none;
	margin: 0 1mm;
}

.dy_r p .new:before {
	display: block;
	content: "New";
	line-height: 1;
	color: #fff;
	background: #e33;
	padding: 0 .2em;
	margin-left: .4em;
	border-radius: 2pt;
	text-shadow: 0 0 2px #722;
	box-shadow: 0 0 0 .15em #e33;
}

.dy_check,
.dy_r a.begin,
.dy_r a.qx {
	margin: 3mm 1mm 0;
	width: 5em;
	height: auto;
	line-height: 1.6;
	border-radius: 2mm;
	transition: .5s;
}

.dy_r a.begin {
	border-color: #6bd;
}

.dy_r a.qx {
	border-color: #aaa;
}

.u-tab-head p a {
	transition: .2s;
}

.dy_r a.begin:hover,
.dy_r a.qx:hover {
	color: #eee;
	background: #ddf;
	text-shadow: 1px 1px 0 #004,
		-1px 1px 0 #004,
		1px -1px 0 #004,
		-1px -1px 0 #004,
		0 0 .5em #36b;
	transition: .1s;
}

.shadow {
	box-shadow: 0 2px 1mm rgba(0, 0, 0, .3)
}

@keyframes pulse {
	15% {
		transform: scale(1.2)
	}
}

@media screen and (min-width: 1200px) {

	.cover {
		height: 18vw;
	}

	.dy_content .dy_content_li {
		width: calc(50% - 3mm);
	}
}

@media screen and (min-width: 1600px) {

	.cover-list li,
	.idx-mc-cont li {
		width: calc(10% - 2mm);
