@-moz-document domain("zen.yandex.ru") {
/* GRID:
    Grey Backgrounds:
        - #1d1d1d hsb(0, 0, 11)
        - #242424 hsb(0, 0, 14)
        - #2b2b2b hsb(0, 0, 17)
        - #333333 hsb(0, 0, 20)
        - #3b3b3b hsb(0, 0, 23)
        - #424242 hsb(0, 0, 26)
        - #4a4a4a hsb(0, 0, 29)
        - #525252 hsb(0, 0, 32)

    Accent:
        - #3570e5 hsb(220, 77, 90)
		- #02ed97 hsb(220, 77, 99)

    Foreground:
        - #999691 hsb(42, 5, 60)
        - #a6a39d hsb(42, 5, 65)
        - #b3b0aa hsb(42, 5, 70)
        - #bfbcb6 hsb(42, 5, 75)
        - #ccc9c2 hsb(42, 5, 80)
        - #d9d5ce hsb(42, 5, 85)
        - #e5e2da hsb(42, 5, 90)
*/

:root {
    --color_bg0: #0e0e0f;
    --color_bg1: #131314;
    --color_bg2: #1a1b1c;
    --color_bg3: #212224;
    --color_bg4: #2f3133;
    --color_bg5: #37393b;
    --color_bg6: #3e4042;
    --color_bg7: #46484a;
    --color_bg8: #4e5052;
    
    --color_bg1_o09: rgba(29, 29, 29, 0.9);
    
    --color_accent1: #5790ff;
    --color_accent2: #79a7ff;
    
    --color_fg1: #999691;
    --color_fg2: #a6a39d;
    --color_fg3: #b3b0aa;
    --color_fg4: #bfbcb6;
    --color_fg5: #ccc9c2;
    --color_fg6: #d9d5ce;
    --color_fg7: #e5e2da;
    
    --color_bg_shift: rgba(255, 255, 255, 0.05);
}

/* BACKGROUNDS */

body.has-footer #footer {
    background-color: #000;
}

html, body, .zen-app, .page_article, .page_article-editor, .page_article-m, .article, .zen-page._theme_white .feed-header__container {
	background-color: var(--color_bg1);
}

.article-stat-tip, .zen-popup._theme_normal, .zen-popup._theme_normal-dropdown, .article-root, .zen-check-button._theme_normal._checked._pressed, .article-footer_similar {
	background-color: var(--color_bg2);
}

.more-button__content, .more-button._theme_white .more-button__container, .zen-check-button._theme_normal._checked {
	background-color: var(--color_bg3);
}

.zen-check-button._theme_normal._checked:hover {
	background-color: var(--color_bg4);
}


/* BORDERS */

.hint-eula._theme_white .hint-eula__link {
	border-color: var(--color_fg1);
}

.zen-page._theme_white .feed-header__container {
	border-bottom: 1px solid rgba(0, 0, 0, 0.3);
}

.article-root {
 	border-bottom: 1px solid var(--color_bg_shift);   
}

.article-footer_similar {
	border-top: 1px solid var(--color_bg_shift);
}

.article-stat-tip, .zen-popup._theme_normal, .zen-popup._theme_normal-dropdown, .zen-check-button._theme_normal._checked {
	border: 1px solid var(--color_bg_shift);
}

.more-button__content {
	border: 1px solid var(--color_bg_shift);
}

.article-left-block__block {
	border-color: var(--color_fg1);
}

/* FOREGROUND */

.channel-subscribers, .channel-subscribers._size_m, .zen-page._theme_white .about-link {
	color: var(--color_fg2);
    opacity: 1;
}

.article-block, .article-left-block__title,  .article-header__publisher-link, .back-link, .article-header__title, .subscribe-channel__not-subscribed, .subscribe-channel__subscribed, .article__title, .channel-header__description, .zen-button._theme_clear, .article-block-image__caption, .more-button__text, .zen-page._theme_white .feed-header__title, .zen-page._theme_white .similar__title, .article-footer__title, .article-left-block__share-button a, .hint-eula._theme_white .hint-eula__text, .hint-eula._theme_white .hint-eula__link, .publisher-subscribe__title {
	color: var(--color_fg4);
}

.article-stat-tip *, .article-stat__counts-wrapper *, .article-stat__date, .feed-header__promo-link {
	color: var(--color_fg4);
    opacity: 1;
}

.channel-title, .zen-page:not(._touch) .more-button__content:hover .more-button__text, .zen-button._theme_clear:hover, .zen-check-button._theme_normal._checked:hover, .hint-eula__link:hover, .feed-header__promo-link:hover {
	color: var(--color_fg6);
    opacity: 1;
}

/* ACCENT */

a, .zen-button._theme_link._pressed {
	color: var(--color_accent1);
}

a:hover, a:focus, a:active, .zen-button._theme_link:hover, .zen-page._theme_white .about-link:hover {
	color: var(--color_accent2);
}

/* TEXT */
.article-block {
	font-weight: 300;
}

.article-block {
	text-shadow: 0.7px 0.7px 0 rgba(0, 0, 0, 0.25);
}

/* LAYOUT */

.article-stat__icon_type_perusal, .icon_type_perusal, .article-stat__icon_type_time, .icon_type_time, .article-stat__icon_type_views, .icon_type_views, .icon_type_plus-round, .icon_type_checkmark, .icon_type_like, .icon_type_dislike, .icon_type_like-w, .icon_type_dislike-w, .icon_type_fb, .icon_type_tw, .icon_type_vk, .icon_type_arrow-left, .icon_type_plus-round, .icon_type_plus-round-old, .icon_type_checkmark, .icon_type_play, .icon_type_sad-face, .icon_type_play-again, .icon_type_mark-black, .icon_type_sound, .icon_type_perusal, .icon_type_time, .icon_type_views, .like-icon, .dislike-icon, .liked-icon, .disliked-icon, .z-icon, .cross_white-icon, .article-left-block__icon_type_like, .icon_type_like, .article-left-block__icon_type_dislike, .icon_type_dislike, .article-left-block__icon_type_like-w, .icon_type_like-w, .article-left-block__icon_type_dislike-w, .icon_type_dislike-w, .article-left-block__icon_type_fb, .icon_type_fb, .article-left-block__icon_type_tw, .icon_type_tw, .article-left-block__icon_type_vk, .icon_type_vk, .article-left-block__icon_type_arrow-left, .icon_type_arrow-left, .article-left-block__icon_type_plus-round, .icon_type_plus-round, .article-left-block__icon_type_plus-round-old, .icon_type_plus-round-old, .article-left-block__icon_type_checkmark, .icon_type_checkmark, .article-left-block__icon_type_play, .icon_type_play, .article-left-block__icon_type_sad-face, .icon_type_sad-face, .article-left-block__icon_type_play-again, .icon_type_play-again, .article-left-block__icon_type_mark-black, .icon_type_mark-black, .article-left-block__icon_type_sound, .icon_type_sound, .article-left-block__icon_type_perusal, .icon_type_perusal, .article-left-block__icon_type_time, .icon_type_time, .article-left-block__icon_type_views, .icon_type_views, .article__aside-left-icon_type_like, .icon_type_like, .article__aside-left-icon_type_dislike, .icon_type_dislike, .article__aside-left-icon_type_like-w, .icon_type_like-w, .article__aside-left-icon_type_dislike-w, .icon_type_dislike-w, .article__aside-left-icon_type_fb, .icon_type_fb, .article__aside-left-icon_type_tw, .icon_type_tw, .article__aside-left-icon_type_vk, .icon_type_vk, .article__aside-left-icon_type_arrow-left, .icon_type_arrow-left, .article__aside-left-icon_type_plus-round, .icon_type_plus-round, .article__aside-left-icon_type_plus-round-old, .icon_type_plus-round-old, .article__aside-left-icon_type_checkmark, .icon_type_checkmark, .article__aside-left-icon_type_play, .icon_type_play, .article__aside-left-icon_type_sad-face, .icon_type_sad-face, .article__aside-left-icon_type_play-again, .icon_type_play-again, .article__aside-left-icon_type_mark-black, .icon_type_mark-black, .article__aside-left-icon_type_sound, .icon_type_sound, .article__aside-left-icon_type_perusal, .icon_type_perusal, .article__aside-left-icon_type_time, .icon_type_time, .article__aside-left-icon_type_views, .icon_type_views, .article-header__publisher-link:before, .back-link:before, .channel-menu .zen-button, .zen-page._theme_white .zen-logo._lang_ru, .more-button._theme_white .more-button__icon, .icon_type_more-button {
	filter: invert(0.8);
}

.doc-channel-name._read {
    opacity: .3;
}

.doc._read .doc__title {
    opacity: .5;
}
.doc._read .doc__text {
    opacity: .6;
}

.zen-popup._theme_normal, .zen-popup._theme_normal-dropdown {
    box-shadow: 0 8px 22px 0 rgba(0, 0, 0, .25);
}

.more-button__content {
	border-radius: 20px;
}
.zen-page:not(._touch) .more-button__content:hover {
	opacity: 1; 
}

.more-button__content {
    box-shadow: 0px 2px 12px 2px rgba(0, 0, 0, 0.5);
}

.feed__row:first-of-type {
	padding-top: 8px;
}

.icon_type_checkmark, .subscribe-channel__subscribed {
    filter: invert(0.8) contrast(95%) saturate(0);
}

}
