css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("winfuture.de"){

table[width="212"],
td[width="164"],
td[valign="top"][align="center"] > a,
#skyscraper,
.footerFeedTopBorder,
.footerFeedMiddle,
.footerFeedBottomBorder
{
  display:none !important;
}

table, td, tr
{
  height:100% !important;
  width:100% !important;
}

body > table
{
  margin:auto !important;
}


.newsinnenhead#nointelliTXT
{
 width:100% !important;
 height:100% !important;
 display:inline !important;
}

table[style="margin-bottom: 7px;"] > tbody > tr > td[class="newstext"]
{
 width:1% !important;
}

body > table > tbody > tr > td:first-child,
#nointelliTXT,
.relatednewsbox,
#nointelliTXT + table,
#intelliTXT > table > tbody > tr:first-child,
#intelliTXT > table > tbody > tr[valign="bottom"],
table[align="right"],
body > table > tbody > tr:first-child
{
  width:0% !important;
  height:0% !important;
  display:none !important;
}

}
