@-moz-document domain("roblox.com")
{
/* i really want this to be global */
  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  } /*Ad removal */


  #header
  {
    background-color: #f9a83e;
  }

  .navigation .notification-icon,
.rbx-highlight
  {
    background: #200e84;
  }

  #chat-header
  {
    background: #f9a83e;
  /*Party header*/;
  }

  .slide-item-container-left,
.profile-avatar-right
  {
    background: #f9a83e;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #145A32 inset;
 /* Yea idk what this is for*/;
  }

  .forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #f9a83e !important;
 /*Forum colors and messaging underline color*/;
  }

  .rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs
  {
    background-color: #5DADE2 !important;
    border-color: #200e84;
   /*Updrade button*/;
  }

  .rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover
  {
    background: #200e84 !important;
    border-color: #200e84;
  }

  #TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input,
#navbar-search-btn
  {
    border: 1px solid #f9a83e;
 /*Search border color*/;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://pbs.twimg.com/media/DSix8FtXkAAfHP2.jpg") !important;
 /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #145A32, #145A32);
  }

  #ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: rgba( 214, 234, 248, 0.8) /*Left side navi*/;
  }

  .rbx-btn-primary-lg
  {
    background-color: rgba(253, 254, 254, 0.5);
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: rgba(253, 254, 254, 0.5);
  } 
}

@-moz-document domain("www.roblox.com"), domain("web.roblox.com"), domain("forum.roblox.com")
{
  #groups-switcher,.btn-control-md,.btn-control-xs,.btn-primary-md,.btn-secondary-md,.btn-secondary-xs,.dialog-container.dialog-visible,.dropdown-menu,.form-control,.form-group,.game-card-container,.game-card-native-ad,.game-card-thumb,.input-dropdown-btn,.item-card-container,.menu-vertical,.nav-menu-title,.popover,.popover-content,.profile-container .asset-item img,.profile-container .asset-item span[title],.scroller,.section-content,.stack-row,.vertical-menu,div.col-sm-6.text-overflow.slide-item-container-right.groups,img.header-thumb
  {
    border-radius: 14px!important;
  }

  .section-content-off
  {
    border-radius: 14px;
  }

  .btn-primary-lg
  {
    border-radius: 30px;
    padding: 12px;
  }

  div.col-sm-6.slide-item-container-left div a img
  {
    border-radius: 30px!important;
  }

  #chat-header,#chat-main,#dialog-container,.chat-windows-header,.dialog-container.group-dialog.ng-scope.dialog-visible
  {
    border-radius: 14px 14px 0 0!important;
  }

  .dialog-message
  {
    border-radius: 10px!important;
  }

  .small.dialog-message.dialog-message-content.dialog-triangle.ng-binding
  {
    box-shadow: none;
  }

  #navbar-search-input
  {
    border: none;
  }

  .nav-menu-title,.nav-menu-title:hover
  {
    background: rgba(0,0,0,0)!important;
  }

  #navbar-search-btn
  {
    visibility: hidden;
  }

  #CreateGroupBtn,.translate
  {
    border: none;
    border-radius: 10px;
    box-shadow: 0 0 4px 0 rgba(0,0,0,.2);
    background: #fcfcfc;
  }

  #CreateGroupBtn,#ctl00_cphRoblox_GroupSearchBar_SearchButton,.btn-control.btn-control-medium,.btn-control.btn-control-medium:hover
  {
    background-image: none;
    background-color: #00A2FF;
    color: #fff;
    font-family: 'Source Sans Pro',Arial,Helvetica,sans-serif;
    font-size: 14px;
    padding-left: 8px;
    padding-right: 8px;
    border: none;
    height: 25px!important;
  }

  #ctl00_cphRoblox_GroupSearchBar_SearchKeyword,#ctl00_cphRoblox_GroupStatusPane_StatusTextBox
  {
    padding: 10px;
    border: none;
  }

  .GroupControlsBox
  {
    border: none;
    background: #fff;
    padding: 8px;
    border-radius: 20px;
    box-shadow: 0 0 4px 0 rgba(0,0,0,.2);
  }

  #CreateGroupBtn
  {
    background-image: none!important;
    font-size: 18px!important;
    height: 35px!important;
    border-radius: 100px;
  }

  #navigation
  {
    transform: translate(0);
    transition: ease-out 70ms;
  }

  #horizontal-tabs,.page-content .rbx-tabs-horizontal .rbx-tab,.rbx-tab-heading
  {
    border-radius: 14px!important;
    box-shadow: none!important;
  }

  #horizontal-tabs
  {
    margin-top: 4px!important;
    margin-bottom: 4px!important;
    background: rgba(0,0,0,0);
  }

  .rbx-tab
  {
    width: 23%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }

  .rbx-tab .rbx-tab-heading:hover
  {
    background-color: #efefef;
    transition: ease-out .1s;
  }

  .rbx-tab .rbx-tab-heading
  {
    background-color: #fff;
    transition: ease-out .1s;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,.2)!important;
  }

  .rbx-tab .rbx-tab-heading:hover
  {
    box-shadow: 0 2px 6px 0 rgba(0,0,0,.25)!important;
  }

  #mCSB_1_container ul li
  {
    padding: 2px;
    transition: ease-out .2s;
  }

  #mCSB_1_container ul li:hover
  {
    transition: ease-out .2s;
    background-color: #efefef;
    border-radius: 8px;
  }

  #mCSB_1_container ul li a span,#mCSB_1_container ul li a span:hover
  {
    color: #000!important;
  }

  .rbx-tab.active .rbx-tab-heading,.rbx-tab.active .rbx-tab-heading:hover
  {
    color: #fff!important;
    background: #00A2FF!important;
    transition: ease-out .1s!important;
  }

  .rbx-tab.active .rbx-tab-heading
  {
    box-shadow: 0 2px 6px 0 rgba(0,0,0,.2)!important;
  }

  .dropdown-menu,.dropdown-menu li:hover,.menu-option.active,.popover-content
  {
    box-shadow: none!important;
  }

  .dropdown-menu li,.dropdown-menu li:hover
  {
    transition: ease-out .15s;
  }

  .enable-three-dee.btn-control.btn-control-small,button.input-addon-btn
  {
    border-radius: 100px!important;
    border: none!important;
  }

  .form-control.input-field.search-input.ng-pristine.ng-valid.ng-empty.ng-valid-maxlength.ng-touched
  {
    margin-right: 10px;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }

  footer.container-footer
  {
    border-radius: 40px 40px 0 0;
    width: 90%;
    margin-left: 5%;
    margin-right: 5%;
  }

  .home-header-content h1 a
  {
    font-weight: 300;
    font-size: 30px;
  }

  button.input-dropdown-btn.category-options.ng-scope
  {
    margin-right: 10px;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }
}

@-moz-document url("https://www.roblox.com/my/groups.aspx"), url-prefix("https://www.roblox.com/My/Groups.aspx?gid=")
{
  #Body,#rbx-body
  {
    background: #efefef;
  }

  #SearchControls
  {
    border: none;
    background: rgba(0,0,0,0);
  }

  .GroupListContainer.StandardBox
  {
    border: none!important;
    background: #fff!important;
    border-radius: 20px;
    padding: 6px;
  }

  #left-column
  {
    width: 140px;
    margin-right: 10px;
  }
}

@-moz-document url("https://www.roblox.com/my/avatar")
{
  .rbx-tab
  {
    width: 18%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }
}

@-moz-document url-prefix("https://www.roblox.com/my/messages/")
{
  ul.nav.nav-tabs
  {
    background: rgba(0,0,0,0)!important;
    box-shadow: none!important;
  }

  li#next.pager-next a,li#previous a,li.first a,li.last a
  {
    border-radius: 100px!important;
    border: none!important;
  }

  .btn-control-sm
  {
    border-radius: 14px!important;
    border: none!important;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
    padding: 7px 12px;
  }

  label#includePreviousMessage::before,label#messageCheckbox::before,label#userCheckbox::before
  {
    border-radius: 100px!important;
  }

  div.message-reply textarea
  {
    border-radius: 12px!important;
  }

  #roblox-reply-btn,div.roblox-sendMessage-action button,div.section-content-off.ng-binding
  {
    border-radius: 14px;
  }

  .messages .messageDivider
  {
    transition: ease-out .2s;
    margin-bottom: 4px;
    border-radius: 14px;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,.3);
  }

  .messages .messageDivider:hover
  {
    transition: ease-out .2s;
    box-shadow: 0 1px 6px 0 rgba(25,25,25,.5);
  }
}

@-moz-document url-prefix("https://www.roblox.com/users/")
{
  .rbx-tab
  {
    width: 48%!important;
    margin-right: 1%;
    margin-left: 1%;
    padding: 10px;
  }
}
