* {
    outline: none;
}

body,
td,
th {
    font-family: 'Roboto', sans-serif;
    font-size: 14px;
}

.th-l-scrollcontainer-td {
    background: #fff;
    min-height: 845px !important;
}
/*loading-start-hello*/

.crmUIHostDialogContainer .crmUIHost-table {
    background: rgba(255, 255, 255, 1);
    background: -moz-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 70%, rgb(221, 221, 221) 100%);
    background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(255, 255, 255, 1)), color-stop(70%, rgba(255, 255, 255, 1)), color-stop(100%, rgba(221, 221, 221, 1)));
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 70%, rgb(221, 221, 221) 100%);
    background: -o-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 70%, rgb(221, 221, 221) 100%);
    background: -ms-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 70%, rgb(221, 221, 221) 100%);
    background: linear-gradient(to bottom, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 1) 70%, rgb(221, 221, 221) 100%);
}
.th-uifbox-i1, 
.th-uifbox-bottom, 
.th-uifbox-top {
    border-left: none !important;
    border-right: none !important;
border-bottom: none !important;
    background-color: unset !important;
}
#crmUIHostDialogText{
    visibility:hidden;
display:inline-block;
}
span#crmUIHostDialogText:after {
visibility: visible;
    display: block;
    content: "Do you like UFO? Let me know via csaba.szep@sap.com !";
    font-size: 1em;
    font-family: 'Roboto', sans-serif;
}

span#crmUIHostDialogText:before {
visibility: visible;
    display: block;
    content: "Hello, I'm BCP UFO!";
    font-size: 3.5em;
    font-family: 'Roboto', sans-serif;
letter-spacing: 5px;
    margin-bottom: 50px;
 overflow: hidden; /* Ensures the content is not revealed until the animation */
  border-right: .10em solid #008fd3; /* The typwriter cursor */
  white-space: nowrap; /* Keeps the content on a single line */
  margin: 2px auto; /* Gives that scrolling effect as the typing happens */
  animation: 
     typing 2s steps(30, end),
     blink-caret .5s step-end infinite;
}

/* The typing effect */
@keyframes typing {
  from { width: 0 }
  to { width: 100% }
}

/* The typewriter cursor effect */
@keyframes blink-caret {
  from, to { border-color: transparent }
  50% { border-color: #008fd3; }
}
/*animation end on hello iam bcp message*/
