.th-lk {
    color: #8A8A8A;
    font-family: 'Roboto', sans-serif;
    font-size: 14px;
    text-decoration: none;
}
.th-l-logofflink,
.th-l-logofflink:visited {
    color: #dfdfdf !important;
    background: rgba(250, 250, 250, 0.16);
    padding: 9px 13px;
    border-radius: 2px;
