css:
@-moz-document domain(www.insomnia.gr) {

body{
background-image: none !important;
background-color: #1a2835 !important;
}

#masthead, .search-form-container {
background-color: #1c2229 !important;
}

.ipsBox {
margin-bottom: 2px !important;
background-color: #ffffff !important;
}

.ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
    border-color: #dadada !important;
}

.ipsLayout_container-block {
background-color: #eeedea !important;
}

h1 {
color: black !important;
}

.breadcrumb > span a {
color: black !important;
}

html[dir="ltr"] .cPost .ipsComment_meta {
padding: 15px 0 10px;
margin-right: 0px !important;
}

.ipsPos_right {
color: #000000 !important;
}

.ipsToolList.ipsToolList_horizontal > li.ipsToolList_primaryAction .ipsButton:not( .ipsButton_link ) {
color: #000000 !important;
}

#ipsLayout_contentWrapper {
width: 99% !important;
}

.cPost.ipsComment .ipsType_richText {
font-size: 14px !important;
line-height: 1.6 !important;
}

.cool-container .cool-news {
background-color: #000000 !important;
}

.all-news > .ipsLayout_container-block {
background-color: #000000 !important;
}

.classifieds__categories ul li a {
background-color: #000000 !important;
}

#elProfileHeader h1 {
color: white !important;
}

}
