css:
@-moz-document domain('tarvalon.net')
{
	
  /* Remove the background image and set a dark background colour */
  html, body
  {
    background-image: none;
    background-color: #414347;
    color: #d4d4d4;
  }

  /* Remove centre-column background */
  #content div.pageContent
  {
    background: none;
    border: none;
    padding: 0;
  }
  /* Adjust various colours */
  .titleBar h1
  {
    color: white;
  }

  .discussionList .sticky, .discussionList .sticky .stats
  {
    background-color: #c2c2c2 !important;
  }

  a.OverlayTrigger
  {
    color: white;
  }

  ul.listInline li.username a:link, ul.listInline li.username a:visited
  {
    color: white;
  }

  .sidebar .section, .sectionMain
  {
    border: 1px solid transparent;
  }

  .forumNodeInfo,
  .categoryForumNodeInfo,
  .secondaryContent
  {
    background-color: #2c2f33 !important;
    border-bottom-color: #414347 !important;
  }

  h3.nodeTitle a
  {
    color: #d4d4d4;
  }

  .nodeStats dd,
  span.lastThreadTitle span,
  ol.subForumList
  {
    color: #717171;
  }

  ol.subForumList h4.nodeTitle a
  {
    color: #d4d4d4;
  }

  fieldset.breadcrumb
  {
    background-color: #676f71;
    border-color: #676f71;
  }

  a.crumb
  {
    border-color: #414347 !important;
    color: #d4d4d4 !important;
    font-weight: bold;
    background: #676f71 !important;
    background: -moz-linear-gradient(left, #676f71 0%, #414347 99%) !important;
    background: -webkit-linear-gradient(left, #676f71 0%,#414347 99%) !important;
    background: linear-gradient(to right, #676f71 0%,#414347 99%) !important;
  }

  span.crust span.arrow
  {
    background-color: #676f71 !important;
    color: #414347 !important;
    border-left-color: #2c2f33 !important;
  }

  span.crust span.arrow span
  {
    border-left-color: #414347 !important;
  }

  .secondaryContent a.avatar img,
  .secondaryContent a.avatar span.img
  {
    padding: 0;
    border: 1px solid #676f71;
    background-position: 0,0;
  }

  .secondaryContent ul li
  {
    display: inline-block;
  }

  li.discussionListItem
  {
    background-color: #2c2f33;
    border-color: #414347;
  }

  li.discussionListItem.sticky
  {
    background-color: #35373a !important;
  }

  .titleText a
  {
    color: #d4d4d4;
  }

  li.discussionListItem.sticky .titleText h3 a
  {
    color: #d4d4d4 !important;
  }

  li.discussionListItem.sticky a.username
  {
    color: #717171 !important;
  }

  li.discussionListItem.sticky a span.DateTime
  {
    color: rgb(155, 155, 155) !important;
  }

  li.discussionListItem.sticky span.sticky
  {
    background-color: transparent !important;
    color: #d4d4d4;
  }

  li.discussionListItem.sticky .listBlock.stats
  {
    background-color: transparent !important;
    color: #d4d4d4;
  }

  li.discussionListItem.sticky .listBlock.stats dt
  {
    color: #717171 !important;
  }

  li.discussionListItem a.avatar img
  {
    background-color: transparent !important;
    border: 1px solid #676f71 !important;
  }

  .discussionList .sticky .posterAvatar
  {
    background-color: transparent !important;
  }

  .discussionList .listBlock
  {
    color: #d4d4d4;
  }

  .discussionList .listBlock i.fa
  {
    color: #d4d4d4;
  }

  .discussionList .listBlock a.username
  {
    color: #676f71;
  }

  ul.tabs
  {
    background-color: #676f71;
    border-color: #676f71;
  }

  .discussionList.section
  {
    border: 0;
  }

  form.InlineModForm
  {
    border: 0;
  }

  #messageList .message
  {
    background-color: #2c2f33;
    border: 0;
    color: #d4d4d4;
  }

  #messageList .message .messageText
  {
    color: #d4d4d4;
  }

  #messageList .message .messageText a,
  #messageList .message .messageText a:link
  {
    color: #776f71;
  }

  #messageList .message .messageText a:hover,
  #messageList .message .messageText a:active
  {
    color: white;
    background-color: #776f71;
    box-shadow: none;
  }

  #messageList .messageUserBlock
  {
    background-color: #414347 !important;
    border: 0;
  }

  #messageList .messageUserBlock a.username
  {
    color: #d4d4d4;
  }

  #messageList .messageUserBlock .userText
  {
    border: 0;
  }

  #messageList .messageUserBlock .avatarHolder
  {
    background-color: #414347;
    border: 0;
  }

  #messageList .messageUserBlock .extraUserInfo
  {
    background-color: #414347;
  }

  #messageList .messageUserBlock span.arrow
  {
    border-color: transparent;
  }

  #messageList .messageUserBlock span.arrow span
  {
    border-left-color: #414347;
  }

  #messageList .messageUserBlock a.avatar img
  {
    border: 0;
    background-color: transparent;
  }

  #messageList .publicControls a
  {
    color: #776f71;
  }

  #messageList .messageContent .bbCodeQuote
  {
    border: 0;
  }

  #messageList .messageContent .bbCodeQuote .attribution.type
  {
    background-color: #5d6467;
    color: #d4d4d4;
    border-color: transparent;
  }

  #messageList .messageContent .bbCodeQuote .quoteContainer
  {
    background-color: #676f71;
  }

  .quoteContainer a
  {
    color: white !important;
  }
  .quoteContainer a:hover
  {
    background-color: #414347 !important;
  }

  #messageList .messageContent .bbCodeQuote .quoteExpand
  {
    background: -moz-linear-gradient(top, rgba(103,111,113,0) 0%, rgba(65,67,71,0.99) 99%, rgba(65,67,71,1) 100%) !important;
    background: -webkit-linear-gradient(top, rgba(103,111,113,0) 0%,rgba(65,67,71,0.99) 99%,rgba(65,67,71,1) 100%) !important;
    background: linear-gradient(to bottom, rgba(103,111,113,0) 0%,rgba(65,67,71,0.99) 99%,rgba(65,67,71,1) 100%) !important;
  }

  .quickReply.message
  {
    background-color: #2c2f33;
    border-color: #414347;
  }

  .quickReply.message .avatarHolder
  {
    background-color: #414347;
  }

  .quickReply.message .avatarHolder a img
  {
    background-color: #414347;
    border: 0;
  }

  .quickReply.message .messageUserBlock
  {
    border-color: #414347;
  }

  .quickReply.message .messageUserBlock span.arrow
  {
    border-color: transparent;
  }

  .quickReply.message .messageUserBlock span.arrow span
  {
    border-left-color: #414347;
  }

  .autoCompleteList
  {
    color: black !important;
  }

  #AccountMenu a
  {
    color: #d4d4d4;
  }

  #AccountMenu .menuHeader,
  #AccountMenu .sectionFooter,
  #ConversationsMenu .menuHeader,
  #ConversationsMenu .sectionFooter,
  #AlertsMenu .menuHeader,
  #AlertsMenu .sectionFooter
    {
      background-color: #414347;
      border: 0;
    }

  #AccountMenu .menuHeader a,
  #AccountMenu .sectionFooter a,
  #ConversationsMenu .menuHeader a,
  #ConversationsMenu .sectionFooter a,
  #AlertsMenu .menuHeader a,
  #AlertsMenu .sectionFooter a
    {
      color: #d4d4d4;
    }

  .navPopup .PopupItemLinkActive
  {
    border: 0;
  }

  ol.alerts.alertsScroller
  {
    color: #5c5b5b;
  }

  ol.alerts.alertsScroller a
  {
    font-weight: bold;
  }

  .navPopup .PopupItemLinkActive:hover
  {
    background-color: #414347;
    color: #d4d4d4;
  }

  .navTabs .navTab.PopupOpen .navLink
  {
    color: #d4d4d4;
  }
  
  .hasJs .bbCodeSpoilerText
  {
    background-color: rgba(0,0,0,0);
    border-color: rgba(0,0,0,0);
  }

  .PreviewContainer .section
  {
    border-color: rgba(0,0,0,0);
  }
  .PreviewContainer .primaryContent
  {
    background-color: #2c2f33;
  }

  .MultiQuoteItem .messageArea
  {
    background-color: #414347;
  }
  .MultiQuoteItem .primaryContent
  {
    background-color: #414347;
  }
  .MultiQuoteItem .messageGradient
  {
    display: none;
  }
  #MultiQuoteForm .sectionFooter
  {
    background-color: #2c2f33;
  }

  .button,
  a.button,
  a.callToAction span
  {
    color: #2c2f33;
  }

  .titleBar h1 em
  {
    color: #c2c2c2;
  }

  .threadListItem .title,
  .threadListItem .additionalRow
  {
    white-space: normal;
  }

  .membersOnline .secondaryContent {
    color: grey;
  }

  .PageNav nav a.PageNavPrev
  {
    color: #d4d4d4;
  }
  .PageNav nav a:hover
  {
    color: #2c2f33;
  }

  .xenPreviewTooltip .section .primaryContent
  {
    background-color: #414347;
  }

  .searchResultsList h3.title a
  {
    color: #2c2f33;
  }
}
