html, body, .index_headers, .cells  {
        background-color: #111 !important;
        color: #ccc !important;
        border-color: #333!important;
    }
.pagenav_pages, .pagenav_current, .pagenav_next, .pagenav_last, .searchbutton, .titles_block, .pagenav_prev, .pagenav_first{
        background-color: #111 !important;
        color: #ccc !important;
        border-color: #333!important;
    }
input, textarea, select, button, .smalltext, td, .odd, .even, blockquote{
 background-color:black !important;   
    color:white !important;
}
