css:
@-moz-document domain("basecamphq.com") {
#launchbar { display: none; }
table.month_row_events tr td .item {
    margin: 0;
    padding: 5px ;
    border-top: 1px solid rgba(0,0,0,.1);
    position: relative;
}

.milestone_status_checkbox {
    display: none;
}

.item:hover .milestone_status_checkbox {
    position: absolute;
    display: block;
    bottom: 0px;
    right: 10px;
    z-index: 99;
}

.entry_meta {
    display: block;
    position: relative;
    overflow: hidden;
    text-indent: -8px;
}

.milestone_label {
    position: relative;
    font-size: 10px;
    z-index: 2;
}

table.month_row_events div.event.milestone {
    padding: 0px;
}

table.month_row_events div.milestone.complete {
    opacity: .3;
}

.commentable {
    position: relative;
}

.comments_target {
    position: absolute;
    top: 0;
    right: 0;
    z-index: 1;
    opacity: .1;
}

.comments_target:hover {
    opacity: 1;
}
}
