css:
@-moz-document domain("dm5.com"), domain("dm5.cn"), domain("manhuaren.com"), domain("xianman123.com")
{

.correlationItem-imgiframe,
footer,
#cp_footer,
#lb-win,
#nAd,
#HMRichBox,
#last-win .view-rec,
#last-win .index-manga-list,
.cat-filter .footer-btn,
.guide,
.ad_360_item,
.today,
.view_user,
.clDiv,
.sitemaji_banner,
.topBar>a:last-child,
.detailForm>div:nth-child(3),
.floatBar_1,
.finalPage .info,
.new-tip,
.bottom_fix_ad,
.phone,
.header-search,
.banner_border_bg,
a[href="#"],
a[href="/vipopen/"],
a[href="javascript:showTip(3)"],
a[href="javascript:showTip(5)"],
a[href*="manhuaren://"],
img[src$="view_code_ad.jpg"],
.view-comment-main .list li.solid:last-child,
body>.view-paging[style="margin-top:5px;"] {
	display: none !important
}

:root {
	--font-size1: calc(10pt + .3vw);
	--font-size2: calc(9pt + .22vw);
	--font-size3: calc(9pt + .15vw);
	--dateProgressBarColor: #ccc;
	--menu-arrow-size: 7pt;
}

body {
	min-width: 900px;
	background: radial-gradient(circle at 50vw 30vh,
			#666 5vw,
			#444 70vw)
}

body.white {
	background: radial-gradient(circle at 50vw 30vh,
			#ddd 5vw,
			#bbb 70vw)
}

img {
	object-fit: contain
}

body,
div,
dl,
dt,
dd,
ul,
ol,
li,
h1,
h2,
h3,
h4,
h5,
h6,
pre,
form,
fieldset,
input,
textarea,
p,
blockquote,
th,
td {
	font-family: sans-serif
}

.header-title li a,
.header-title li i:before,
.view-header-2,
.view-header-2 .back,
.view-header-2 .title,
.view-header-2 .title .active,
.view-header-2 .right-bar .bookshelf,
.view-header-2 .right-bar .collection,
.right-bar .read-style,
.hover .hover-list .block .title,
.view-comment-main .list li .info .content,
body.white .view-comment-main .list li .info .content {
	font-size: var(--font-size1) !important;
}

.right-bar .read-style-con .info,
.hover .hover-user .block .top .info .title,
.hover .hover-list .block .title a,
.hover .hover-user .block .line,
.hover .hover-user .block .bottom a,
.hover .hover-user .btn,
.hover .hover-list .block ul li .info p,
.hover .hover-list .block ul li .info a,
.hover .hover-list .block ul li .info .subtitle,
.hover .hover-list .block ul li .info .tip,
.hover .hover-list .block ul li .info .tip .red,
.hover .hover-list .block .bottom,
.view-comment .view-win-list li a {
	font-size: var(--font-size2) !important;
}

.view-comment .view-win-list li a span,
.view-comment-main .list li .info .bottom {
	font-size: var(--font-size3) !important;
}

body>.full-class,
body>.full-class~div,
body>.full-class~section,
body>.topFIxedMenu~div,
body>.topFIxedMenu~section,
body>.index-title,
body>.index-title~div,
body>.index-title~section {
	width: 1280px;
	max-width: none;
	margin: 0 auto;
	padding: 2mm 20px;
	background: rgba(255, 255, 255, .4);
}

.dateProgressBar .bar-wrap .data-list {
	text-align: center;
}

.dateProgressBar .bar-wrap .data-list li {
	display: inline-block;
	float: none;
}

.topFIxedMenu>div {
	max-width: 100vw;
}

.topFIxedMenu>div .top-menu {
	left: unset;
	right: -12mm;
	z-index: 20;
}

.container,
.banner_detail_form {
	width: auto;
	min-width: 800pt;
	max-width: 1650pt;
	margin: 0 auto;
	overflow: visible;
}

main.container {
	padding: 0 8mm;
}

.box.pb40 {
	padding: 2mm 15mm 5mm
}

.box.pb40.js_top_container {
	width: calc(100% - 300px);
	margin: 0 12.5mm;
	z-index: 10;
}

.mh-itme-top {
	background: none;
	border-color: #aaa;
	padding-left: 1mm;
}

body>.full-class .container,
body>.full-class~div .container,
body>.full-class~section .container {
	margin-left: 5mm;
	margin-right: 5mm;
}

.header-bar li {
	float: none;
	display: inline-block;
	width: auto;
	margin: 0;
	white-space: nowrap;
}

.header-bar {
	position: absolute;
	right: 50px;
	float: none;
	margin: .8em 1em 0;
}

.header-bar a {
	display: inline-block;
	min-width: 3rem;
	padding: 2mm;
}

.header-bar .vip {
	filter: brightness(.7) contrast(2) saturate(3);
}

.index-title {
	height: auto;
}

.index-original-list li {
	width: 33%
}

.index-rank {
	padding: 0 0 0 20px;
}

.index-rank-bar .right-list {
	width: calc((100% - 90px)/5 - 32px);
}

.index-rank-bar .right-list li .sign span.top-1 {
	background: rgba(255, 120, 0, 1);
	text-shadow: 0 0 2px #620;
}

.index-rank-bar .right-list li .sign span.top-2,
.mh-itme-top .num.num-1 {
	background: rgba(255, 120, 0, .7);
	text-shadow: 0 0 2px #620;
}

.index-rank-bar .right-list li .sign span.top-3,
.mh-itme-top .num.num-2 {
	background: rgba(255, 120, 0, .4);
	text-shadow: 0 0 2px #620;
}

.index-booklist-list li {
	width: calc(100%/5 - 4px);
	overflow: hidden;
}

.box-con-8n3 {
	height: 630px
}

.full-class {
	max-width: 1220px;
	margin: 0 auto
}

.index-rank {
	width: 33%
}

.index-cooperation-list {
	display: flex;
	flex-wrap: wrap;
	width: auto;
}

.index-cooperation-list li {
	flex: 1 1 140px;
	margin: 2px
}

.index-cooperation-list li .cover,
.index-cooperation-list li .cover img {
	width: 100% !important;
	height: 100% !important;
	max-width: 200px;
	max-height: 200px;
	object-fit: contain;
}

img[src$="/cross_red.png"] {
	top: -4mm !important;
	right: -4mm !important;
	border-radius: 50%;
	box-shadow: 0 0 0 1mm red, 0 0 0 2mm #fff;
	zoom: .9
}


#showimage,
#barChapter {
	position: relative;
	max-width: 100vw;
	min-height: calc(100vh - 7mm) !important;
	margin: 0 auto;
	padding: 3mm 0;
	text-align: center;
	direction: rtl;
	font-size: 0
}

#barChapter img {
	width: auto !important;
	margin: 0 auto;
}

.coverForm,
.coverForm img {
	max-height: 600px
}

.readPage {
	background: #777;
	padding: 3mm 0 0
}

#cp_image,
#cp_img>img {
	width: auto !important;
	min-width: 60vh;
	max-width: calc(100% - 4mm) !important;
	height: calc(100vh - 12mm) !important;
	margin: 2mm auto !important;
	border-radius: 2mm;
	box-shadow: 0 0 3mm #666
}

.view-main-1 {
	padding: 4mm 0 0
}

.rightToolBar,
.white .rightToolBar {
	bottom: 40px !important;
	height: auto;
	padding: 1mm 2mm;
	border-radius: 8mm;
	opacity: .3;
	transition: .15s
}

.rightToolBar:hover {
	opacity: 1
}

.rightToolBar a,
.white .rightToolBar a {
	margin: 1mm
}

.rightToolBar a.logo_3 {
	display: none !important
}

.readPage .readForm {
	direction: rtl;
	text-align: center
}

.readPage .readForm img {
	display: inline-block;
	min-width: 80%;
	width: auto !important;
	max-width: calc(100vw - 2mm) !important;
	max-height: calc(100vh - 9mm) !important;
	margin: 2.22mm 1mm;
	border-radius: 2mm;
	box-shadow: 0 1mm 3mm black
}

.readPage .readForm img:first-child {
	display: block;
	margin: 2.22mm auto !important
}

.header {
	left: 0 !important;
	background: rgba(255, 255, 255, .5);
	padding: 0 16mm;
	box-shadow: 0 0 1mm rgba(0, 0, 0, .4);
	z-index: 10;
	backdrop-filter: blur(6mm);
	-webkit-backdrop-filter: blur(6mm);
	transition: .2s;
}

.header:hover {
	background: rgba(255, 255, 255, .9);
	box-shadow: 0 0 2mm rgba(0, 0, 0, .4);
}

.view-header-2 {
	position: fixed !important;
	border-bottom: 1px rgba(128, 128, 128, .5) solid;
	top: -57px !important;
	text-shadow: 0 0 3mm #000;
	box-shadow: 0 0 2mm rgba(0, 0, 0, .5);
	background: rgba(200, 200, 200, .4);
	backdrop-filter: blur(1mm);
	-webkit-backdrop-filter: blur(1mm);
	transition: .8s .5s, width 0s;
}

.view-header-2:hover {
	background: rgba(0, 0, 0, .6);
	backdrop-filter: blur(5mm);
	-webkit-backdrop-filter: blur(5mm);
	transition: .2s 0s, width 0s;
}

body.white .view-header-2 {
	text-shadow: 0 0 3mm #fff;
	background: rgba(0, 0, 0, .3);
	backdrop-filter: blur(1mm);
	-webkit-backdrop-filter: blur(1mm)
}

body.white .view-header-2:hover {
	background: rgba(240, 240, 240, .85);
	backdrop-filter: blur(5mm);
	-webkit-backdrop-filter: blur(5mm);
}

.header-title li,
.header-title li a {
	border-radius: 4mm;
	transition: .2s;
}

.header-title li a:hover {
	background: rgba(240, 180, 50, .2);
}

.header-title li a,
.header-title li i,
.header-title li i:before {
	display: inline-block;
	width: 100%;
	line-height: 2.4em;
	vertical-align: middle !important;
	margin: 0;
}

.view-header-2 .right-bar {
	right: 16mm
}

.view-header-2 a,
.view-header-2 .right-bar .bookshelf,
.view-header-2 .right-bar .collection,
.view-header-2 .right-bar .read-style {
	color: #bbb
}

body.white .view-header-2 a,
body.white .view-header-2 .right-bar .bookshelf,
body.white .view-header-2 .right-bar .collection,
body.white .view-header-2 .right-bar .read-style {
	color: #444
}

.view-header-2>.title {
	text-align: left;
	margin-left: 9em;
	margin-right: 24em;
	text-overflow: ellipsis;
	overflow: hidden;
	white-space: nowrap;
	display: -webkit-box;
	-webkit-line-clamp: 1;
	-webkit-box-orient: vertical;
}

.view-header-2 .title .right-arrow {
	background-position: 5px 50%
}

.view-fix-top-bar {
	position: fixed;
	top: -2em !important;
	height: 3em;
	line-height: 3em;
	opacity: .3;
	box-shadow: 0 0 0 1px #888, 0 0 5px #444;
	transition: all .8s !important
}

.view-fix-top-bar-title {
	height: 3em;
	line-height: 3em;
	padding-left: 3em
}

.view-fix-top-bar-back,
.view-fix-top-bar-right-logo {
	height: 1.4em;
	top: 0;
	padding: .8em 2mm;
	margin: 0
}

.vPage .topToolBar .center,
.vPage .topToolBar .center a {
	color: #ccc;
	display: inline-flex;
	text-overflow: ellipsis;
	overflow: hidden;
	white-space: nowrap
}

.readPage .titleBar {
	top: -30px !important;
	opacity: .3 !important;
	transition: all .8s !important
}

body>.view-header-2:hover,
.readPage .titleBar:hover,
.view-fix-top-bar:hover {
	top: 0 !important;
	opacity: 1;
	transition: .2s 0s, width 0s;
}

.readPage .toolBar {
	width: 100vw;
	position: fixed !important;
	bottom: -37px !important;
	opacity: .2 !important;
	transition: .8s .5s, width 0s;
}

.view-fix-bottom-bar {
	bottom: -47px !important;
	box-shadow: 0 0 0 1px #888, 0 0 5px #444;
	opacity: .3 !important;
	transition: .8s .5s, width 0s;
}

.readPage .toolBar:hover,
.view-fix-bottom-bar:hover {
	bottom: 0 !important;
	opacity: .95 !important;
	transition: .2s, width 0s;
}

.readPage .readTipForm {
	background: navy;
	padding-bottom: 8mm
}

.am-scrollable-horizontal {
	width: 50vw;
	margin: 0 auto
}

.am-avg-sm-4>li {
	width: auto;
	min-width: 6em
}

.am-avg-sm-4>li:nth-of-type(4n+1) {
	clear: none
}

.am-avg-sm-4>li>a {
	transition: all .2s
}

.am-avg-sm-4>li>a:hover {
	background: #DDF
}

.am-avg-sm-4>li>a:active {
	box-shadow: inset 0 0 2mm gray
}

div[style=" margin-top:0px; height:35px;border-bottom:none;margin-bottom:-6px;"] {
	background: #888;
	width: 100vw !important;
	padding: 5px;
	position: fixed;
	bottom: -30px;
	opacity: .2;
	transition: all .8s
}

div[style=" margin-top:0px; height:35px;border-bottom:none;margin-bottom:-6px;"]:hover {
	bottom: 0;
	opacity: .9;
	transition: all .2s
}

.v_yan1,
.v_yan2,
.search_fy a {
	transition: all .2s
}

.v_yan1,
.v_yan2 {
	width: 6em !important;
	margin: 0 1em
}

.search_fy {
	padding: 0 2em
}

.vPage.white .commentList .list li .info p.c {
	font-size: 11pt
}

#goBak {
	cursor: pointer;
	padding: .6em 1em;
	margin-top: -11px;
	border-radius: 1.5mm
}

.topBar img.backlogo {
	width: 48px;
	height: 36px;
	padding: 8px;
	margin-top: -13px;
	border-radius: 1.5mm
}

.readPage .toolBar li {
	color: #aaa
}

.readPage .toolBar li img {
	cursor: pointer;
	padding: .2em 45% 1.8em 45%;
	margin: .2em 0 -1.7em 0;
	border-radius: 1.5mm;
	background: transparent;
	transition: background-color .8s
}

.readPage .selectBar .chapterList {
	height: auto
}

.readPage .selectBar .chapterList li {
	width: 4em;
	margin: .5em
}

#goBak:hover,
.topBar img.backlogo:hover,
.readPage .toolBar li img:hover,
.readPage .selectBar .chapterList li a:hover {
	display: inline-block;
	background: rgba(255, 255, 255, 0.3);
	transition: background-color .2s
}

#goBak:active,
.topBar img.backlogo:active,
.readPage .toolBar li img:active,
.readPage .selectBar .chapterList li a:active {
	background: #5a3;
	box-shadow: inset 0 0 1mm black
}

body>.view-paging {
	width: 100vw;
	height: 30px;
	margin: 0 !important;
	padding: 8px 0 !important;
	background-color: rgba(220, 220, 220, .2);
	position: fixed;
	bottom: -32px;
	left: 0;
	z-index: 3;
	box-shadow: 0 0 0 1px #888;
	backdrop-filter: blur(1mm);
	-webkit-backdrop-filter: blur(1mm);
	transition: .8s .5s, width 0s;
}

body.white>.view-paging {
	background-color: rgba(30, 30, 30, .3);
}

body>.view-paging:hover {
	bottom: 0;
	background-color: rgba(0, 0, 0, .6);
	backdrop-filter: blur(5mm);
	-webkit-backdrop-filter: blur(5mm);
	transition: .2s, width 0s;
}

body.white>.view-paging:hover {
	background-color: rgba(30, 30, 30, .5)
}

.view-bottom-bar {
	padding: 0 0 4mm
}

.view-bottom-bar-item {
	margin: 0 5px 0 0;
	height: 30px;
	line-height: 30px
}

.warning-bar p {
	color: #888;
}

.left-bar,
.view-comment-main {
	margin: 3mm
}

.view-comment-main {
	width: calc(100% - 280px) !important;
	border-radius: 2mm;
	overflow: hidden;
	background: rgba(128, 128, 128, .2);
	border: none;
	box-shadow: 0 0 0 1px rgba(255, 255, 255, .3), 0 0 3mm rgba(0, 0, 0, .4)
}

#barChapter~.view-comment .view-comment-main,
#showimage~.view-comment .view-comment-main {
	width: 100% !important;
}

body.white .view-comment-main {
	background: rgba(255, 255, 255, .8);
	border: none
}

.view-comment-main .top {
	line-height: 3em;
	height: 3em;
	margin-bottom: 1.5mm
}

.view-comment-main .top .page {
	margin-top: 2mm;
	line-height: 2em
}

.view-comment-main .top .page a,
.view-comment-main .top .page span {
	margin: 0 0 0 1.5mm
}

.view-comment-main .list {
	width: 100%
}

.view-comment-main .list li .info .comment-item {
	padding-top: 1em
}

.view-comment-main .list li .info .content {
	color: #bbb;
	line-height: 1.2;
	margin: 0 !important
}

.view-comment-main .list li .info {
	width: calc(100% - 100px)
}

.view-comment-main .list li .info .bottom {
	text-align: right;
	font-size: 12pt;
	line-height: 1.2;
	color: #aaa;
	margin: 1mm 0 0 !important;
	filter: brightness(.75);
}

.view-comment-main .list li .info .bottom .right .zan,
.view-comment-main .list li .info .bottom .right .comment {
	margin-left: 4mm;
	color: #aaa
}

body.white .view-comment-main .list li .info .content {
	color: #666
}

body.white .view-comment-main .list li .info .bottom {
	color: #ccc;
}

body.white .view-comment-main .list li .info .bottom .right .zan,
body.white .view-comment-main .list li .info .bottom .right .comment {
	margin-left: 4mm;
	color: #aaa
}

.view-comment-main .list li .info .bottom .right {
	margin-left: 2mm
}

.view-comment-main .list li .info .bottom .right a {
	padding: 1px 0
}

.view-comment-main .list li {
	width: 100%;
	border: none;
	padding: 5px 0 2px !important
}

.view-comment-main .list li.dashed {
	width: 100%;
	background: linear-gradient(180deg,
			rgba(200, 200, 200, .8) 0,
			transparent 1pt)
}

.view-comment-main .list li.solid,
.view-comment-main .list li.dashed .info {
	padding: 1px 0 0 !important;
	border: none !important
}

.view-comment-main .list li .info .title {
	margin-top: 0
}

.view-comment-main .list li .cover img {
	background-color: #eee;
	border-radius: 2mm;
	box-shadow: 0 0 0 1px #666
}

body.white .view-comment-main .list li .cover img {
	box-shadow: 0 0 0 1px #ccc
}

.view-comment-main .bottom-bar {
	padding: 3mm 0 !important;
	background-color: rgba(33, 33, 33, .6)
}

body.white .view-comment-main .bottom-bar {
	background-color: rgba(220, 220, 220, .6)
}

#last-win {
	border-radius: 3mm
}

.view-comment {
	padding: 0 0 5mm;
	margin: 0;
	background: linear-gradient(0deg,
			rgba(0, 0, 0, .3) 10vh,
			rgba(0, 0, 0, .5) 90vh)
}

.banner_detail {
	padding: 50px 0 0;
	min-height: 0;
	z-index: 0;
}

.banner_detail:before {
	background: linear-gradient(0deg,
			rgba(0, 0, 0, .5) 20%,
			rgba(0, 0, 0, .4) 80%);
}

.banner_detail_form,
.view-comment>.container:not([style]) {
	width: calc(100% - 250px);
	background: linear-gradient(90deg,
			#bbb 0,
			#ddd 30vw,
			#eee 60vw,
			#ddd 100vw);
}

.banner_detail_form {
	padding: 2mm
}

.banner_detail_form .cover img {
	object-fit: cover
}

.banner_detail_form .info,
.banner_detail_form .info .bottom {
	width: calc(100% - 350px) !important;
}

.banner_detail_form .info .content {
	width: 100%;
	font-size: 11pt;
}

.index-title h2,
.view-comment .index-manga .col7 {
	float: none;
}

.view-comment>.container:not([style]) {
	padding: 0 4mm 4mm;
	border-radius: 0 0 2mm 2mm
}

.view-comment .left-bar {
	width: calc(100% - 270px)
}

.view-comment .view-win-list {
	width: auto;
	overflow: visible
}

.view-comment .view-win-list li {
	float: none;
	display: inline-block;
	min-width: 8em;
	line-height: 1;
	height: auto;
	text-align: center;
	border-radius: 5mm;
	margin: 3px;
	padding: 0;
	background: rgba(240, 245, 255, .7);
	box-shadow: 0 0 0 1px rgba(0, 0, 0, .2);
	transition: .3s
}

.view-comment .view-win-list li:hover {
	background: rgba(255, 255, 240, 1);
	box-shadow: 0 1px 1mm 1px rgba(80, 50, 20, .4);
	transition: .1s
}

.chapteritem {
	display: inline
}

.view-comment .view-win-list li a {
	padding: .4em 1em;
}

.view-comment .view-win-list li a:hover {
	color: #229;
}

#barChapter img {
	display: inline-block;
	width: auto !important;
	height: auto !important;
	max-width: calc(100% - 4mm) !important;
	margin: 3mm auto !important;
}

.view-win .view-win-list {
	display: flex;
	flex-wrap: wrap;
}

.view-win .view-win-list li {
	text-align: center;
	width: auto;
	flex: 1 1 30%;
	background: transparent;
	padding: 0;
	margin: 2mm;
	height: auto
}

.view-win .view-win-list li a {
	white-space: nowrap;
	overflow: hidden;
	background: #f3f3f3;
	text-overflow: ellipsis;
	padding: 0 .6em;
	line-height: 2.2;
	border-radius: 2em;
	box-shadow: 0 0 0 1px #aaa
}

.header>.container {
	position: relative
}

.cat-filter {
	max-height: 85px;
	background: #dcdee4;
