css:
@-moz-document domain("43rumors.com"),
domain("sonyalpharumors.com"),
domain("fujirumors.com"),
domain("nikoneye.com"),
domain("mirrorlessrumors.com"),
domain("canonwatch.com"),
domain("l-rumors.com") {

.full-width-main-layout .container,
.full-width-main-layout .vc_row-fluid.container,
.boxed-main-layout #wrap,
body.has-sidebar .content-area,
body.owt #globalcontainer {
	width: 95% !important;
	max-width: 1600px !important;
}
body.owt #container #content {
	width: calc(100% - 420px) !important;
	margin-right: 10px;
}
#primary {
	width: calc(100% - 350px) !important;
}
#content-wrap {
	background: transparent;
}
body.owt .clearfix {
	overflow: visible;
}
.meta, .entry .meta {
	padding: .4em 1em .15em;
	border-radius: 3mm 0 3mm 0 !important
}
.blog-entry.large-image-entry-style {
	padding-left: 1em;
}
.single-post .meta.meta-with-title {
	left: 3mm;
}
article.blog-entry,
article.single-blog-article {
	width: 100% !important;
	padding: 3em 1em 1em;
	margin: 4mm 4mm 0;
	border-radius: 3mm;
	box-shadow: 0 2mm 5mm gray;
}
div.postmain {
	box-shadow: 0 2mm 5mm gray;
}
article.single-blog-article {
	margin: 0 3mm;
}
article img {
	width: auto !important;
	max-width: 80% !important;
	margin: 0 auto;
}
#sidebar {
	width: auto;
	max-width: none;
	overflow: hidden;
	padding: 1em;
	border-radius: 3mm;
	background-color: white !important;
	box-shadow: 0 1mm 5mm gray;
}
#sidebar-inner {
	max-width: 300px;
}
article iframe {
	max-width: 900px !important;
	margin: 0 auto 1em !important;
}
.post-grid {
	width: auto !important;
}
#blog-entries>:not(article),
#topgoo,
#footer {
	display: none !important
}

}
