css:
@-moz-document domain("ycombinator.com") {

body {
    background-color: #ededed;
    padding: 5px;
    padding-bottom: 30px;
}

#hnmain {
    width: calc(85% + 20px);
    padding: 10px;
    box-shadow: 4px 4px 23px 2px rgba(0,0,0,0.10);
}

td, table {
    border-color: #fffffc;
    background-color: #fffffc !important;
}

.topsel a {
    color: #ccc !important;
}

.subtext {
    opacity: 0.7;
}

.sitebit {
    opacity: 0.3;
}

.sitebit:hover {
    opacity: 0.7;
}

.sitestr {
    color: #fc6621;
}

.sitestr:hover, .sitebit a:hover {
    text-decoration: none;
}

table center input[type="text"] {
    width: 30%;
}
}
