css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.wizards.com/dndinsider/compendium/database.aspx") {

body
{
  min-width: 910px !important;
  padding-left: 15px !important;
  padding-right: 0px !important;
}

#search, #background, #filters, #resultstop, #resultsbody
{
  background: none !important;
}

#background
{
    height: 1px !important;
}

#search 
{
  top:    4px   !important;
  left:   1px   !important;
  right:  1px   !important;
  width:  900px !important;
  height: 55px  !important;
}

#searchterm
{
  top:   0px !important;
  left:  0px !important;
  width: 760px !important;
  height: 2em !important;
}

#nameonly
{
  top:   14px !important;
  left:  764px !important;
}

#nameonlytext
{
  top:   14px !important;
  left:  785px !important;
}

#search_box_border
{
  display: none !important;
}

#searchbutton_text
{
  top:   0px !important;
  left:  770px !important;
}

#searchbutton, #reset_search_ball
{
  display: none !important;
}

#reset_search_text
{
  top:    0px !important;
  left:   850px !important;
  height: 14px !important;
}

#gem_race, #gem_class, #gem_item, #gem_creature, #gem_epic, #gem_paragon, #gem_ritual, #gem_feat, #gem_power, #gem_skill, #gem_deity, #gem_glossary, #gem_trap
{
  display: none !important;
}

#races_link, #items_link, #creatures_link, #paragon_link, #feats_link, #skills_link, #glossary_link
{
  font-size: x-small !important;
  top:  30px !important;
}

#classes_link, #epic_link, #rituals_link, #powers_link, #deity_link, #traps_link
{
  font-size: x-small !important;
  top:  46px !important;
}

#races_link, #classes_link
{
  left: 0px !important;
}

#paragon_link, #epic_link
{
  left: 100px  !important;
}

#feats_link, #powers_link
{
  left: 265px  !important;
}

#items_link, #rituals_link
{
  left: 410px  !important;
}

#creatures_link, #traps_link
{
  left: 560px  !important;
}

#skills_link, #deity_link
{
  left: 700px  !important;
}

#glossary_link
{
  left: 800px !important;
}

#filters
{
  top:    70px  !important;
  left:   4px   !important;
  right:  4px   !important;
  width:  900px !important;
  height: 100px !important;
}

#filter_title
{
  top:  4px !important;
  left: 0px !important;
}

#total_results
{
  top:   4px !important;
  right: 4px !important;
}

#filterRace, #filterClass, #filterEpicDestiny, #filterParagonPath, #filterRitual, #filterFeat, #filterSkill, #filterItem, #filterMonster, #filterPower
{
  left: 45px !important;
  top:  89px !important;  
}

#filterDeity, #filterGlossary, #filterTrap
{
  left: 0px !important;
  top:  0px !important;  
}

#view_all_text
{
  top: 40px !important;
  left: 393px !important;
}

#apply_filter_text
{
  top: 40px !important;
  left: 130px !important;  
}

#reset_filter_text
{
  top: 40px !important;
  left: 600px !important;  
}

#apply_filter_ball, #view_all_ball, #reset_filter_ball
{
  display: none;
}

#resultstop
{
  top:   100px !important;
  width: 910px !important;
}

#results_per_page
{
  left: 6px !important;
}

#search_results_text
{
  top: 0px !important;
}

#resultsbody
{
  top:  145px !important;
  left:   0px !important;
}


#footer, #resultsbottom
{
  display: none;
}

}
