css:
@-moz-document domain("whattomine.com") {
/*
* Name:			Dark WhatToMine
* Homepage:		https://userstyles.org/styles/153712/dark-whattomine
* Author:		Atx
* Version:		v1.6
* Updated: 		20.01.2021
*/

body, .input-group > span.input-group-btn > input.btn-default:hover, .lower > .popover.fade.bottom.in .popover-content, .chosen-container-multi .chosen-choices li.search-choice {
    background-color: #1C1C1C !important;
}
.input-group > span.input-group-btn > input.btn.btn-default, .chosen-container .chosen-results li.no-results, .navbar-default, .nav-tabs > li > a, .chosen-container-multi .chosen-choices, .chosen-container-single .chosen-single, .chosen-container .chosen-drop, .table-hover > tbody > tr:hover, .dropdown-menu, .input-group-addon, .nav-tabs > li > a:hover, .ck-button input + span, .calculator, .bg-light, .lower .popover, .input-group-text, .card, .popover {
    background-color: #2C2C2C !important;
}
.table > tbody > tr.table-active > td, .table-hover > tbody > tr:hover, .table.table-hover tbody > tr.active, .dropdown-menu > .dropdown-item:hover, .nav-tabs > li.active > a, .nav-tabs.secondary > .nav-item > a.nav-link.active, .nav-tabs.secondary > .nav-item > a.nav-link:hover, .nav-tabs.secondary > .nav-item > a.nav-link:focus, .form-control, .list-group > .list-group-item, .btn-outline-secondary:hover {
    background-color: #3C3C3C !important;
}
.nav-tabs.secondary > .nav-item > a.nav-link:hover {
    background-color: #4C4C4C !important;
}
body, .chosen-choices > li.search-field input[type="text"], .chosen-container-multi .chosen-choices li.search-choice, .chosen-container-multi .chosen-choices, .chosen-container-single .chosen-single, ul.chosen-choices li.search-choice span, .ck-button-nv input + span, .ck-button-amd input + span, .btn.btn-default, .chosen-container .chosen-results li.no-results, .navbar-default, .nav-tabs > li > a, .nav-tabs > li.active > a, .input-group-addon, .nav-tabs > li > a:hover, .form-control, caption, .calculator > .link-index.name_tag_algo > h3 > a, .popover-body, .input-group-text, footer a, .dropdown-menu > .dropdown-item, .table > thead > tr > th, .table > tbody > tr > td, .btn-outline-secondary, td.text-center > a.btn {
    color: #CCC !important;
}
.chosen-container .chosen-results, .dropdown-menu > li > a {
    color: #777 !important;
}
.row > .col-xs-3 > p > a {
    color: #609DD2 !important;
}
a:hover, a:focus {
    color: #4285F4 !important;
}
.table-success > td {
    background-color: #29491D !important;
    color: #BCDCBD !important;
}
.table-warning > td {
    background-color: #664D00 !important;
    color: #BCDCBD !important;
}
.input-group-addon, .nav-tabs > li > a:hover, .nav-tabs > li.active > a, .form-control, .btn-default, .chosen-container .chosen-drop, .chosen-container-multi .chosen-choices, .nav-tabs .nav-link.active, .input-group-text {
    border: 1px solid #4C4C4C !important;
}
.nav-tabs.secondary > .nav-item > a.nav-link:hover {
    border: 1px solid #5A5A5A !important;
}
.bs-callout {
    border: 1px solid #5A5A5A !important;
    border-left-width: 5px !important;
    border-radius: 3px !important;
}
.bs-callout-primary {
    border-left-color: #428BCA !important;
}
.ck-button-inputs input + span {
    background-color: #2D6A9F !important;
}
.btn-info {
    background-color: #1E7994 !important;
}
.btn-info:focus, .btn-info:hover {
    background-color: #1A677F !important;
    border-color: #238aa9;
}
.list-group-item svg.octicon > path, .clipboard > svg.octicon, .text-center > span > svg.octicon > path {
    filter: invert(75%) !important;
}
.btn-primary, .ck-button-inputs input + span, .ck-button input:checked + span, .btn-info {
    color: #DDD !important;
}
.ck-button input:checked + span, .btn-primary.btn-block {
    background-color: #2D6A9F !important;
}
.btn-primary {
    border: 1px solid #4C91CD !important;
}
.btn-primary:hover {
    background-color: #286090 !important;
}
.table-hover.table-vcenter > tbody > tr.success > td {
    background-color: #1F3715 !important;
}
.table-hover.table-vcenter > tbody > tr.warning > td {
    background-color: #453B07 !important;
}
.ck-button-inputs label span.badge {
    color: #1A1A1A !important;
}
.table > tbody > tr > th, .table > tbody > tr > td, footer, .table > thead > tr > th {
    border-top: 1px solid #4C4C4C !important;
}
html body.lower div.container table.table.table-sm.table-hover.table-vcenter thead tr th {
    border-top: 2px solid #4C4C4C !important;
}
.nav-tabs, .page-header {
    border-bottom: 1px solid #4C4C4C !important;
}
.table > thead > tr > th {
    border-bottom: 2px solid #4C4C4C !important;
}
.ck-button-amd input + span {
    background-color: #660000 !important;
    border: 1px solid #4C4C4C !important;
}
.ck-button-nv input + span {
    background-color: #006600 !important;
    border: 1px solid #4C4C4C !important;
}
.ck-button-amd input:checked + span, .ck-button-nv input:checked + span {
    background-color: #000 !important;
}
.chosen-container-multi .chosen-choices, .chosen-container-single .chosen-single, .chosen-container-multi .chosen-choices li.search-choice {
    background-image: none !important;
}
.chosen-container-single .chosen-single, .chosen-container-multi .chosen-choices li.search-choice {
    box-shadow: none !important;
    border: 1px solid #5A5A5A !important;
}
.row.margin-bot-10 {
    display: none !important;
}
.lower .popover.fade.bottom.in {
    background-color: #5A5A5A !important;
}
.nav-tabs > li > a:hover, .nav-tabs .nav-item.show .nav-link {
    border-color: #4C4C4C !important;
}
.calculator {
    border: 5px solid #3C3C3C !important;
}
}
