css:
@-moz-document domain("google.co.jp"), domain("google.com") {
.irc_land.irc_bg,
.irc_land.irc_bg .irc_c,
.irc_land.irc_bg .irc_c .irc_hic {
    min-width: calc(100vw - 17px);
    min-height: 100vh;
    margin: 0!important;
}
img.irc_mi {
    width: auto;
    max-width: 100vw;
    height: auto;
    max-height: 100vh;
    margin: 0!important;
}
.irc_land .irc_b {
    background: rgba(0, 0, 0, 0.8);
    position: absolute;
    height: 100%;
    opacity: 0!important;
}
.irc_ft {
    opacity: 0.5!important;
}
.irc_land .irc_b:hover,
.irc_ft:hover {
    opacity: 1!important;
}
.irc_land .irc_sep{
	display:none!important;
}
}
