css:
@-moz-document domain("thepiratebay.org") {
/*********** TPB Site Background ***********/

body {
    background-color: #151515 !important;
    color: #edf9e8 !important;
}
a {
    color: #ff3333 !important;
    border-bottom: none !important;
    transition: 500ms;
}
a:hover {
    color: #efffff !important;
    border-color: #81fa07 !important;
    transition: 500ms;
    text-decoration: underline;
}
input[type="search"] {
    background-color: #202020 !important;
    border: 1px solid #666666 !important;
    color: #ff0000 !important;
}
input[type="submit"] {
    background-color: #202020 !important;
    border: 2px solid #666464;
    color: #ccc7c7 !important;
    border-radius: 4px;
    transition: 500ms;
    border-style: outset;
}
input[type="submit"]:hover {
    background-color: #444444 !important;
    border-color: #444444 !important;
    color: #ffffff !important;
    cursor: pointer !important;
    transition: 500ms;
}
h2 {
    background-color: #505050 !important;
    border-color: #404040 !important;
}
select,
select[size="0"],
select[size="1"] {
    border-color: #303030 !important;
}
select {
    background-color: #202020 !important;
    border-radius: 4px;
    color: #d3b5b5 !important;
}
/*********** Browse ***********/

.detDesc {
    color: #a9a5a5 !important;
}
table#searchResult td.vertTh {
    color: #d3b5b5 !important;
}
thead#tableHead th {
    background-color: #303030 !important;
}
table#searchResult th,
table#searchResult td {
    border-color: #303030 !important;
}
table#searchResult tr:nth-child(odd) {
    background-color: #202020 !important;
}
table#searchResult tr:nth-child(even) {
    background-color: #252525 !important;
}
table#searchResult tr:hover {
    background-color: #303030 !important;
}
/*********** Torrent Details ***********/

#detailsframe,
#detailsframe #title {
    background-color: #505050 !important;
    border-color: #303030 !important;
}
#details {
    background-color: #101010 !important;
}
#details dd {
    border-color: #303030 !important;
}
#details dt {
    border-color: #303030 !important;
    color: #a9a5a5 !important;
}
.nfo,
#filelistContainer {
    background-color: #303030 !important;
    border-color: #404040 !important;
    font-family: Consolas, Monaco, "Ubuntu Mono", monospace !important;
    line-height: 1.15 !important;
    padding-bottom: 5px !important;
}
#details h4,
#comments p.byline {
    color: #a9a5a5 !important;
}
#comments .comment {
    background-color: #202020 !important;
}
/*********** Blog ***********/

#blog #comments dt {
    background-color: #404040 !important;
    color: #ffff66 !important;
}
/*********** RSS Subscriptions ***********/

#subscribe li a:hover {
    background-color: #202020 !important;
}
}
