css:
@-moz-document domain("wink.messengergeek.com") {
.d-header {
    background-image: url(https://i.imgur.com/F8Z6I2f.png);
    background-color: #004e82;
    background-position: bottom;
    background-position: right;
    background-repeat: no-repeat;
}
.extra-info-wrapper .topic-link {
    color: #FFFFFF;
}
.d-header #site-logo {
    /* these numbers match the new image's dimensions */
    padding-left: 0px !important;
    padding-top: 0px !important;
    background: url(https://i.imgur.com/NkwoEZw.png) no-repeat !important;
    z-index: -1!important;
    background-size: contain;

	   width:0px; height: 0px;

	   padding: 40px 239px 0 0 !important;
}
.d-header .icons .icon {
    color: #FFFFFF;
	cursor: click;
    transition: none;
    width:50px; height: 60px;
    font-size: 10px;
}
html {
    font-family: tahoma,sans-serif;
    font-size: 12px;
}
img.avatar {
    border-radius: 0%
}
.fa {
    font: tahoma,sans-serif;
    font-size: 12px;
}
.user-main i.fa-heart {
    background: url(https://i.imgur.com/VQ0XP6G.png) no-repeat !important;
    color: rgba(0, 0, 0, 0) !important;
    
}
.user-menu i.fa-heart {
    background: url(https://i.imgur.com/VQ0XP6G.png) no-repeat !important;
    width:16px; height: 15px;
    color: rgba(0, 0, 0, 0) !important;
	
}
.user-stream .notification .user-main .user-stream .notification .icon {
    background: url(https://i.imgur.com/71jmubv.png) no-repeat !important;
    color: rgba(0, 0, 0, 0) !important;
}
div.menu-links-header .fa-user {
    background: url(https://i.imgur.com/6UweiNI.png) no-repeat !important;
    width:16px; height: 15px;
    color: rgba(0, 0, 0, 0) !important;
}
.fa-mail-reply, .fa-reply {
    background: url(https://i.imgur.com/uJIRgku.png) no-repeat !important;
    width:19px; height: 15px !important;
    color: rgba(0, 0, 0, 0) !important;
}
.fa-certificate {
    background: url(https://i.imgur.com/71jmubv.png) no-repeat !important;
    width:18px; height: 17px !important;
    color: rgba(0, 0, 0, 0) !important;
}
.extra-info-wrapper .badge-wrapper.bullet span.badge-category {
color: #ffffff !important;
}
}
