css:
@-moz-document regexp("chrome-extension://.*"),
regexp("moz-extension://.*") {
  /*! Stylus-Dark v1.3 (2018-09-14)
      Repository: https://github.com/overdodactyl/Stylus-Dark
      License:    https://creativecommons.org/licenses/by-sa/4.0/

      Code Mirror based on Firefox
*/
  #notes,
  body {
    background: #38383d!important;
    color: #b1b1b3!important
  }
  #header {
    color: silver!important;
    background: #2a2a2e!important;
    border-style: none!important
  }
  #stylus-popup {
    background: #4a4a4f!important;
    color: silver!important
  }
  #hotkey-info[data-active] {
    border-left: 2px solid #2a2a2e!important
  }
  #hotkey-info div {
    border-top: 1px solid #2a2a2e!important;
    background-color: #2a2a2e!important
  }
  #help-popup,
  #sections,
  .newUI .target {
    color: #b1b1b3!important
  }
  #help-popup,
  #message-box > div,
  #message-box-buttons,
  #message-box-title,
  body {
    background: #2a2a2e!important
  }
  .search-result {
    background: #4a4a4f!important;
    border-color: #737373!important
  }
  .search-result:hover {
    border-color: #b1b1b3!important
  }
  .search-result-meta {
    background-color: #4a4a4f!important;
    color: #b1b1b3!important
  }
  button,
  input:not([type]),
  select {
    font-family: arial,sans-serif!important
  }
  input[type=checkbox]:not(.slider) {
    background: #38383d!important
  }
  .applies-to li,
  .firefox select,
  input {
    background-color: #38383d!important;
    color: silver!important;
    border: 1px solid #4a4a4f!important
  }
  .filter-selection select {
    background-color: transparent!important;
    border: none!important
  }
  #filters .filter-selection:hover {
    background-color: transparent!important
  }
  button {
    background: #2a2a2e!important;
    border: 1px solid #484848!important;
    color: silver!important;
    border-radius: 3px!important
  }
  .svg-icon.checked {
    fill: #45a1ff!important
  }
  button:hover {
    color: #45a1ff!important
  }
  a,
  a:visited {
    color: #e1e1e1!important
  }
  #save-button:after {
    content: 'd'!important
  }
  body.dirty #save-button:after {
    content: '?'!important
  }
  .newUI .entry:hover .svg-icon:hover,
  .svg-icon:hover {
    fill: #45a1ff!important
  }
  #message-box.danger #message-box-title {
    background-color: #b22222!important
  }
  .newUI .can-update .update,
  .newUI .no-update:not(.update-problem):not(.update-done) .up-to-date {
    color: #000!important
  }
  .svg-icon {
    fill: #b1b1b3!important
  }
  #url {
    filter: invert(65%)!important
  }
  .CodeMirror-gutters {
    color: #b1b1b3!important
  }
  .newUI .entry {
    background: #38383d!important
  }
  .newUI .entry:hover {
    background: #2a2a2e!important
  }
  .disabled h2::after {
    border: 1px solid #444!important
  }
  .svg-icon.select-arrow {
    fill: silver!important
  }
  .newUI .entry .style-name:hover::before {
    background: 0 0!important
  }
  body.all-styles-hidden-by-filters::after,
  body.all-styles-hidden-by-filters::before {
    color: #45a1ff!important
  }
  .active #filters-stats {
    background-color: #45a1ff!important;
    border-color: #45a1ff!important
  }
  #reset-filters {
    fill: #45a1ff!important
  }
  .CodeMirror {
    border: 1px solid #4a4a4f;
    border-radius: 4px!important
  }
  .Codemirror-hints {
    background: #353b48!important;
    border-color: #737373!important
  }
  .Codemirror-hint {
    color: #d7d7db!important
  }
  .CodeMirror {
    background: #2a2a2e!important;
    color: #b1b1b3!important
  }
  div.CodeMirror-selected {
    background: #353b48!important
  }
  .CodeMirror-line > span > span::selection,
  .CodeMirror-line > span::selection,
  .CodeMirror-line::selection {
    background: #353b48!important
  }
  .CodeMirror-line > span > span::-moz-selection,
  .CodeMirror-line > span::-moz-selection,
  .CodeMirror-line::-moz-selection {
    background: #353b48!important
  }
  .CodeMirror-gutters {
    background: #2a2a2e!important;
    border-right: 0 solid #4a4a4f!important
  }
  .CodeMirror-guttermarker,
  .CodeMirror-guttermarker-subtle {
    color: #555!important
  }
  .CodeMirror-linenumber {
    color: #939393!important
  }
  .CodeMirror-cursor {
    border-left: 1px solid #fff!important
  }
  span.cm-comment {
    color: #939393!important
  }
  span.cm-atom,
  span.cm-attribute,
  span.cm-builtin,
  span.cm-error,
  span.cm-keyword,
  span.cm-quote {
    color: #ff7de9!important
  }
  span.cm-number,
  span.cm-string,
  span.cm-string-2 {
    color: #6b89ff!important
  }
  span.cm-hr,
  span.cm-meta {
    color: #939393!important
  }
  span.cm-header,
  span.cm-qualifier,
  span.cm-variable-2 {
    color: #75bfff!important
  }
  span.cm-property {
    color: #86de74!important
  }
  span.cm-bracket,
  span.cm-def,
  span.cm-link:visited,
  span.cm-tag {
    color: #75bfff!important
  }
  span.cm-variable {
    color: #b98eff!important
  }
  span.cm-variable-3 {
    color: #d7d7db!important
  }
  span.cm-link {
    color: #737373!important
  }
  span.cm-operator {
    color: #b1b1b3!important
  }
  span.cm-special {
    color: #d7d7db!important
  }
  .CodeMirror-activeline-background {
    background: rgba(185,215,253,.15)!important
  }
  .CodeMirror-matchingbracket {
    outline: rgba(255,255,255,.25) solid 1px!important;
    color: #fff!important
  }
  select {
    color: silver;
  }
  option, optgroup {
    background-color: #2a2a2e
  }
}
