css:
@-moz-document domain(twitch.tv){

.top-nav__menu {
    background-color: #ffb4ec !important;
}
.twilight-root {
    color: #ffb4ec;
}

.tw-c-background {
    background-color: #ffe4f7 !important
}

div.simplebar-content {
    color: #ecb9ff;
}

body {
    color: #ff6eca;
    font-family: Helvetica Neue,Helvetica,Arial,sans-serif;
    font-size: 1.2rem;
    line-height: 2rem;
}

.scrollable-area--suppress-scroll-x .simplebar-content, .scrollable-area--suppress-scroll-x .simplebar-scroll-content {

    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
    background-color: #ebc6ff;

}

.chat-line__moderation, .chat-line__status {
    color: #ffa4e1;
}

div.tw-border-t.tw-pd-1 {
    background-color: #ffe4f8;
}

.tw-theme--dark .side-nav-card__link {

    background: #ffb7ec;

}

.tw-theme--dark .side-nav-card__title {

    color: #ff7ee7;

}

.tw-theme--dark .side-nav-card__metadata {
    color: #ff39d3;
}

side-nav-show-more-toggle__button {
    color: #d77bff;
}

.tw-textarea {
    border: 1px solid #d480ff;
    background: #ebc6ff;
    color: #f9e6ff;
}

.tw-theme--dark .tw-c-text-alt {
    color: #e07bff !important;
}

.tw-theme--dark .tw-c-text-alt-2 {

    color: #ffdef6 !important;

}

.tw-c-text-alt-2 {
    color: #ffb4e9 !important;
}

.tw-c-text {
    color: #ffa9e6 !important;
}

.chat-line__moderation, .chat-line__status {
    color: #ffa4e1;
}

.tw-c-background-alt-2 {
    background-color: #ffe6fc !important;
}

.tw-theme--dark .tw-checkbox__label {
    color: #fbecff;
}

.tw-pd-3 {
    padding: 3rem !important;
    background: url(https://78.media.tumblr.com/tumblr_lmr1ogcVjP1qcfn0j.bmp) repeat center fixed! important;
}

.top-nav__nav-link {
    color: #ff78e1;   
}

.tw-button, .tw-theme--dark .tw-button, .tw-theme--light .tw-button {
    border: 1px solid #ccefff00;
    background: #b4e5ff;
    color: #5baeff;
}

.tw-button-icon--disabled, .tw-button-icon:disabled {

    border-color: rgba(0,0,0,.05);
    background: rgba(0,0,0,.05);
    color: #cd7eff;
    cursor: not-allowed;

}

.tw-theme--dark .tw-interactable--hover, .tw-theme--dark .tw-interactable:hover {
    background: #e6c2ff;
    color: #fff;
}

.tw-theme--dark .tw-input {
    border: 1px solid #dd81ff;
    background: #ebbeff;
    color: #faf9fa;
}

.tw-theme--dark .tw-border-t {
    border-top: 1px solid #d481ff !important;
}

.tw-button-icon {
    color: #efd4ff;
}

.tw-svg__asset--brand {
    fill: #eab9ff;
}

.tw-input {
    border: 1px solid #68b4ff;
        border-top-color: rgb(104, 180, 255);
        border-right-color: rgb(104, 180, 255);
        border-bottom-color: rgb(104, 180, 255);
        border-left-color: rgb(104, 180, 255);
    background: #bedfff;
    color: #5ea4ff;
    display: block;
    position: relative;
    width: 100%;
    padding: .5rem 1rem;
        padding-left: 1rem;
    -webkit-transition: border .12s ease-in,-webkit-box-shadow .12s ease-in;
    transition: border .12s ease-in,-webkit-box-shadow .12s ease-in;
    transition: box-shadow .12s ease-in,border .12s ease-in;
    transition: box-shadow .12s ease-in,border .12s ease-in,-webkit-box-shadow .12s ease-in;
    border-radius: 0;
    outline: 0;
    font-family: inherit;
    font-size: 1.2rem;
    line-height: 1.5;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
}

.tw-input:focus, .tw-theme--dark .tw-input:focus, .tw-theme--light .tw-input:focus {
    border-color: #d5aeff;
    -webkit-box-shadow: 0 0 6px -2px #7d5bbe;
    box-shadow: 0 0 6px -2px #7d5bbe;
}

.tw-theme--dark .tw-c-text {
    color: #ff78e1 !important;
}

.tw-theme--dark .tw-c-background {
    background-color: #ffb4ec !important;
}

.tw-theme--dark .tw-c-background-alt {
    background-color: #ffb4ec !important;
}

.blue-bar, .tw-theme--dark .blue-bar, .tw-theme--light .blue-bar {
    background: #d09eff;
    color: #ecd4ff !important
}

.side-nav_overlay-wallpaper {
    background-color: #afcaff;
    color: #7aa7ff !important;
}

.tw-dp-3 {
    background: #fff url(https://78.media.tumblr.com/tumblr_lmr1ogcVjP1qcfn0j.bmp) repeat center fixed! important;
}

.tw-pd-t-2 .tw-pd-x-3 {
    
    background: #fff url(https://78.media.tumblr.com/tumblr_lmr1ogcVjP1qcfn0j.bmp) repeat center fixed! important;
    
}

.channel-header {
    background-color: #f9ceff;
    border-bottom: 1px solid #dad8de;
}

.channel-header__item {
    color: #e57bff;
}

.channel-header__item-count {
    color: #8fc4ff;
}

.follow-btn__dropdown-toggle, .tw-theme--dark .follow-btn__dropdown-toggle, .tw-theme--light .follow-btn__dropdown-toggle {
    background: #ffdaf6;
    color: #ffacea;
}

.tw-c-background-alt {
    background-color: #f4d6ff !important;
}

/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/food/foo-6/foo507.ani), url(http://cur.cursors-4u.net/food/foo-6/foo507.png), progress !important;} /* End http://www.cursors-4u.com */

}
