css:
@-moz-document domain(twitter.com){

.bird-topbar-etched {
    color: #ebabff;
    cursor: pointer;
    font-size: 21px;
    margin-top: 14px;
    margin-left: -24px;
    display: inline-block;
    transition: all .25s ease-in-out;
    font-weight: bold;
}

.global-nav-inner {
    background: #ffeafa;
    height: 46px;
}

.global-nav {
    border-bottom: 3px solid rgb(242, 207, 255);
}

.global-nav .search-input {
    background-color: #f2daff;
    border: 1px solid #cc83ff;
    color: #d984ff;
}

.global-nav .search-input {
    background-color: #f2daff;
    border: 1px solid #cc83ff;
    color: #d984ff;
}

.DashboardProfileCard {
    border: 4px solid #ffcff8;
    background: #fff;
}

.Trends {
    border: 4px solid #ffcff8;
}

.Trends .context-trend-item .trend-item-stats {
    color: #dfa1ff;
}

.module .flex-module {
    border: 1px solid #ffcff8;
}

.metadata, .metadata button.btn-link, .metadata a {
    font-size: 12px;
    color: #d691ff;
}

.fullname {
    color: #ffa9f2;
    font-weight: bold;
}

.account-summary .dismiss {
    color: #ffbff5;
}

.import-prompt p > span {
    color: #ffb7ef;
}

.Footer-item, .Footer-link {
    color: #d2a9ff;
}

.top-timeline-tweetbox .timeline-tweet-box {
    border: 4px solid #ffcff8;
}

.tweet {
    border-bottom: 4px solid #ffcff8;
    cursor: pointer;
}

body {
    color: #e49cff;
    font-size: 14px;
    line-height: 20px;
}

.ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #e6c2ff;
}

.new-tweets-bar {
    color: #ff7be1;
    background-color: #f5f8fa;
    border-top: 1px solid #ffdcf8;
    border-bottom: 4px solid #ffcff8;
}

.stream-item:not(.no-header-background-module) {
    background: #fff;
    border-left: 4px solid #ffcff8;
    border-right: 4px solid #ffcff8;
}

.wtf-module .UserSmallListItem {
    border-bottom: 2px solid #edc9ff;
}

.ProfileCardStats-statLabel {
    color: #c24bff;
}

body {
    background: #fcd8ff url(https://78.media.tumblr.com/tumblr_lmr1oa7sht1qcfn0j.bmp) repeat center fixed! important;
}

/* Start http://www.cursors-4u.com */ body, a:hover {cursor: url(http://cur.cursors-4u.net/others/oth-6/oth653.cur), progress !important;} /* End http://www.cursors-4u.com */

}
