css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace html url("http://www.w3.org/1999/xhtml");

/* General text tweaks [font: message-box gives San Francisco font in OS X 10.11] */
label { text-shadow: none !important; }

menu, menuitem
{
	font: message-box !important;
	font-size: 10pt !important;
}

/* ******************************************** */
/* Main Toolbar heights/widths and arrangements */
/* ******************************************** */
#main-window { background: linear-gradient(to bottom, rgb(240,238,240), rgb(222,220,222) 37px, rgb(221,219,221) 60px, rgb(208,206,208) 100px) !important; }
#main-window:-moz-window-inactive { background: rgb(246,246,246) !important; }

#titlebar
{
	-moz-appearance: none !important;
	/*-moz-appearance: -moz-mac-vibrancy-light !important; */
	height: 37px !important;
	margin-bottom: -37px !important;
}

#titlebar-buttonbox-container
{
	margin-left: 5px !important;
	margin-top: 10.5px !important;
}

#nav-bar
{
	/*-moz-appearance: -moz-mac-vibrancy-light !important; */
	position: relative !important;
	z-index: 1 !important;
	-moz-box-ordinal-group: 1 !important;
	-moz-box-align: center !important;
	margin: 0px 5px 0px 75px !important;
	padding: 0px 4px !important;
	border-top: none !important;
	max-height: 37px !important;
	min-height: 37px !important;
	background-image: unset !important;
	background-color: transparent !important;
}

#main-window[inFullscreen] #nav-bar { margin-left: 5px !important; }

#nav-bar-customization-target
{
	padding: 0px !important;
	margin: 0px !important;
	min-height: 37px !important;
	max-height: 37px !important;
	-moz-box-align: center !important;
}

#PersonalToolbar
{
	-moz-box-align: center !important;
	-moz-box-ordinal-group: 2 !important;
	margin-left: 1px !important;
	position: relative !important;
	z-index: 2 !important;
	border-top: 1px rgba(0,0,0,0.1) solid !important;
	background-color: transparent !important;
	padding: unset !important;
	min-height: 23px !important;
	max-height: 23px !important;
}

#personal-bookmarks, #PlacesToolbar, #PlacesToolbarItems, 
#PlacesToolbarItems > .scrollbox-innerbox { -moz-box-align: center !important; }

#TabsToolbar
{
	/*  box-shadow: inset 0px 1px 0px 0px rgb(162,160,162), 
        inset 0px -1px 0px 0px rgb(162,160,162) !important;*/
	box-shadow: none!important;
	margin-bottom: 0px !important;
	margin-left: 0px !important;
	margin-right: -1px !important;
	background: linear-gradient(to bottom, rgb(192,190,192),rgb(187,185,187)) !important;
	-moz-box-ordinal-group: 10 !important;
	border-top: 1.5px solid #ccc !important;
	border-bottom: 1.5px solid #ccc !important;
}

#TabsToolbar:-moz-window-inactive
{
	background: linear-gradient(to bottom, rgb(202,200,202),rgb(197,195,197)) !important;
	/*  box-shadow: inset 0px 1px 0px 0px rgb(152,150,152), 
        inset 0px -1px 0px 0px rgb(152,150,152) !important;*/
	box-shadow: none!important;
}

#TabsToolbar .titlebar-placeholder { display: none !important; }

#tabbrowser-tabs
{
	margin-left: 0px !important;
	height: 24px !important;
}

/* remove this weird extra separator mozilla tacks on before the page content */
#navigator-toolbox::after { display: none !important; }

/* ****************************************** */
/* detailed tab positioning and styling stuff */
/* ****************************************** */
#TabsToolbar .tab-content > *
{
	margin-top: 0px !important;
	margin-bottom: 0px !important;
}

/* get rid of the little spacer lines between tabs that browser.css puts in via CSS */
#TabsToolbar .tabbrowser-tab::after,
#TabsToolbar .tabbrowser-tab::before { visibility: collapse; }

#TabsToolbar .tab-close-button
{
	-moz-box-ordinal-group: 0 !important;
	margin-left: -5.5px !important;
	border-radius: 2px !important;
	transition: opacity 200ms !important;
	/* small x */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16">\ <line x1="4.5" y1="4.5" x2="11.5" y2="11.5" stroke="rgb(84,82,84)" stroke-width="1"/>\ <line x1="4.5" y1="11.5" x2="11.5" y2="4.5" stroke="rgb(84,82,84)" stroke-width="1"/>\ </svg>') !important;
}

@media (max-resolution: 1.9dppx)
{
	#TabsToolbar .tab-close-button { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16">\ <line x1="4.5" y1="4.5" x2="11.5" y2="11.5" stroke="rgb(84,82,84)" stroke-width="1.5"/>\ <line x1="4.5" y1="11.5" x2="11.5" y2="4.5" stroke="rgb(84,82,84)" stroke-width="1.5"/>\ </svg>') !important; }
}

#TabsToolbar tab:not(:hover) .tab-close-button { opacity: 0 !important; }
#TabsToolbar tab:hover .tab-close-button { opacity: 1 !important; }
#TabsToolbar tab .close-icon { -moz-image-region: unset !important; }

#TabsToolbar tab .close-icon:hover
{
	-moz-image-region: unset !important;
	background-color: rgba(0,0,0,0.1) !important;
	background-clip: padding-box !important;
}

#TabsToolbar tab .close-icon:hover:active { background-color: rgba(0,0,0,0.15) !important; }

#TabsToolbar .tab-label-container
{
	-moz-box-ordinal-group: 2 !important;
	margin-left: 4px !important;
}

#TabsToolbar .tab-content:not([pinned]) > .tab-icon-sound
{
	-moz-box-ordinal-group: 3 !important;
	margin-left: 0px !important;
	margin-right: -4px !important;
}

#TabsToolbar .tab-content:not([pinned]) > .tab-throbber
{
	-moz-box-ordinal-group: 4 !important;
	margin-right: -4px !important;
}

#TabsToolbar .tab-content:not([pinned]) > .tab-icon-image { visibility: collapse !important; }

#TabsToolbar .tabbrowser-tab[fadein]:not([pinned])
{
	flex-grow: 1 !important;
	min-width: 11em !important;
	max-width: -moz-available !important;
}

#TabsToolbar .tabbrowser-tab
{
	min-height: 24px !important;
	max-height: 24px !important;
}

#TabsToolbar .tabbrowser-arrowscrollbox > .arrowscrollbox-scrollbox
{
	padding-inline-start: 0px !important;
	padding-inline-end: 0px !important;
}

#TabsToolbar .tab-content:not([selected=true]) { color: rgba(0,0,0,0.8) !important; }
#TabsToolbar .tab-content[selected=true]:-moz-window-inactive { opacity: 0.8 !important; }

/* .tab-background and .tab-background-middle (margins, borders and backgrounds):
   .tab-background changes bg instantly based on selected property,
   .tab-background-middle adds dimming effect on hover via transitions  
   left/right borders are on .tab-background and overlap via margin-left: -1px 
   top/bottom borders are mostly from TabsToolbar inner box-shadows */

#TabsToolbar .tab-background-start, 
#TabsToolbar .tab-background-end { visibility: collapse !important; }

#TabsToolbar .tab-background-middle
{
	border-left: none !important;
	border-right: none !important;
	margin-inline-start: 0px !important;
	margin-inline-end: 0px !important;
	background-image: none !important;
	clip-path: unset !important;
}

#TabsToolbar .tab-background-middle:not([selected=true]) { transition: background-color 200ms ease-in-out !important; }

#TabsToolbar .tab-background
{
	height: 24px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	border-left: 1px solid rgb(162,160,162) !important;
	border-right: 1px solid rgb(162,160,162) !important;
	background: transparent !important;
	background-clip: padding-box !important;
}

#tabbrowser-tabs[overflow] .tabbrowser-tab:hover .tab-background-middle:not([selected=true]):not([pinned]),
#tabbrowser-tabs:not([overflow]) .tabbrowser-tab:hover .tab-background-middle:not([selected=true]) { background: rgba(0,0,0,0.05) !important; }

#tabbrowser-tabs .tab-background[selected=true]
{
	background: linear-gradient(to bottom, rgba(221,219,221,1), rgba(214,212,214,1)) !important;
	/*  border-top: 1px solid rgb(188,186,188) !important; */
	border-top: none!important;
	border-bottom: 1px solid rgb(162,160,162) !important;
}

#TabsToolbar .tab-background[selected=true]:-moz-window-inactive { background: rgb(236,236,236) !important; }

#TabsToolbar .tab-background:not([selected=true]):-moz-window-inactive,
#tabbrowser-tabs[overflow] .tab-background[pinned]:not([selected=true]):-moz-window-inactive
{
	background: rgb(236,236,236) !important;
	opacity: 0.5 !important;
	transition: unset !important;
	border-color: rgba(192,192,192,1) !important;
}

/* pinned tab styling */
#tabbrowser-tabs[overflow] .tab-background[pinned]:not([selected=true])
{
	background: linear-gradient(to bottom, rgb(202,200,202), rgb(197,195,197)) padding-box !important;
	/*  border-top: 0.5px solid rgb(162,160,162) !important; */
	border-top: none!important;
	border-bottom: 0.5px solid rgb(162,160,162) !important;
}

#TabsToolbar .tab-background[pinned]
{
	border-top-width: 0.5px !important;
	border-bottom-width: 0.5px !important;
}

#TabsToolbar .tab-content[pinned]
{
	margin: unset !important;
	padding: unset !important;
	width: 36px !important;
}

#TabsToolbar .tab-content[pinned] > .tab-text { display: none !important; }
#TabsToolbar .tab-content[pinned] > .tab-throbber { margin-left: 10px !important; }

#TabsToolbar .tab-content[pinned] > .tab-icon-image
{
	margin-left: 10px !important;
	filter: grayscale(100%) !important;
	transition: filter 300ms !important;
}

#TabsToolbar tab:hover .tab-content[pinned]:not([selected=true]) > .tab-icon-image { filter: grayscale(0%) !important; }

#TabsToolbar .tab-content[pinned][selected=true] > .tab-icon-image
{
	transition-property: none !important;
	filter: grayscale(0%) !important;
}

/* overlay used for sound muting in pinned tabs */
#TabsToolbar .tab-content[pinned] > .tab-icon-overlay
{
	margin: unset !important;
	margin-left: -13px !important;
	margin-top: -6px !important;
}

#TabsToolbar:-moz-window-inactive .tab-content[pinned]:not([selected=true]) > .tab-icon-image { opacity: 0.5 !important; }

/* more for new tab buttons, all tabs button, and tab scroll buttons */
/* 26x24 button = 24x22 icon plus 2x2 borders */
.tabs-newtab-button
{
	min-width: 26px !important;
	min-height: 24px !important;
	width: 26px !important;
	height: 24px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	border-left: 1px solid rgb(162,160,162) !important;
	border-right: 1px solid rgb(162,160,162) !important;
	border-top: 1px solid rgb(162,160,162) !important;
	border-bottom: 1px solid rgb(162,160,162) !important;
	border-radius: 0px !important;
	transition: filter 200ms !important;
	background: linear-gradient(to bottom, rgb(192,190,192), rgb(187,185,187)) padding-box !important;
	/* plus sign */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22">\ <line x1="25%" y1="50%" x2="75%" y2="50%" stroke="rgb(105,103,105)" stroke-width="1"/>\ <line x1="50%" y1="25%" x2="50%" y2="75%" stroke="rgb(105,103,105)" stroke-width="1"/>\ </svg>') !important;
	-moz-image-region: unset !important;
}

@media (max-resolution: 1.9dppx)
{
	.tabs-newtab-button { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22">\ <line x1="25%" y1="50%" x2="75%" y2="50%" stroke="rgb(105,103,105)" stroke-width="2"/>\ <line x1="50%" y1="25%" x2="50%" y2="75%" stroke="rgb(105,103,105)" stroke-width="2"/>\ </svg>') !important; }
}

/* 24x22 icon = 22x22 image plus 2x0 padding */
.tabs-newtab-button > .toolbarbutton-icon
{
	padding: 0px 1px !important;
	width: 24px !important;
	height: 22px !important;
	min-width: 24px !important;
	min-height: 22px !important;
	border: unset !important;
}

.tabs-newtab-button:hover { filter: brightness(95%) !important; }
.tabs-newtab-button:hover:active { filter: brightness(90%) !important; }

/* 26x24 button = 24x23 icon plus 2x1 borders */
#TabsToolbar #new-tab-button
{
	min-width: 26px !important;
	min-height: 24px !important;
	width: 26px !important;
	height: 24px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	border-top: 0.5px solid rgb(162,160,162) !important;
	border-bottom: 0.5px solid rgb(162,160,162) !important;
	border-left: 1px solid rgb(162,160,162) !important;
	border-right: 1px solid rgb(162,160,162) !important;
	border-radius: 0px !important;
	box-shadow: none !important;
	transition: filter 200ms !important;
	background: linear-gradient(to bottom, rgb(202,200,202), rgb(197,195,197)) padding-box !important;
	/* plus sign */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22">\ <line x1="25%" y1="50%" x2="75%" y2="50%" stroke="rgb(105,103,105)" stroke-width="1"/>\ <line x1="50%" y1="25%" x2="50%" y2="75%" stroke="rgb(105,103,105)" stroke-width="1"/>\ </svg>') !important;
	-moz-image-region: unset !important;
}

@media (max-resolution: 1.9dppx)
{
	#TabsToolbar #new-tab-button { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22">\ <line x1="25%" y1="50%" x2="75%" y2="50%" stroke="rgb(105,103,105)" stroke-width="2"/>\ <line x1="50%" y1="25%" x2="50%" y2="75%" stroke="rgb(105,103,105)" stroke-width="2"/>\ </svg>') !important; }
}

/* 24x23 icon = 22x22 image plus 2x1 padding */
#new-tab-button > .toolbarbutton-icon
{
	padding: 0.5px 1px !important;
	width: 24px !important;
	min-width: 24px !important;
	min-height: 23px !important;
	height: 23px !important;
	border: unset !important;
}

#TabsToolbar #new-tab-button:hover:not([disabled]) > .toolbarbutton-icon,
#TabsToolbar #alltabs-button:hover:not([disabled]) > .toolbarbutton-icon { filter: brightness(60%); }
#TabsToolbar #new-tab-button:hover:active:not([disabled]) { filter: brightness(95%) !important; }

#TabsToolbar #alltabs-button
{
	min-width: 26px !important;
	min-height: 24px !important;
	width: 26px !important;
	height: 24px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	border-top: 0.5px solid rgb(162,160,162) !important;
	border-bottom: 0.5px solid rgb(162,160,162) !important;
	border-left: 1px solid rgb(162,160,162) !important;
	border-right: 1px solid rgb(162,160,162) !important;
	box-shadow: none !important;
	border-radius: 0px !important;
	transition: filter 200ms !important;
	background: linear-gradient(to bottom, rgb(202,200,202), rgb(197,195,197)) padding-box !important;
	-moz-image-region: unset !important;
	/* downward chevron */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="36" height="40">\ <path d="M9,18 L18,26 L27,18" fill="transparent" stroke-linecap="round" stroke="rgb(105,103,105)" stroke-width="2.5"/>\ </svg>') !important;
}

@media (max-resolution: 1.9dppx)
{
	#TabsToolbar #alltabs-button { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="36" height="40">\ <path d="M9,18 L18,26 L27,18" fill="transparent" stroke-linecap="round" stroke="rgb(105,103,105)" stroke-width="4"/>\ </svg>') !important; }
}

#TabsToolbar > #alltabs-button:-moz-any(:hover:active, [open]):not([disabled]) { filter: brightness(95%) !important; }

/* These things suck, we'll do better with CSS3 effects */
.tabbrowser-arrowscrollbox > .arrowscrollbox-overflow-start-indicator, 
.tabbrowser-arrowscrollbox > .arrowscrollbox-overflow-end-indicator { display: none !important; }

.tabbrowser-arrowscrollbox > .scrollbutton-up
{
	background: linear-gradient(to bottom, rgb(202,200,202), rgb(197,195,197)) padding-box !important;
	min-height: 24px !important;
	height: 24px !important;
	-moz-image-region: unset !important;
	border-left: 1px solid rgb(162,160,162) !important;
	border-right: unset !important;
	border-radius: 0px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	position: relative !important;
	z-index: 3 !important;
	filter: brightness(100%) !important;
	/*    box-shadow:
        inset -0.5px 0px 0px 0.5px rgba(0,0,0,0.3),
        3px 0px 3px -2px rgba(0,0,0,0.3);
    transition: box-shadow 150ms ease-in-out, filter 200ms ease-in-out !important; */
	box-shadow: none!important;
	/* left pointing thick chevron */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="13" height="20">\ <path d="M9.5,3.5 L3,10 L9.5,16.5" fill="transparent" stroke-width="3" stroke="rgb(125,122,125)"/>\ </svg>') !important;
}

@media (max-resolution: 1.9dppx)
{
	.tabbrowser-arrowscrollbox > .scrollbutton-up { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="13" height="20">\ <path d="M9.5,3.5 L3,10 L9.5,16.5" fill="transparent" stroke-width="4" stroke="rgb(125,122,125)"/>\ </svg>') !important; }
}

.tabbrowser-arrowscrollbox > .scrollbutton-up[disabled]
{
	filter: brightness(95%) !important;
	/* box shadow "outer border", a cheap hack around weird margins when tabs are pinned */
	box-shadow: inset 0px 0px 0px 0px rgba(0,0,0,0), 1px 0px 0px 0px rgba(162,160,162,1);
	border-top: 1px solid rgb(162,160,162) !important;
	border-bottom: 1px solid rgb(162,160,162) !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-up[disabled] > .toolbarbutton-icon,
.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled] > .toolbarbutton-icon { -moz-image-region: unset !important; }

.tabbrowser-arrowscrollbox > .scrollbutton-down
{
	background: linear-gradient(to bottom, rgb(202,200,202), rgb(197,195,197)) padding-box !important;
	min-height: 24px !important;
	height: 24px !important;
	-moz-image-region: unset !important;
	/* right pointing thick chevron */
	border-left: unset !important;
	border-right: 1px solid rgb(162,160,162) !important;
	border-radius: 0px !important;
	margin-left: -1px !important;
	margin-right: 0px !important;
	position: relative !important;
	z-index: 3 !important;
	filter: brightness(100%) !important;
	box-shadow: inset 0.5px 0px 0px 0.5px rgba(0,0,0,0.3), -3px 0px 3px -2px rgba(0,0,0,0.3);
	transition: box-shadow 200ms ease-in-out, filter 200ms ease-in-out !important;
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="13" height="20">\ <path d="M3.5,3.5 L10,10 L3.5,16.5" fill="transparent" stroke-width="3" stroke="rgb(125,122,125)"/>\ </svg>') !important;
}

@media (max-resolution: 1.9dppx)
{
	.tabbrowser-arrowscrollbox > .scrollbutton-down { list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="13" height="20">\ <path d="M3.5,3.5 L10,10 L3.5,16.5" fill="transparent" stroke-width="4" stroke="rgb(125,122,125)"/>\ </svg>') !important; }
}

.tabbrowser-arrowscrollbox > .scrollbutton-up:hover:not([disabled])
{
	-moz-image-region: unset !important;
	box-shadow: inset -0.5px 0px 0px 0.5px rgba(0,0,0,0.3), 3px 0px 3px -2px rgba(0,0,0,0.3) !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-down:hover:not([disabled])
{
	-moz-image-region: unset !important;
	box-shadow: inset 0.5px 0px 0px 0.5px rgba(0,0,0,0.3), -3px 0px 3px -2px rgba(0,0,0,0.3) !important;
}

.tabbrowser-arrowscrollbox > .scrollbutton-down:hover:not([disabled]) > .toolbarbutton-icon,
.tabbrowser-arrowscrollbox > .scrollbutton-up:hover:not([disabled]) > .toolbarbutton-icon { filter: brightness(80%) !important; }

.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled]
{
	filter: brightness(95%) !important;
	margin-left: -1px !important;
	border-left: 1px solid rgba(162,160,162,1) !important;
	border-top: 1px solid rgb(162,160,162) !important;
	border-bottom: 1px solid rgb(162,160,162) !important;
	box-shadow: unset !important;
}

.tabbrowser-arrowscrollbox > *:-moz-any(.scrollbutton-up, .scrollbutton-down):hover:active:not([disabled]) { filter: brightness(95%) !important; }

.tabs-newtab-button:-moz-window-inactive, 
#TabsToolbar #new-tab-button:-moz-window-inactive, 
#TabsToolbar #alltabs-button:-moz-window-inactive, 
.tabbrowser-arrowscrollbox > .scrollbutton-up:-moz-window-inactive, 
.tabbrowser-arrowscrollbox > .scrollbutton-down:-moz-window-inactive
{
	background: rgb(236,236,236) !important;
	opacity: 0.5 !important;
	transition: unset !important;
	border-color: rgba(192,191,192,1) !important;
	border-radius: 0px !important;
	filter: brightness(100%) !important;
}

/* ************************** */
/* Navigation Toolbar Styling */
/* ************************** */
#TabsToolbar .toolbarbutton-1,
#TabsToolbar .toolbarbutton-1 > .toolbarbutton-menubutton-button,
.tabbrowser-arrowscrollbox > .scrollbutton-up,
.tabbrowser-arrowscrollbox > .scrollbutton-down { border-width: 0.5px !important; }

#nav-bar
{
	--tbb-background: linear-gradient(to bottom, hsla(0,0%,99%,1), hsla(0,0%,95%,1));
	--tbb-active-background: linear-gradient(to bottom, hsla(0,0%,89%,1), hsla(0,0%,85%,1));
	--tbb-boxshadow: inset 0 -0.5px 0px 0px hsla(0,0%,70%,1), inset 0 0.5px 0.5px 0px white;
	--tbb-border: 0.5px solid rgb(213,211,213);
	--toolbarbutton-inner-padding: 2px;
	--toolbarbutton-border-radius: 4px;
	--toolbarbutton-hover-background: none;
	--toolbarbutton-hover-bordercolor: transparent;
	--toolbarbutton-hover-boxshadow: none;
	--toolbarbutton-active-background: none;
	--toolbarbutton-active-bordercolor: transparent;
	--toolbarbutton-active-boxshadow: none;
	--toolbarbutton-checkedhover-backgroundcolor: transparent;
}

window[chromehidden~="status"] #nav-bar .chromeclass-status,
