	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18">\ <path d="M10.5,3.5 L5.5,8.5 L10.5,13.5" fill="transparent" stroke="rgb(128,128,128)" stroke-width="1.6" stroke-linecap="round"/>\ </svg>') !important;
