/* urlbar-container : contains back+forward buttons and urlbar */
#nav-bar #urlbar-container,
#nav-bar #search-container
{
	height: 27px !important;
	padding: 0px 3.5px !important;
	-moz-box-pack: center !important;
	-moz-window-dragging: drag !important;
}

#titlebar { -moz-box-pack: initial !important; }

/* some spacers to avoid ultra-long urlbar */
#nav-bar #urlbar-container::before,
#nav-bar #urlbar-container::after
{
	content: '';
	display: inline-flex;
	min-width: 7px;
	-moz-box-flex: 1 !important;
}

/* urlbar-wrapper : contains forward button and urlbar */
#nav-bar #urlbar-wrapper
{
	margin: 0px !important;
	height: 37px !important;
	clip-path: unset !important;
	max-width: calc(var(--ubwidth) + 28px) !important;
	width: calc(var(--ubwidth) + 28px) !important;
}

/* urlbar : contains the url, info icons, go/reload/stop buttons etc */
#nav-bar #urlbar
{
	height: 23px !important;
	min-height: 23px !important;
	min-width: calc(var(--ubwidth) - 24px) !important;
	width: var(--ubwidth) !important;
	max-width: var(--ubwidth) !important;
	margin: 7px 0px !important;
	padding-inline-start: 2px !important;
	background: linear-gradient(to bottom, hsla(0,0%,99%,1), hsla(0,0%,95%,1)) padding-box !important;
	border: 0.5px solid rgb(213,211,213) !important;
	border-radius: 4px !important;
	font: message-box !important;
	font-size: 12px !important;
	color: rgb(87,87,87) !important;
	/* last two box shadows here are transparent counterparts for animating focus ring shadows below */
	box-shadow: inset 0 -0.5px 0px 0px hsla(0,0%,70%,1), inset 0 0.5px 0.5px 0px white, 0 0 5px 15px rgba(151,195,243,0), inset 0 0 0 0px rgba(151,195,243,0) !important;
}

#nav-bar #urlbar[focused="true"]
{
	color: black !important;
	/* Animated box shadows to recreate the native OS X 10.11 focus-ring effect */
	transition: box-shadow 300ms, border 300ms !important;
	box-shadow: inset 0 -0.5px 0px 0px rgb(168,205,245), inset 0 0.5px 0.5px 0px white, 0 0 0px 2.5px rgba(151,195,243,0.9), inset 0 0 0 0.5px rgba(151,195,243,0.9) !important;
	border: 0.5px solid rgba(136,178,220,0.9) !important;
}

/* urlbar-input-box holds the actual URL textbox */
#nav-bar #urlbar .urlbar-input-box
{
	-moz-box-ƒ√: 5 !important;
	border: none !important;
	padding-inline-start: 2px !important;
	-moz-box-ordinal-group: 3 !important;
	transition: all 300ms ease-in-out !important;
}

.urlbar-input-box, .searchbar-textbox > .autocomplete-textbox-container > .textbox-input-box { text-align: center; }
#nav-bar #urlbar[focused=true] .urlbar-input-box { text-align: left; }

#nav-bar #urlbar[focused=true] .urlbar-input-box::after
{
	opacity: 0 !important;
	transition: unset;
}

/* note: private browsing mode keeps this as is, non-private is the more complex case */
#main-window:not([privatebrowsingmode=temporary]) #nav-bar:-moz-window-inactive #urlbar .urlbar-input-box::after { background: linear-gradient(to left, rgba(255,255,255,1), rgba(255,255,255,0)) 0px 0px / 50px 100% no-repeat, linear-gradient(to bottom, rgb(255,255,255), rgb(255,255,255)) 50px 0px / calc(100% - 50px) 100% no-repeat; }

/* URL bar autocomplete popup/dropdown */
#PopupAutoCompleteRichResult
{
	margin-inline-start: calc(-2px + (var(--ubwidth) - var(--ubdrop)) / 2) !important;
	margin-top: -2px !important;
	max-width: var(--ubdrop) !important;
	border-radius: 5px !important;
	/*     background: linear-gradient(to bottom, rgb(215,213,215), rgb(192,190,192)) !important; */
}

.autocomplete-richlistbox
{
	background: linear-gradient(to bottom, rgb(237,237,237), rgb(239,239,239)) border-box !important;
	border-top-left-radius: 5px !important;
	border-top-right-radius: 5px !important;
	padding-left: 0 !important;
	padding-right: 0 !important;
}

.autocomplete-richlistbox > scrollbox { overflow-y: hidden !important; }

.autocomplete-richlistitem
{
	min-height: 20px !important;
	max-height: 20px !important;
	border: unset !important;
	border-radius: unset !important;
	padding-left: 16px !important;
	padding-right: 16px !important;
}

/* *** */
.searchbar-engine-one-off-item:hover,
.searchbar-engine-one-off-item[selected],
.autocomplete-richlistitem[selected],
.autocomplete-richlistitem:hover { background-color: rgb(17,108,214); }

.ac-site-icon, .ac-type-icon
{
	margin-inline-start: 1px !important;
	margin-inline-end: 8px !important;
}

.ac-site-icon:not([type=searchengine]), 
.ac-type-icon:not([type=switchtab]):not([type=remotetab]):not([type=keyword]),
.ac-separator { display: none !important; }

.ac-type-icon[type=keyword],
.ac-site-icon[type=searchengine]
{
	/* magnifying glass */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16">\ <circle cx="7" cy="7" r="4" fill="none" stroke="rgb(113,113,113)" stroke-width="1" />\ <path d="M13.5,13.5 L10,10" stroke="rgb(113,113,113)" stroke-width="1" />\ </svg>') !important;
}

.autocomplete-richlistitem:hover .ac-type-icon[type=keyword],
.autocomplete-richlistitem:hover .ac-site-icon[type=searchengine],
.ac-type-icon[type=keyword][selected],
.ac-site-icon[type=searchengine][selected]
{
	/* white magnifying glass */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16">\ <circle cx="7" cy="7" r="4" fill="none" stroke="white" stroke-width="1" />\ <path d="M13.5,13.5 L10,10" stroke="white" stroke-width="1" />\ </svg>') !important;
}

.ac-title
{
	font-size: 12px !important;
	color: rgb(62,62,62);
}

.ac-url { color: gray !important; }
.autocomplete-richlistitem:hover *,
.autocomplete-richlistitem[selected] * { color: white !important; }

.autocomplete-richlistitem:hover .ac-tags-text > html|span.ac-tag,
.autocomplete-richlistitem[selected] .ac-tags-text > html|span.ac-tag
{
	background-color: white;
	color: rgb(17,108,214);
}

.ac-normal-text { font: message-box !important; }

.ac-url, .ac-action
{
	font-size: 11px;
	margin: 0 !important;
	padding: 0 !important;
}

.ac-tags
