#nav-bar #urlbar[pageproxystate="invalid"] #identity-box { visibility: collapse !important; }
#nav-bar #urlbar #identity-box > *:not(#identity-icon-labels) { transition: width 300ms ease-in-out !important; }

#urlbar #identity-box > #identity-icon-labels
{
	max-width: calc((var(--ubwidth) - 40px) * 0.33) !important;
	-moz-box-ordinal-group: 6 !important;
	transition: margin 300ms ease-in-out !important;
}

#urlbar #identity-box > #identity-icon-labels > label
{
	max-width: calc((var(--ubwidth) - 40px) * 0.33) !important;
	transition: margin-left 300ms ease-in-out !important;
}

#nav-bar #urlbar[focused=true] > #identity-box > *:not(#identity-icon-labels)
{
	margin: 0px !important;
	padding: 0px !important;
	width: 0px !important;
	pointer-events: none !important;
	background: none !important;
	transition: margin 300ms ease-in-out, padding 300ms ease-in-out, width 300ms ease-in-out !important;
}

#nav-bar #urlbar[focused=true] #identity-icon-labels { margin-inline-start: calc((var(--ubwidth) - 40px) * -0.33) !important; }

#nav-bar #urlbar[focused=true] #identity-icon-labels > label
{
	margin-left: calc((var(--ubwidth) - 40px) * -0.33) !important;
	pointer-events: none !important;
}

/* identity box hovers */
#identity-box:hover > #identity-icon:not(.no-hover),
#identity-box[open=true] > #identity-icon,
#identity-box:hover > #identity-icon:not(.no-hover) ~ #tracking-protection-icon,
#identity-box[open=true] > #tracking-protection-icon,
#identity-box:hover > #identity-icon:not(.no-hover) ~ #blocked-permissions-container,
#identity-box[open=true] > #blocked-permissions-container { filter: brightness(60%) !important; }
#identity-box:hover > #identity-icon:not(.no-hover) ~ #connection-icon,
#identity-box[open=true] > #connection-icon { filter: brightness(60%) !important; }
#identity-box:hover:-moz-any(.verifiedDomain,.verifiedIdentity,.mixedActiveBlocked,.certUserOverridden,.insecureLoginForms,.mixedActiveContent,.weakCipher,
                                .mixedDisplayContent,.mixedDisplayContentLoadedActiveBlocked) > #identity-icon:not(.no-hover) ~ #connection-icon,
#identity-box[open=true]:-moz-any(.verifiedDomain,.verifiedIdentity,.mixedActiveBlocked,.certUserOverridden,.insecureLoginForms,.mixedActiveContent,.weakCipher,
                                .mixedDisplayContent,.mixedDisplayContentLoadedActiveBlocked) > #connection-icon,
#identity-box:hover > #identity-icon:not(.no-hover) ~ #identity-icon-labels,
#identity-box[open=true] > #identity-icon-labels { filter: brightness(75%) !important; }

/* notification popup box / blocked permissions container: info about stuff like plugin permissions and location requests */
.notification-anchor-icon
{
	margin-inline-start: 1px !important;
	width: 14px;
}

#notification-popup-box
{
	margin: 0 !important;
	padding: 0 !important;
}

#notification-popup-box > *, 
#blocked-permissions-container > * { transition: width 300ms ease-in-out, padding 300ms ease-in-out, margin 300ms ease-in-out !important; }

#nav-bar #urlbar[focused=true] #notification-popup-box > *, 
#nav-bar #urlbar[focused=true] #blocked-permissions-container > *
{
	width: 0px !important;
	padding: 0px !important;
	margin: 0px !important;
	pointer-events: none !important;
}

/* urlbar history dropmarker */
#navigator-toolbox .urlbar-history-dropmarker
{
	width: 15px !important;
	padding: 0px !important;
	box-shadow: inset 0 0.5px 0.5px 0px white !important;
	opacity: 1 !important;
	-moz-box-ordinal-group: 8 !important;
	-moz-image-region: unset !important;
}

#navigator-toolbox .urlbar-history-dropmarker > .dropmarker-icon
{
	width: 15px !important;
	padding: 9px 4px 6px 4px;
	margin: 0px !important;
}

#nav-bar .urlbar-history-dropmarker[open],
#main-window[privatebrowsingmode=temporary] #nav-bar .urlbar-history-dropmarker[open] { -moz-image-region: unset !important; }
#nav-bar .urlbar-history-dropmarker:hover:not([open]) > .dropmarker-icon { filter: brightness(50%) !important; }
#nav-bar #urlbar[focused="true"] .urlbar-history-dropmarker { margin-inline-start: -2px !important; }

/* go, stop, reload buttons */
#nav-bar #urlbar-go-button,
#nav-bar #go-button
{
	margin-inline-start: 2px !important;
	margin-inline-end: 4px !important;
	padding: unset !important;
	border: none !important;
	-moz-image-region: unset !important;
	/* arrow pointing right */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18">\ <line x1="1.5" y1="9" x2="16.5" y2="9" stroke="rgb(125,125,125)" stroke-width="1.5"/>\ <path d="M9.5,2 L16.5,9 L9.5,16" fill="transparent" stroke="rgb(125,125,125)" stroke-width="1.5"/>\ </svg>') !important;
}

#nav-bar #urlbar-reload-button,
#nav-bar #reload-button
{
	margin-inline-start: 2px !important;
	margin-inline-end: 4px !important;
	padding: unset !important;
	border: none !important;
	-moz-image-region: unset !important;
	/* 270 degree arc w/triangle endcap */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18">\ <path d="M15,9.5 A 6,6 0 1 1 9,3.5" fill="transparent" stroke="rgb(125,125,125)" stroke-width="1.5"/>\ <path d="M9,0 L9,7 L14.5,3.5 C" stroke="rgb(125,125,125)" fill="rgb(125,125,125)" stroke-width="0.5"/>\ </svg>') !important;
}

#nav-bar #urlbar-stop-button,
#nav-bar #stop-button
{
	margin-inline-start: 2px !important;
	margin-inline-end: 4px !important;
	padding: unset !important;
	border: none !important;
	-moz-image-region: unset !important;
	/* big X */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18">\ <line x1="3" y1="3" x2="15" y2="15" stroke="rgb(125,125,125)" stroke-width="1.5"/>\ <line x1="3" y1="15" x2="15" y2="3" stroke="rgb(125,125,125)" stroke-width="1.5"/>\ </svg>') !important;
}

#nav-bar *:-moz-any(#urlbar-stop-button, #urlbar-reload-button, #urlbar-go-button, #stop-button, #reload-button, #go-button):hover { filter: brightness(50%); }

/* search textbox */
#nav-bar .searchbar-textbox
{
	height: 23px !important;
	min-height: 23px !important;
	margin: 7px 3.5px !important;
	background: linear-gradient(to bottom, hsla(0,0%,99%,1), hsla(0,0%,95%,1)) padding-box !important;
	border: 0.5px solid rgb(213,211,213) !important;
	border-radius: 4px !important;
	font: message-box !important;
	font-size: 12px !important;
	color: rgb(87,87,87) !important;
	/* last two box shadows here are transparent counterparts for animating focus ring shadows below */
	box-shadow: inset 0 -0.5px 0px 0px hsla(0,0%,70%,1), inset 0 0.5px 0.5px 0px white, 0 0 5px 15px rgba(151,195,243,0), inset 0 0 0 0px rgba(151,195,243,0) !important;
}

#nav-bar .searchbar-textbox[focused=true]
{
	color: black !important;
	/* Animated box shadows to recreate the native OS X 10.11 focus-ring effect */
	transition: box-shadow 300ms, border 300ms !important;
	box-shadow: inset 0 -0.5px 0px 0px rgb(168,205,245), inset 0 0.5px 0.5px 0px white, 0 0 0px 2.5px rgba(151,195,243,0.9), inset 0 0 0 0.5px rgba(151,195,243,0.9) !important;
	border: 0.5px solid rgba(136,178,220,0.9) !important;
}

.searchbar-search-button
{
	/* magnifying glass */
	-moz-image-region: unset;
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20">\ <circle cx="9" cy="9" r="4" fill="none" stroke="rgb(160,160,160)" stroke-width="1" />\ <path d="M15.5,15.5 L12,12 " stroke="rgb(160,160,160)" stroke-width="1" />\ </svg>');
}

.searchbar-search-button:hover, .searchbar-search-button:hover:active
{
	-moz-image-region: unset;
	filter: brightness(50%);
}

/* dropmarker (downward chevron) styling */
#navigator-toolbox .toolbarbutton-menu-dropmarker,
#navigator-toolbox .toolbarbutton-menubutton-dropmarker,
#navigator-toolbox .urlbar-history-dropmarker
{
	/* small down chevron */
	list-style-image: url('data:image/svg+xml;utf8,\ <svg xmlns="http://www.w3.org/2000/svg" width="7" height="5">\ <path d="M0.75,1.25 L3.5,4 L6.25,1.25" fill="transparent" stroke="rgb(115,115,115)" stroke-width="1.4" stroke-linecap="round"/>\ </svg>') !important;
}

