/* **************************************/
/* Personal / Bookmarks Toolbar styling */
/* **************************************/
#PersonalToolbar .toolbarbutton-1
{
	background: transparent !important;
	border-radius: 4px !important;
	border-width: 0px !important;
	padding: unset !important;
	margin: 1.5px 3px !important;
	min-width: 28px !important;
	transition: unset !important;
}

/* tone down button icons a tad */
#PersonalToolbar .toolbarbutton-1:not(:hover):not([open=true]) .toolbarbutton-icon { filter: grayscale(25%) contrast(75%) brightness(120%); }
#PersonalToolbar .toolbarbutton-1 { box-shadow: unset !important; }
#PersonalToolbar .toolbarbutton-1:hover { background: rgba(0,0,0,0.204) !important; }
#PersonalToolbar .toolbarbutton-1:hover:not([open=true])[buttonover] .toolbarbutton-icon,
#PersonalToolbar .toolbarbutton-1:hover stack,
#PersonalToolbar .toolbarbutton-1[open=true] stack { filter: drop-shadow(0.5px 0 0 white) drop-shadow(-0.5px 0 0 white) drop-shadow(0 0.5px 0 white) drop-shadow(0 -0.5px 0 white); }
#PersonalToolbar .toolbarbutton-1:hover:active,
#PersonalToolbar .toolbarbutton-1[open=true] { background: rgba(0,0,0,0.5) !important; }

#PersonalToolbar .toolbarbutton-1 .toolbarbutton-menubutton-dropmarker
{
	border-radius: 1px !important;
	border-left: 1px solid darkgray !important;
}

#PersonalToolbar > toolbaritem:-moz-window-inactive { opacity: 0.5 !important; }
#PlacesToolbarItems toolbarbutton:not([_moz-menuactive="true"]),
#PlacesToolbarItems menu:not([_moz-menuactive="true"]),
#PlacesToolbarItems menuitem:not([_moz-menuactive="true"]) { color: rgb(82,82,82); }

#PlacesToolbarItems .bookmark-item
{
	margin: 0px 0.5px 0px 0.5px !important;
	padding: 1.5px 9px 1.5px 9px !important;
	border-radius: 4px !important;
	transition: none !important;
	border-color: transparent !important;
	border-width: 0px !important;
}

.menu-right { margin-inline-end: -5px; }

#PersonalToolbar toolbarbutton.bookmark-item:hover
{
	background: rgba(0,0,0,0.204) !important;
	color: white;
}

#PersonalToolbar toolbarbutton.bookmark-item:active,
#PersonalToolbar toolbarbutton.bookmark-item[open=true]
{
	background: rgba(0,0,0,0.5) !important;
	color: white !important;
}

#PlacesToolbarItems toolbarbutton.bookmark-item { box-shadow: none !important; }
#PlacesToolbarItems .bookmark-item *:-moz-any(menuitem,menu) *:-moz-any(.menu-text,.menu-iconic-text) { font-size: 9.75pt !important; }
#PlacesToolbarItems > .bookmark-item > .toolbarbutton-icon { display: none !important; }
#PlacesToolbarItems { -moz-box-pack: center !important; }

.bookmark-item > .toolbarbutton-menu-dropmarker
{
	display: -moz-box !important;
	margin-inline-start: 2px;
	margin-inline-end: -2px;
	margin-top: -0.5px !important;
	-moz-image-region: unset !important;
}

.bookmark-item > .toolbarbutton-menu-dropmarker > .dropmarker-icon { filter: brightness(75%); }
.bookmark-item:-moz-any(:hover,[open=true]):not(.subviewbutton) > .toolbarbutton-menu-dropmarker > .dropmarker-icon { filter: brightness(1000%) !important; }
#PersonalToolbar .toolbarbutton-1:hover .toolbarbutton-menubutton-dropmarker > .dropmarker-icon { filter: brightness(75%) !important; }
#PersonalToolbar .toolbarbutton-1:hover:not([buttonover]) .toolbarbutton-menubutton-dropmarker > .dropmarker-icon,
#PersonalToolbar .toolbarbutton-1[open=true] .toolbarbutton-menubutton-dropmarker > .dropmarker-icon { filter: brightness(1000%) !important; }
#personal-bookmarks menu,
#personal-bookmarks menuitem { max-width: 460px !important; }

