:root
{
	--tab-min-height: 24px !important;
	--space-above-tabbar: 0px !important;
	--tab-toolbar-navbar-overlap: 0px !important;
	--navbar-tab-toolbar-highlight-overlap: 0px !important;
	--backbutton-urlbar-overlap: 0px !important;
	--toolbarbutton-combined-backgroundimage: none !important;
}

.tab-label
{
	-moz-box-flex: 1 !important;
	text-align: center !important;
	margin-left: -2em !important;
}

tab[selected="true"] { border-bottom: 1px solid #ccc !important; }
tab { -moz-appearance: none !important; }

/* Completely hide the "title changed" notification dot on pinned tabs */
.tabbrowser-tab > .tab-stack > .tab-content[pinned][titlechanged] { background-image: none !important; }
.tabbrowser-tab .tab-throbber,
.tabbrowser-tab .tab-icon-image,
.tabbrowser-tab .tab-sharing-icon-overlay,
.tabbrowser-tab .tab-icon-overlay,
.tabbrowser-tab .tab-label-container,
.tabbrowser-tab .tab-icon-sound { -moz-box-ordinal-group: 2 !important; }
.tab-label-container[pinned] { display: none !important; }
tab { border-top: -2px !important; }
:root:-moz-lwtheme { --tab-line-color: transparent!important; }


/* optional this hide the url field button noise
remove this if you want the buttons back in the url field*/
#page-action-buttons{display:none !important;}





