css:
@-moz-document domain("www.dailyrotation.com") {
body {
	font-family: system-ui, "Greta Sans Pro", "Avenir Next", Calibri, sans-serif !important;
	background-image: none!important;
	padding: 0px!important;
}
#dhtmltooltip {
	opacity: 0.95;
	position: absolute;
	width: 350px;
	border: 5px solid #DDD;
	padding: 7px;
	background-color: #EFEFEF;
	visibility: hidden;
	z-index: 1000;
	color: #000!important;
	font-family: "Greta Sans Pro", "Avenir Next", Calibri, sans-serif !important;
	font-size: 18px;
	font-style: normal;
	font-weight: normal;
	text-align: justify;
}
#heading {
	display: none;
}
#menu {
	opacity: 0.5;
	top: 5px!important;
	left: 5px!important;
}
#headlines2, #feedtable {
	width: 100%!important;
	left: 5px;
	top: 15px;
	font-weight: normal;
}
#headlines2 > form {
	margin: 0.5em !important;
}
form > br {
	display: none !important;
}
table#feed_col_table {
	width: 100%!important;
	padding: 0!important;
	border-spacing: 0px!important;
	border-collapse: collapse;
}
table#feed_col_table>tbody>tr>td {
	width: 33%!important;
	padding: 0px!important;
}
table#feed_col_1 {
	width: 100%!important;
}
table#feed_col_2 {
	width: 100%!important;
}
table#feed_col_3 {
	width: 100%!important;
}
table.go {
	width: 100%!important;
	background-color: #FFF;
}
.sechead21 {
	font-family: "Greta Sans Pro", "Avenir Next", Calibri, sans-serif !important;
	font-size: 12px;
	padding-bottom: 6px;
	font-weight: normal;
}

a.addthis_button span { display: none; }
a.addthis_button:after { content: "▫︎"; }

.sortlinez, .orange {
	width: 97%!important;
	padding: 0px!important;
}
ul {
	-webkit-padding-start: 13px!important;
	-webkit-margin-before: 0.5em;
	-webkit-margin-after: 0.5em;
}
li::marker {
	color: red;
}
.orange li, .orange td {
	list-style-type: circle; /*lower-greek;*/
	font-family: "Greta Sans Pro", "Avenir Next", Calibri, sans-serif !important;
	font-size: 13px;
	padding-bottom: 8px;
	font-weight: normal;
	line-height: 1em;
}
@media screen and (min-width:750px) {
	.orange li, .orange td {
		font-size: 14px;
		padding-bottom: 10px;
	}
	ul {
		-webkit-padding-start: 14px!important;
	}
}
@media screen and (min-width: 1000px) {
	.orange li, .orange td {
		font-size: 15px;
		padding-bottom: 12px;
	}
	ul {
		-webkit-padding-start: 16px!important;
	}
}
@media screen and (min-width: 1300px) {
	.orange li, .orange td {
		font-size: 17px;
		padding-bottom: 14px;
	}
	ul {
		-webkit-padding-start: 18px!important;
	}
}
.orange li:hover {
	list-style-type: disc;
	font-style: italic;
	word-spacing: 0.3em;
}
.orange li a {
	color: #221;
}
.orange table {
	width: 100%!important;
}
}
