css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(www.adam4adam.com) {
    
body{color: white !important; background-color: #383838 !important; font-family: Verdana, Arial, Helvetica, sans-serif !important;}
  
.table-favorites-members {background-color: #383838 !important;}
.tab-content {background-color: #383838 !important;}
.aside-nav .blog ul{background-color: white !important;}
.aside-nav > ul a {color: white !important;}
.aside-nav > ul a:hover{color: #fe742b !important;}
.aside-nav .search select{color: black !important;}
.aside-nav .visits-list ul {background-color: #383838 !important;}
.aside-nav .visits-list .member-name span {color: #fe742b !important;}
main .select-wrap, .modal-body .select-wrap {background-color: #12a5f4 !important;}
option {color: black !important;}
.profile .chat-text-message {color: black !important;}
.inner-page main {background-color: #383838 !important;}
/*tabs*/
.favorites-online main table .member-city, .blocked main table .member-city {color: white !important;}
.favorites-online main .tab-content, .blocked main .tab-content {border-style: solid; border-width: 1px;}
.last-visits .tab-content {border-style: solid; border-width: 1px;}
.smiles .tab-content {border-style: solid; border-width: 1px;}
.smiles .tab-content td {color: black !important;}
.tab-content {
    padding: 25px 25px 30px 30px;
    background: #fbfbfb;
    -webkit-box-shadow: 10px 10px 18px 0px rgba(0,0,0,0.56);
    -moz-box-shadow: 10px 10px 18px 0px rgba(0,0,0,0.56);
     box-shadow: 10px 10px 18px 0px rgba(0,0,0,0.56);
   /* border-bottom-left-radius: 5px; */
   /* border-bottom-right-radius: 5px; */
}
.account .my-profile a, .account .other a, .account h2 {
    color: #ffffff !important;
}
/*MESSAGE PEOPLE COLUMN*/
#messaging .conversation-list-wrap .conversation-list-container {
width: 100% !important;}
#messaging .conversation-list-wrap .messaging-full-header .title, #messaging .conversation-list-wrap h3 .title {
    color: #ffffff !important;
}
/* CHAT */
#messaging.full .chat-conversation {background-color: #383838 !important;}
#messaging.full .chat-text-message {color: black !important;}
#messaging.minimized .conversation-list-wrap {background-color: #383838 !important;}
#messaging .conversation-list-wrap .contact-message {color: black !important;}
#messaging.minimized .chat-tab .message-incoming .text-message span, #messaging.minimized .chat-tab .message-incoming .text-message > a { color: black !important;}
#messaging .conversation-list-wrap input[type="search"] {color: black !important;}
#messaging .conversation-list-wrap .conversation-list {background-color: #cccccc !important;}
#messaging .conversation-list-wrap h3 {background-color: #383838!important;}
#messaging .conversation-list-wrap h3 {color: white !important;}
/* #messaging .conversation-list-wrap .conversations-control {margin-bottom: 100px !important;} */
#messaging .chat-tab .chat-conversation{background-color: #383838 !important;}
#messaging.minimized .chat-tab .message-photo img {border-style: solid; border-width: 2px; border-radius: 20%;}
#messaging.minimized .chat-tab .message-outgoing .text-message span, #messaging.minimized .chat-tab .message-outgoing .text-message > a {font-size: 14px !important;}
#messaging.full .contact-name {color: #383838 !important; font-weight:900 !important;}
#messaging.minimized .contact-name {color: #383838 !important; font-weight:900 !important;}
#messaging.full .text-message div {color: #fe742b !important;}
#messaging.full .text-message > span {font-size: 14px !important;}
#messaging.minimized .chat-tab .chat-text-message {color: black !important;}
#messaging.minimized .chat-tab-container { bottom: 20px !important;}

.popup-content {color: black !important;}
.popup-top {color: #fe742b !important;}
/* IMAGE MISC */
      .modal.photo {z-index: 100;}
/* VISITED*/
.members-photos-gallery li:not(.member-photo-add) a {margin-left: 10px !important; margin-bottom: 10px !important; border-style: solid !important; border-width: 2px !important;}
/* ONLINE DOT */
.members-photos-gallery .member-photo-online::before {
    width: 20px !important;
    height: 20px !important;
    -webkit-box-shadow: 5px 5px 8px 0px rgba(0,0,0,0.8);
    -moz-box-shadow: 5px 5px 8px 0px rgba(0,0,0,0.8);
     box-shadow: 5px 5px 8px 0px rgba(0,0,0,0.8);
}
  #messaging .conversation-list-wrap .contact-name.online::before {
    border-style: solid; border-width: 1px; border-color: #383838;
  }
/* IMAGE POPUP */
.modal.photo .modal-dialog {position: relative !important; top: -70px !important;}
.modal-body {color: black !important;}

.favorites-online main table td:nth-child(4) .icon, .blocked main table td:nth-child(4) .icon {filter: invert(100%) !important;}
.favorites-online main .add-friend, .blocked main .add-friend {filter: invert(100%) !important;}
.favorites-online main .mutual, .blocked main .mutual {filter: invert(100%) !important;}
  /* Small fixes */
.profile dl:first-child dt { width: auto !important;}

}
