css:
@-moz-document domain('neuralnetworksanddeeplearning.com'), url-prefix('http://neuralnetworksanddeeplearning.com/'){

body {
  background-color: #101010 !important;
  
}

.section {
    margin-left: auto !important;
    margin-right:auto !important;
    color: #a0a0a0 !important;
}

img, svg {
    background-color: #ffffffcf !important;
    padding: 10px !important;
    border-radius: 10px;
}

p  {
    color: #a0a0a0 !important;
}

code  {
    color: rgba(170, 125, 125, 1) !important;
}

span {
  color: rgba(80, 125, 170, 1) !important;
}

a {
    color: hsl(207, 10%, 86%) !important;
}

#toc {
    display: none;
}

header {
    color: #d8dcdf !Important;
    background-color:#9aa2a9 !important;
}


.marginnote{
    display:block !important;
    color: #00000085 !important; 
    background-color: dimgrey;
    margin-left: 330px !important;
    padding: 8px !important;
    border-radius: 5px;
}

.marginnote:hover{
        display: block;
        color: black !important;
        background-color: #ffffffa6 !important;
        
}
}
