@-moz-document url-prefix("https://avenoel.org/")
{
html {
    font-family: monospace;
    background-image: url(https://image.noelshack.com/fichiers/2018/01/6/1515263532-40651305461a71a9604186cc96f5dd29d66d2a9e72b02cdd7fcb607b3cc54d18.png);
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
}
body {
    font-family: monospace;
    background: linear-gradient(180deg, rgba(0, 0, 0, 0.35), rgba(255, 255, 255, 0));
    background-size: 100% 10px;
    animation: scanlines 1s linear infinite;
    -webkit-animation: scanlines 2s linear infinite;
    -moz-animation: scanlines 2s linear infinite;
    -o-animation: scanlines 2s linear infinite;
}
nav.navbar {
    background: linear-gradient(90deg, #ff02ff, #ef0079, #a622fe, #37002e);
    background-size: 300% 300%;
    animation: backgroundVape 13s ease infinite;
}

main.container .container-content section
{
    background-color: rgba(77, 0, 102, 0.5);
}

tbody
{
    background-color: #4d0066;
}

td
{
    background-color: rgba(128, 0, 128, 0.9);
}

tr
{
    background-color: rgb(172, 0, 230);
}

.breadcrumb
{
    background-color: rgb(172, 0, 230);
}

tr.active, tr.active td
{
    background-color: #ff00ff !important;
}

.btn.btn-primary
{
    background-color: purple !important;
}

.navbar li.active a
{
    background-color: purple !important;
}

.bloc-title
{
    color: white;
}

label
{
    color: white;
}

a, th, .topics-author, .topics-amount, .topics-date, .topic-title
{
    color: white;
}

.breadcrumb li.active
{
    color: white !important;
}

.row.topic-message.odd 
{
    background-color: rgba(100, 50, 128, 0.9);
}

.row.topic-message  
{
    background-color: rgba(90, 0, 128, 0.9)
}

.message-content
{
    color: white;
}

.message-username a
{
    color: white !important;
}

.message-infos
{
    color: white;
}

.message-content-quote
{
    background-color: purple !important;
}

.message-content-quote a, .message-content-quote i
{
    color: white;
}

.profile-wrapper, .profile-infos tr, .profile-title, profile-stats
{
    background-color: rgb(128, 0, 128) !important;
    color: white;
}

.profile-title
{
    color: white !important;
}

section
{
    color: white;
}

.pagination.pagination-topic a
{
    color: white !important;
}

.chat-message, .chat-message-username a
    {
    color: white !important;
    }

.aside a
{
    color: white !important;
}

.spoiler-content
{
    background-color : purple !important;
}

.grey-btn.btn
{
    background-color: purple;
}

.form-control.post-content, .form-control
{
    opacity: 0.85;
    color: black;
}

a:hover
{
    color: white;
}

.form-group.preview
{
  background-color: rgba(90, 0, 128, 0.9);
  color:white;
}

.profile-biography
{
  background-color: purple !important;
  color: white;
}

a:focus
{
  color: white;
}

.admin a
{
color: red !important;
}

.mod a
{
  color: orange !important;
}

/**
Fait bouger les couleurs de fond
*/
 
@keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-webkit-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-moz-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@-o-keyframes backgroundVape {
    0% {
        background-position: 0% 0%
    }
    50% {
        background-position: 100% 0%
    }
    100% {
        background-position: 0% 0%
    }
}
@keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-webkit-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-moz-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
@-o-keyframes scanlines {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: 0% 10px
    }
}
}

