@-moz-document url-prefix("https://www.trillian.im/web/4.0/"), url-prefix("https://trillian.im/web/4.0/") {
html {
	background-color : #000;
}
body {
	color : #FFF;
	background-color : #000;
}
.header {
	background-color : #000;
	border-bottom    : 1px solid #0080e0;
	color            : #0080e0;
}
.header .buttons .header_button:hover {
	color : #FFF;
}
.viewport {
	background : #000;
}
.left {
	border-right-color : #0080e0;
	border-right-width : 1px;
}
.button.light {
	background-color : #000;
	color            : #FFF;
}
.button.light.active, .button.light:hover {
	color            : #0080e0;
	background-color : #000;
}
.left .search input[type=text] {
	border-color : #0080e0;
}
textarea, select, input[type=text], input[type=password] {
	color: #FFFF;
}
input {
	background-color: #000;
}
textarea {
	background-color : #000;
	border-color     : rgb(169, 169, 169);
}
textarea, select, input[type=text], input[type=password] {
	color : #FFF;
}
.right .edit input[type=text], .right .edit textarea {
	border-color : #0080e0;
}
.file_button {
	border-color : #0080e0;
	color        : #FFF;
	background   : #0080e0;
}
.file_button:hover {
	border-color : #FFF;
	color        : #0080e0;
	background   : #FFF;
}
.right .message .avatar.empty {
	background : darkviolet;
    color      : #FFF;
}
.right .message .text {
	border-bottom : 1px solid #000;
}
.right .message.incoming a.file .mediaName, .right .message.incoming a.file .mediaSize, .right .message.incoming .text {
	color : #FFF;
}
.right .message.incoming .bubble {
	background : darkviolet;
}
.right .message .arrow {
	border-right-color : darkviolet;
}
.right .message a {
	color: #FFF;
}
.lightmenu {
	background   : #111;
	border-color : #0080e0;
}
.emojibrowser {
	border-color : #0080e0;
	background   : #111;
}
.emojibrowser .explanation {
	background : #111;
}
.emojibrowser .explanation span.explanationText {
	color: #FFF;
}
.emojibrowser .category .title {
	color : #FFF;
}
.emojibrowser .categories {
	filter : invert(100%);
}
.emojibrowser .explanation .explanationImage span[class*="-category-"] {
	filter : invert(100%);
}
.helpbutton.delete, .helpbutton {
	background : #000;
}
.helpbutton.delete:hover, .helpbutton:hover {
	background : #000;
}

.overlay {
	background : black;
	background : rgba(0, 0, 0, 0.888);
}
.modal {
	background : #111;
	border     : 1px solid #0080e0;
}
.modal .header {
	background-color : #111;
}
.modal .modal_left {
	background : #111;
}
.modal .modal_right {
	border-left : 1px solid #0080e0;
}
#historyModal .footer {
	border-top : 1px solid #0080e0;
}
.ui-datepicker a {
	color: #CCC;
}
.ui-datepicker a.ui-state-hover {
	color : #FFF;
}
.ui-datepicker a.ui-state-active.ui-state-hover {
	color : #0080e0;
}
.right .media {
	background-color : black;
	background-image: linear-gradient(45deg, #222 25%, transparent 25%), linear-gradient(-45deg, #222 25%, transparent 25%), linear-gradient(45deg, transparent 75%, #222 75%), linear-gradient(-45deg, transparent 75%, #222 75%);
	background-size: 20px 20px;
	background-position: 0 0, 0 10px, 10px -10px, -10px 0px;
}
#login .status, #login .maintenance {
	background-color : #333;
	color            : #FFF;
}
#login .login {
	background   : #111;
	border-color : #0080e0;
}
#x_domainUserList img[src="img/spinner.gif"] , 
#x_mediaListLoading img[src="img/spinner.gif"] , 
#x_invoiceList img[src="img/spinner.gif"] , 
#x_domainPlanPaymentLoading img[src="img/spinner.gif"] , 
#x_domainBillingList img[src="img/spinner.gif"] , 
#x_domainPolicyList img[src="img/spinner.gif"] {
	display : none;
}
#x_domainUserList:before , 
#x_mediaListLoading:before , 
#x_invoiceList:before , 
#x_domainPlanPaymentLoading:before , 
#x_domainBillingList:before , 
#x_domainPolicyList:before {
	background-color : #000;
	height : 32px;
	width  : 32px;
	content : url(data:image/gif;base64,);
}
}

