@-moz-document url-prefix("https://www.trillian.im/eula/"), url-prefix("https://trillian.im/eula/") {
body {
	background-color : #000;
	color            : #FFF;
}
h3 {
	color : #DDD;
}
.content {
	color : #EEE;
}
.content p a {
	color : #0080e0;
}
.footer .links {
	color       : #DDD;
	text-shadow : 0px 1px #000;
}
.footer .links a {
	color : #DDD;
}
.footer .links a:visited {
	color : #DDD;
}
.footer .copyright {
	color       : #DDD;
	text-shadow : 0px 1px #000;
}
}

