css:
@-moz-document url-prefix("http://acgn-stock.com/") ,url-prefix("https://acgn-stock.com/") ,url-prefix("https://museum.acgn-stock.com/") ,url-prefix("https://test.acgn-stock.com/")
{
/*

標題：ACGN股票交易市場-夜間模式

範圍：ACGN股票交易市場

最後修改日期：2018 / 03 / 18

作者：vios10009 (Ƹ̵̡Ӝ̵̨̄Ʒ)

*/

  
   body
  {
  /*背景圖片，修改【  url(這裡是圖片網址) !important;  即可】*/
   background-image: linear-gradient(90deg,rgba( 0, 0, 0, 0.80) 0%,rgba( 0, 0, 0, 0.50) 100%), 
      url(
        https://i.imgur.com/C9U56BI.jpg 
      ) !important;
  /*固定位置*/
   background-attachment: fixed  !important;
  /*對齊位置*/
   background-position: center center!important;
  /*不重複*/
   background-repeat: no-repeat !important;
  /*圖片大小*/
   background-size: cover !important;
  /*背景顏色*/
   background-color: #222 !important;
  /*漸變特效（畫面緩緩顯示）*/
  /*-webkit-animation:myGradual 0.4s;*/
  /*-moz-animation:myGradual 0.4s;*/;
  }  
  /*文字預設的顏色*/
  #BH-background
  {
    color: #FFF !important;
  }
  
 /*--------------------------------------------------------------*/  

/*****ACGN字體的顏色*****/

  /*基礎字體*/
   body,
  /*跳頁文字*/
  .input-group-addon,
  /*下拉菜單*/
  .dropdown-menu,
  /*董事長*/
  .text-muted,
  /*主選單*/
  .navbar-inverse .navbar-nav .nav-link,
  /*外掛菜單主題*/
  .dropdown-header
  {
   color: rgba( 255, 255, 255, 0.84) !important;
  } 

  /*不明顯顏色(原為藍色字體)*/
  .text-info{
   color: rgba( 86, 251, 77, 0.53) !important;
   font-weight: bold;
  }

/******資訊顏色粗體******/

  /*綠色、股價下降*/
  .text-success,
  /*紅色、股價上升*/
  .text-danger
  {
   font-weight: bold;
  }

/********版面配置********/

  /*上下層*/
  .bg-faded{
   background-color: rgba( 0, 31, 39, 0.35) !important;
  }
  /*上層欄位跟著頁面往下*/
  .navbar{
   position: fixed;
   top: 0px;
   max-width: 90%;
   width: 550px;
   z-index: 90;
  } 
  @media (min-width: 576px){
   .navbar{
     position: fixed;
     top: 0px;
     max-width: 95%;
     width: 520px;
     z-index: 90;
    }           
  }
  @media (min-width: 768px){
    .navbar{
     position: fixed;
     top: 0px;
     max-width: 95%;
     width: 710px;
     z-index: 90;
    }           
  }
  @media (min-width: 991px){
    .navbar{
     position: fixed;
     top: 0px;
     max-width: 95%;
     width: 950px;
     z-index: 90;
    }           
  }

  @media (min-width: 1200px){
    .navbar{
     position: fixed;
     top: 0px;
     max-width: 1110px;
     width: 100%;
     z-index: 90;
    }
  }

  /*內層*/
  .dropdown-menu{
   background-color:rgba( 7, 54, 66, 0.5) !important;
   list-style:none !important;
  }
  .dropdown-item{
   color: #d8e3e4;
  }
  /*主題配置*/
  #main>.card{
   top: 40px;
  }
  .card{
   background-color:rgba( 255, 255, 255, 0) !important;
   border: 1px solid rgba( 255, 255, 255, 0);
  }
  /*頁數*/
  .page-link{
   background-color: rgba( 255, 255, 253, 0.5) !important;
   border-color: rgba( 255, 255, 255, 0) !important;
   color: #1c756e;
  }
  .page-item.active .page-link{
   color:#f7009d;
  }
  .input-group-addon{
   color:rgba( 7, 54, 66, 0.65);
   background-color:rgba( 255, 255, 255, 0);
  }
  /*搜索*/
  .btn{
   margin-right: 0.25rem !important;
   padding: 0.5rem 0.7rem;
  }
  .form-control{
   padding: 0.5rem 0.125rem;       
  }
  .btn-secondary{
   background-color: rgba( 88, 110, 117, 0.5);
   border-color: rgba( 88, 110, 117, 0.3);      
  }
  /*標籤*/
  .badge-default{
   background-color: rgba( 131, 148, 150, 0.4);
  }
  /*規則討論表格框線顏色*/
  .table-bordered th, .table-bordered td{
   border: 2px solid rgba( 255, 255, 255, 0.2) !important;
  }
  .nav-tabs{
   border-bottom: 2px solid rgba( 255, 255, 255, 0.2) !important;
  }
  /*規則討論議題選項表格框線顏色*/
  .list-group-item{
   border: 2px solid rgba( 255, 255, 255, 0.2) !important;
  }
  /*教學導覽顏色*/
  .card-header{
   background-color: rgba( 7, 54, 66, 0.5) !important;
  }
  /*伺服器連線數量*/
  .text-primary{
    color: #efb604 !important;
  }
  
/**********按鈕**********/

  /*普通按鈕*/
  .btn-primary{
   background-color: rgba( 255, 194, 4, 0.5);
   border-color: rgba( 181, 137, 0, 0.3);
  }
  /*重要按鈕*/
  .btn-danger{
   background-color: rgba( 255, 65, 154, 0.5);
   border-color: rgba( 211, 54, 130, 0.3);
  }
  /*買賣按鈕*/
  .btn-info{
   background-color: rgba( 0, 150, 255, 0.5);
   border-color: rgba( 0, 150, 255, 0.3);
  }
  /*最萌按鈕*/
  .btn-success{
   background-color: rgba( 14, 177, 165, 0.5);
   border-color: rgba( 14, 177, 165, 0.3);
  }
  /*取石按鈕*/
  .btn-warning{
   background-color: rgba( 255, 75, 0, 0.5);
   border-color: rgba( 255, 75, 0, 0.3);
  }

/**********公司**********/

  /*董事會留言*/
  .form-control{
   color: #060e5a !important;
   background-color: rgba( 169, 189, 189, 0.5);
  } 
  /*買賣股票*/
  .modal-content{
   background-color: rgba( 7, 54, 66, 0.8) !important;
   border: rgba( 7, 54, 66, 0.8) !important;
  } 
  /*產品中心*/
  .card-footer{
   background-color: rgba( 255, 255, 255, 0);
   border-top: 1px solid rgba( 255, 255, 255, 0);
  }

/*******經營管理介面******/
  /*修改分配*/
  .form-control:disabled, .form-control[readonly]{
   background-color: rgba(101, 123, 131, 0.5);
  }

/**********卡片**********/

  /*卡片文字顏色*/

  /*強制改變顏色的文字*/
  /*創立時間*/
  .company-card .row-info small,
  /*股份購入*/
  .company-card .btn-tab
  {
   color: rgba( 255, 255, 255, 0.85) !important;
  }

  /*不強制改變顏色的文字*/
  /*股票資訊*/
  .company-card .row-info p,
  .container-dark .company-card-default .row,
  .container-dark .company-card-default .row-info p,
  /*董事和經理*/
  .company-card .row
  {
   color: rgba( 255, 255, 255, 0.85) ;
  }

  /*卡片陰影*/
  .container-dark .company-card{
   border: 1px solid rgba( 0, 0, 0, 0.5);
   box-shadow: 6px 6px 6px rgba( 0, 0, 0, 0.5);
  }
  .container-dark .company-card-default{
   background: rgba( 0, 43, 54,0.58) ;
   color: #FFFFFF !important;
  }
  /*公司名稱背景*/
  .company-card .title{
   background: -webkit-linear-gradient(left, transparent, rgba( 0, 0, 0, 0.5) 5%, rgba( 0, 0, 0, 0.5) 95%, transparent);
  }
  /*卡片半透明*/
  /*灰卡*/
  .company-card-default{
   background: rgba( 136, 136, 136, 0.7) ;
  }
  /*銀卡*/
  .company-card-holder{
   background: linear-gradient(135deg, rgba( 238, 242, 243, 0.5), rgba( 142, 158, 171, 0.5));
  }
  /*紅卡*/
  .company-card-manager{
   background: linear-gradient(225deg, rgba( 255, 251, 213, 0.5), rgba( 178, 10, 44, 0.5));
                        
/*****自訂紅卡背景-如果要使用將*、/去掉就行，可使用於其他種類卡片*****/

                        
/*
   background-image: linear-gradient(90deg,rgba( 0, 0, 0, 0.80) 0%,rgba( 0, 0, 0, 0.50) 100%),
     url(
        https://i.imgur.com/NagElSP.jpg 
      ) !important;
*/
 /*background-attachment: fixed  !important;*/
  /*固定位置*/
 /*background-position: center!important;*/
  /*對齊位置*/
 /*background-repeat: no-repeat !important;*/
  /*不重複*/
 /*background-size: cover !important;*/
  /*圖片大小*/;

                        
/*****範例頭(不需要可整段移除)*****/
                        
   background-image: linear-gradient(90deg,rgba( 0, 0, 0, 0.60) 0%,rgba( 0, 0, 0, 0.30) 45%),
     url(
        https://i.imgur.com/2HMZOIs.png
      ) !important;

  /*固定位置*/
  /*background-attachment: fixed  !important;*/

  /*對齊位置*/
   background-position: -40px center !important;

  /*不重複*/
   background-repeat: no-repeat !important;

  /*圖片大小*/
   background-size: 160%  !important;

/*****範例尾(不需要可整段移除)*****/


  }
  /*金卡*/
  .company-card-chairman{
   background: linear-gradient(135deg, rgba( 252, 234, 187, 0.5), rgba( 248, 181, 0, 0.5));
  }

/**********VIP**********/

  .container-dark .company-vip-table .vip-level-5{
   border-radius: 1.5rem;
   border: 1px solid rgb(253, 186, 5);
  /*VIP圖片，修改【  url(這裡是圖片網址) !important;  即可】*/
   background-image: linear-gradient(0deg,rgba( 0, 0, 0, 0.60) 0%,rgba( 0, 0, 0, 0.30) 45%),
     url(
        https://i.imgur.com/WZgr9yU.png
      ) !important;
                    
  /*對齊位置*/
   background-position: right !important;

  /*不重複*/
   background-repeat: no-repeat !important;

  /*圖片大小*/
   background-size: contain  !important;

  /*背景顏色*/
   background-color: rgba( 0, 0, 0, 0);
                         
  }

/********帳號資訊********/

  /*帳號顏色*/
   a{
   color: #f3de3c
  }
  /*董事長、經理、員工*/
  .nav-tabs .nav-link.active, .nav-tabs .nav-item.show .nav-link{
   background-color: rgba( 41, 41, 41, 0.8);
  border-color: rgba( 41, 41, 41, 0.8);
  }
  /*移除滑鼠移動到選項上面的邊框*/
  a[Class='nav-link']:hover{
   border-color: rgba( 255, 255, 255, 0)!important;
  }

/******Markdown語法******/

  /*縮牌字體顏色*/
  .container-dark pre code{
   color: #92dae2 !important;
  }

  /*反引號標記*/
  code{
   color: #f3de3c;
   background-color: rgba(156, 59, 59, 0.4);
  }

/********外掛插件********/

  /*VIP黃底文字顏色*/
  td[bgcolor]{
   color: rgba( 0, 0, 0, 1) !important;
   font-weight: bold;
  }

  /*最多持股公司*/
  .dropdown-menu[Class='dropdown-menu px-3 show']{
   max-height: 300px;
   overflow-y: auto;
  }

/**********廣告**********/

  /*廣告宣傳背景顏色*/
  .table-success, .table-success>th, .table-success>td{
   background-color: rgba( 42, 161, 152, 0.5) !important;
  }
  /*自動關閉廣告*/
  .bg-info[Class='media bg-info text px-2 py-1 my-2 rounded']{
   display: none !important;
  }
  .bg-info[Class='media bg-info px-2 py-1 my-2 rounded footer-message-container']{
   display: none !important;
  }
  .float-left[Class='scriptAD float-left'],.float-left[name='scriptAd']{
   display: none !important;
  }
 /*--------------------------------------------------------------*/
}
