css:
@-moz-document domain(forum.stagerp.com) {

/* Body */

body {
	color: #222222 !important;
}

html {
	background-color: transparent !important;	
}

.footer {
	background-color: black !important;	
}

body .muted, body a.muted, body .muted a {
	color: #222222; 
}

.bbCodeQuote {
	border: none;	
}

.message .likesSummary {
    background-color: transparent !important; 
}

.bbCodeQuote .quoteContainer {
	color: #333333 !important;
	background-color: transparent;
	border: solid 1px #222222;
}

#pageDescription {
	color: #333333 !important;	
}

.Menu .primaryContent {
    background-color: rgba(250, 250, 250, 0.96) !important;
}

.Menu .secondaryContent {
    background-color: rgba(250, 250, 250, 0.96) !important;
}

.secondaryContent {
    background-color: transparent !important;
}

.quickReply {
    background-color: transparent;
    padding: 10px;
    border-bottom: none;;
}

.Menu {
	background-color: transparent !important;
	border: none;
}

.find_new_posts {
    background: url(http://img.stagerp.ru/img/background.png) no-repeat !important; 
	background-size: cover;
}

.forum_list {
    background: url(http://img.stagerp.ru/img/background.png) no-repeat !important; 
	background-size: cover;
}

.discussionListItem .noteRow {
	background-color: transparent;
	color: #222222;
}

#content {
	background: url(http://img.stagerp.ru/img/background.png) no-repeat !important;
	background-size: cover;
}

.PageContent {
    background: url(http://img.stagerp.ru/img/background.png) no-repeat !important; 
	background-size: cover;
}

.navTabs .navTab.selected .navLink {
	background: transparent;
	border: solid 1px #ffae00 !important;
	z-index: 99;
	color: white !important;
}

.sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
	color: #222222;	
}

.tabs li a, .tabs.noLinks li {
    border: none;
    color: white !important;
}

.Menu .sectionFooter {
    background-color: rgba(250, 250, 250, 0.9) !important;
}

#content .pageContent {
    background: transparent;
}

.xenOverlay.memberCard .userLinks a {
	color: #ffae00 !important;	
}

.bbCodeQuote .attribution {
	background-color: #ffae00 !important;
	color: #222222 !important;
	border-radius: 10px;
	border-bottom: none;
	border-bottom-left-radius:0px;
	border-bottom-right-radius:0px;
}

.thread_view .messageInfo, .message .messageInfo {
	background-color: transparent !important; 	
}

.message .newIndicator {
	background-color: #ffae00 !important;
	color: #222222 !important;
}

.prefix, .userBanner {
	background: none; 
	color: black !important;
}

.userBanner.bannerRed, .userBanner.bannerGreen, .userBanner.bannerLightGreen, .userBanner.bannerPrimary {
    color: #ffffff !important;
}

#DiscussionListOptionsHandle a {
	background-color: transparent;	
}

.userBanner.bannerStaff {
    color: rgb(116, 115, 115) !important;
}

.primaryContent, .blockLinksList {
	background-color: transparent !important;	
}

.xenOverlay .formOverlay .textCtrl {
 	background-color: white !important;   
}

#logo:hover {
    position: relative;
 	display: block;
    background: url(https://pp.userapi.com/c621511/v621511127/521d3/GA-1NTYIkZg.jpg);
    background-size: 7%;
    background-repeat: no-repeat;
    background-position: 130px;
    transition: 1s;
}



.primaryContent a {
	color: #333333 !important;
}

.messageList .message {
    background-color: #f6f6f6 !important;
    border-bottom: none;
    border-bottom: 20px solid transparent;
}


/* Блоки и вся эта хуйня */

.nodeIcon {
	display: none;   
}

.formPopup .controlsWrapper {
    background: #ffffff;
}

.node .nodeText {
    margin: 10px 270px 10px 10px;
}

.formPopup {
    background-color: #000000;
}

.tabs li a, .tabs.noLinks li {
	background-color: #2c2c2c;	
}

.tabs li.active a, .tabs.noLinks li.active {
	background-color: #ffae00;
}

.nodeList .categoryStrip {
    background: #ffae00 !important;
	border-radius: 10px;
	border-bottom-left-radius:0px;
	border-top-right-radius:0px;
}

.nodeList .categoryForumNodeInfo,
.nodeList .forumNodeInfo,
.nodeList .pageNodeInfo,
.nodeList .linkNodeInfo
{
	background-color: #000000;
}

#headerMover #headerProxy {
    background-color: #000000; 
}

.navTabs .navTab.selected .tabLinks {
    background-color: black !important;
}

#logoBlock {
	background-color: black !important;
}

#logo {
	background-color: black !important;
	border-radius: 0px;
}

#header {
	background-color: black !important;
	border-radius: 0px;
}


.node.forum.level_2:nth-child(odd), .discussionListItem:nth-child(odd), .node.page.level_2:nth-child(odd), .node.link.level_2:nth-child(odd) {
	background-color: transparent;
}

.navTabs {
	background: black !important;
	border-radius: 0px;
	border: solid 2px black;
	z-index: 99;
}

.sectionMain {
	background-color: transparent !important; 
}

.breadcrumb .crust a.crumb {
	background: #ffae00 !important;
	color: #222222;
} 

.breadcrumb .crust .arrow span {
	border-left-color: #ffae00 !important; 
	border-right: 1px none #ffae00 !important;
	border-radius: 5px;
}

.breadcrumb .crust .arrow {
	border-left-color: #ffae00 !important; 
	border-right: 1px none #ffae00 !important;
	
}

.breadcrumb {
	background-color: transparent !important;
    border: 1px solid transparent !important;
}

#QuickSearch {
	background-color: black;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
	background-color: #ffae00;
}

.sidebar, .hasJs .bbCodeSpoilerText {
	background-color: transparent !important;
}

.node.forum.level_2:nth-child(odd), .discussionListItem:nth-child(odd), .node.page.level_2:nth-child(odd), .node.link.level_2:nth-child(odd) {
	background-color: transparent;
}

.discussionListItem, .avatar img, .avatar .img, .avatarCropper {
	background-color: transparent;
}

.discussionList .sectionHeaders {
	background-color: #ffae00 !important;
	border-radius: 10px;
	border-bottom-left-radius:0px;
	border-top-right-radius:0px;
}


/* Текст вроде */

.blockLinksList a, .blockLinksList label {
	color: #222222;
}

.discussionList .sectionHeaders a {
	color: #222222 !important;
}


.PageNav a.currentPage {
	background-color: #ffae00 !important;
	border-color: #ffae00 !important;
}

.tabs {
	background-color: transparent;
	border: none;
}

.tabs li a, .tabs.noLinks li {
	border: none;	
}

.secondaryContent a {
    color: rgb(0, 100, 255);
}


a:link, a:visited {
    text-decoration: none;
	color: #222222 !important;
}

.PreviewTooltip {
	color: #222222 !important;	
}

.items {
	color: #222222;	
}

.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified, dt .subForumsPopup, .PopupOpen .dt, .subForumsPopup .dt {
	color: #222222; 
}

.nodeList .categoryStrip .nodeTitle a {
	color: #222222; 
}

.username .style2 {
	color: #333333; 
}

.nodeList .categoryStrip .nodeDescription {
	color: #222222 !important; 
	float: left !important;
}

.nodeTitle a, .nodeTitle a:visited {
	float: none !important; 
	color: #222222 !important;
}

.navTabs .navTab.PopupClosed .navLink, .navTabs .navLink:hover {
	color: white !important; 
}

.navTabs .navTab.selected .tabLinks a {
	color: white !important;
	border: none !important;
}

.profilePage .tabs.mainTabs li a, .navigationSideBar .heading, .heading, .xenForm .formHeader {
	background-color: #ffae00 !important;
	color: #000000 !important;
	border: none;
}

.ctrlUnit > dd .explain, .ctrlUnit > dd > * > li .hint, .textCtrl, .event .content .description, .event .content .DateTime {
	color: #222222 !important ;
}

.textHeading, .xenForm .sectionHeader, .Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
	color: #ffae00 !important;
}



.navTab selected {
	background-color: #ffae00 !important;
}

a.callToAction span, .sectionFooter, .subHeading, .textWithCount.subHeading .text, .textWithCount.subHeading .count {
	background-color: #ffae00 !important;
	color: #000000 !important;
}

.profilePage .tabs.mainTabs {
	background-color: transparent !important;	
}



.nodeList .categoryStrip .nodeTitle {
    float:none !important;	
}
.textCtrl {
	background-color: transparent;
	color: white;
}

.sidebar .avatarList .userTitle {
	color: #222222;
}

.sidebar .section .primaryContent   h3 a, .sidebar .section .secondaryContent h3 a, .sidebar .section .secondaryContent .minorHeading a, .sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
	color: #222222;
}

.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt {
	color: #222222; 
}

.tabs li.active a, .tabs.noLinks li.active {
	color: #222222 !important;
}

.navTab {
	color: white !important;
}

.footer a, .footer a:visited {
	color: white !important;	
}

.PageNav a {
	background-color: #ffffff;	
}

.OverlayTrigger .Tooltip {
	color: white;
}

.thread_view .messageInfo, .xenOverlay.memberCard .userInfo h4, .memberListItem .extra .bigNumber {
	color: #222222;	
}

.redactor_box {
	border: solid 1px rgb(174, 174, 174);	
}



#QuickReply {
	background-color: transparent !important;	
}

.avatarHolder {
	background-color: transparent !important;
	border: none;
}

.messageUserBlock .extraUserInfo {
	background-color: transparent; 
	border: none;
}

.avatar img, .avatar .img, .avatarCropper {
    background-color: #2c2c2c;
    padding: 1px;
    border: 1px solid #333333; 
}

.messageUserBlock {
	background-color: transparent; 
	border: none;	
}

.avatar img, .avatar .img, .avatarCropper {
	background-color: #2c2c2c;
    border: none; 
}

.messageUserInfo {
	border: none;
	border-radius: 10px;
	padding: 5px;
	
}

.messageUserBlock .arrow {
	display: none;	
}

#exposeMask {
	background-color: rgb(0, 0, 0) !important;	
}

.xenOverlay.memberCard .userLinks {
    color: rgb(255, 123, 0);
}

.xenOverlay.memberCard {
	color: #222222 !important;	
	background-color: rgba(237, 237, 237,0.95);
}

.xenTooltip {
    background-color: #ffffff;
    color: #222222;
}


}
