	/*
     * Name:    DarkSearch for Google
     * Author:  Nass O
     * Version: v2.1.8+
     */

    
    :root
    {
        --main-bg-col: #101010;
        --card-bg-col: #151515;
        --pop-card-bg-col: #1A1A1A;
        --search-bar-bg: #222;
        --border-main-col: #333;
        --image-label-bg: rgba(127, 127, 127, .05);

