css:
@-moz-document url("https://www.kryssord.org/"), domain("kryssord.org") {
a {
    color: white;
}
caption {
    background: none;
    color: navajowhite;
}
hr {
    width: 0;
}
html body {
    text-transform: uppercase;
    font-family: Roboto, Calibri, Consolas;
    background-color: #242424;
    color: #ffffff;
    background-image: url(data:image/png;base64,);
    font-size: smaller;
}
input {
    background-color: #111;
    color: #ffffff;
    border-style: none;
}
table {    
    background: rgba(76,65,54,0.2);
    color: white;
    border: none;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}
tbody td a {
    color: white;
    font-weight: normal;
}
tbody td a:hover {
    color: #eee;
}
thead th {
    background: none;
    border: none;
    font-size: xx-small;
}
td, tr {
    border: none;
}
tr:nth-child(even) {
    background: none;
}
#left, #right, #top {
    visibility: collapse;
}
#middle {
    background-color: #222;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
    padding: 1em;
}
.odd {
    background-color: #333;
}
.tcdiv {
    background: #222;
}
#middle {
    visibility: collapse;
}

#middle > table {
    visibility: visible;
}

#middle > form {
    visibility: visible;
}
input {
    font-family: "Roboto", 'Helvetica Neue, Helvetica, Arial', sans-serif;
    font-size: 14px;
    font-weight: 400;
    line-height: 1.1;
    text-transform: uppercase;
    letter-spacing: inherit;
    padding: 10px 14px;
    color: rgba(255, 255, 255, 0.87);
    outline: 0;
    outline-offset: 0;
    border: 0;
    border-radius: 2px;
    transition: all 0.15s ease-in-out;
}
}
