css:
@-moz-document domain("2sucres.org") {
body {
	color: #000;
}

body .sb-site-container {
    background-color: rgba(250, 250, 250, 0);
    background: linear-gradient(#363636 , #bdbdbd);
}

.topic-date > a {
 	color: #000;   
}

div.list-topics-container h1.topic-title.mb-1.no-mt.pl-1.ng-binding {
 	background-color: #99cc00; 
    font-size: 14.4px;
    font-family: Arial;
    font-weight: 700;
    padding: 1px 0;
    line-height: 22px;
}

div h1.topic-title.mb-1.no-mt.pl-1.ng-binding {
 	background-color: #fff;   
    padding: 0px;
}

div.list-topics-container h1.mb-1 {
  	margin-bottom: 0px!important;  
}

div.list-topics-container.forum-view.ng-scope md-toolbar._md.flex-100._md-toolbar-transitions {
    padding-bottom: 25px;
    margin-bottom: 15px; 
    border: solid #99cc00 1px;
    border-bottom-style:dotted;
    border-bottom-color: #3fc724;    
    box-shadow: 0px 10px #e1f6dc;
}

md-input-container {
 	border: solid #DDD 1px; 
    padding: 0px;
    margin-top: 20px;
    margin-right: 10px;
    padding-bottom: 1px;
}

md-input-container.md-input-focused input#input_2 { 
    border-bottom: solid #7b9dd4 1px;
}

md-select.md-default-theme:not([disabled]):focus .md-select-value, md-select:not([disabled]):focus .md-select-value {
   	border: solid #7b9dd4 1px; 
    border-radius: 1px;
}


md-select-value {
 	border: solid #DDD 1px;   
}



div.list-topics-container h1.topic-title.mb-1.no-mt.pl-1.ng-binding span {
 	color: #fff;
    font-size: 14.4px;
}

.table tr th.topic-date {
 	padding-left: 4px!important;
    padding-right: 4px!important;
}

.ms-header.ms-header-primary {
 	background-color: #363636;   
}

nav.navbar {
	background-color: #252525; 

}

.ms-footer {
  	background-color: #666666;   
}

.ms-navbar .navbar-nav li a {
    color: #f4f8fa;
    /*font-family: Arial,"Helvetica Neue",Helvetica,sans-serif;*/
}

#forum-container {
 	background-color: #fff;   
}

th.topic-name, .topic-user {
 	text-align: center;   
}

th.topic-name img {
 	position: absolute;
}

td.topic-date {
 	text-align: center;  
}

.topic-date > a {
 	margin-right: 0px;   
}

.list-topics > thead > tr {
 	background-color: #999999 !important; 
    color: #fff;
}

.list-topics > thead > tr > th.selected {
 	color: #fff;   
}

.table>thead>tr>th {
    border: none;
}

.list-topics > tbody > tr.even {
    background-color: #e9effa;
}

.list-topics > tbody > tr {
	background-color: #f5f5f5;
}

.one-topic .topic-name a:visited {
 	color: #888888;   
}

.list-topics > tbody > tr.even:hover {
    background-color: #e9effa;
}

.list-topics > tbody > tr:hover {
    background-color: #f5f5f5;
}

table.list-topics {
    border-spacing: 1px;
    border-collapse: separate;
 	border: solid #DDD 1px;   
}

table.list-topics a.topic-name-truncated {
 	font-weight: bold; 
    color: #0A77B8;
    font-family: Tahoma;
    font-size: 11.2px;
    vertical-align: top;
}


table.list-topics > tbody > tr > td.topic-user {
 	font-family: Tahoma, sans-serif;
    font-size: 85%;
    vertical-align: middle;
}

.table tr th {
    padding: 7px 0px!important; 
    font-size: 12.24px;
    font-family: Tahoma;
}



/*/////// FIX ///////// */
table.list-topics > tbody > tr > td.topic-nb-message {
	vertical-align: middle;
}

table.list-topics > tbody > tr > td.topic-connected-count {
	vertical-align: middle;
}

table.list-topics > tbody > tr > td.topic-date {
	vertical-align: middle;
}
/*////////////////////*/

.topic-message {
	background-color: #eff4fc;
    border-color: #c0d7ed;
    border-radius: 0px;
}

.odd.topic-message {
	background-color: #f9f9f9;
    border-color: #d1d1d1;
}

.message-pinned {
 	border-width: 2px;   
    border-color: #7aacfc;
    border-radius: 2px;
}

.md-button.md-default-theme.md-hue-1.md-accent.md-raised, .md-button.md-hue-1.md-accent.md-raised, .md-button.md-default-theme.md-hue-1.md-accent.md-fab, .md-button.md-hue-1.md-accent.md-fab {
 	background-color: #616161;
    color: #fff;
}

aside.topic-message {
  	border-width: 0px;  
}

.toolbar-msg {
   	background-color: #325361 !important; 
}

.forum-aside-content {
 	background-color: #ededed;   
    color: #000;
}

md-switch.md-default-theme.md-hue-1.md-checked .md-bar, md-switch.md-hue-1.md-checked .md-bar {
    background-color: #0da7d3;
}

md-switch.md-default-theme.md-hue-1.md-checked .md-thumb, md-switch.md-hue-1.md-checked .md-thumb {
    background-color: #0382a6;
}

#form-send-new-message.topic-message {
	background-color: #fff;
    border-color: #d1d1d1;
    border-radius: 0px;
}

md-progress-linear.md-default-theme.md-accent .md-container, md-progress-linear.md-accent .md-container {
 	background-color: #0a77be;   
}

md-progress-linear.md-default-theme.md-accent .md-bar, md-progress-linear.md-accent .md-bar {
 	background-color: #0d639a;   
}

.one-topic .topic-icon > md-icon {
 	 margin-left: 3px; 
}
}
